css:
@-moz-document url("https://steamstat.us/")
{

.title{
     /*Steam Status Title*/
     
     text-align: center;
     margin: 10px 0;
     font-size: 2.6em;
     font-weight: 300;
     color: white;
     text-shadow: 0px 0px 6px white;
}
a{
     color: #5d91df;
     text-decoration: none;
     text-shadow: 0 0 6px #5d91df;
     font-weight: bold;
}
.pull-right,
.service .status{
     text-align: right;
     float: right;
}



.good{
     /*Green*/
     
     color: #009e49;
     text-shadow: 0px 0px 6px #009e49;
}
.pull-right,
.service .status{
     text-align: right;
     float: right;
}



.minor{
     /*Light Blue*/
     
     color: #00e3ff;
     text-shadow: 0 0 6px #00e3ff;
     font-weight: bold
}



#js-refresh{
     /*Refreshing in idk seconds*/
     
     color: #00AFED;
     text-shadow: 0 0 6px #00afed;
     font-weight: bolder
}
.services,
#psa,
noscript{
     position: relative;
     background-color: #282936;
     border: 1px solid #191921;
     border-radius: 6px;
     font-size: 1em;
     line-height: 1.5;
     text-shadow: 0px 0px 6px black;
}
body{
     margin: 0;
     color: #8A8DB7;
     background: #1B1B24;
     font-family: 'Open Sans', Helvetica, Arial, sans-serif;
     font-size: 16px;
     text-shadow: 0 0 6px black;
     font-weight: lighter
                                                       
}
.major{
     /*Yellow*/
     
     color: #FFC107;
     text-shadow: 0 0 6px #FFC107;
     font-weight: bold
}

[data-tooltip]:before{
     /*Time since last status change Box*/
     
     visibility: hidden;
     opacity: 0;
     font-size: .9em;
     content: attr(data-tooltip);
     overflow: hidden;
     white-space: pre;
     word-wrap: break-word;
     position: absolute;
     top: 0;
     left: 2%;
     width: 97%;
     background: rgb(27, 27, 36);
     color: #FFF;
     text-align: left;
     padding: 8px;
     border-radius: 6px;
     z-index: 1;
     text-shadow: 0 0 10px #FFF;
     font-style: italic;
     font-weight: lighter
                                                       
 }
}
