}
#viewissuesidebar {
    opacity: 0.8;
}
.split-view .list-results-panel,
.navigator-issue-only .issue-body-content {
    border-color: #444;
}
.filter-list>li .filter-link:active,
.filter-list>li .filter-link.active,
.filter-list>li .filter-link:focus {
    color: #bbb;
}
.issue-header.detached {
    box-shadow: 0 0 15px 5px #111;
}
#footer-logo {
    background-color: #222;
}
/**** Dashboard ****/

#dashboard {
    background-color: #111;
    border-color: #555;
}
.gadget .dashboard-item-content,
.gadget.default,
#issuetable,
.issue-table,
#issuetable>tbody>tr,
.issue-table>tbody>tr,
#issuetable>tfoot>tr,
.issue-table>tfoot>tr,
#ghx-pool {
    background-color: #333;
    color: #eee;
}
.gadget .dashboard-item-header,
.gadget .dashboard-item-content {
    border-color: #777;
}
#ghx-column-header-group.ghx-fixed,
.ghx-column-headers .ghx-column {
    background-color: #333;
}
.ghx-column-headers .ghx-column {
    border-color: #777;
}
.ghx-column-headers h2 {
    color: #ccc;
}
.ghx-swimlane-header:after {
    background-color: #333;
    box-shadow: -5px 0 10px 5px #333;
}
.ghx-columns .ghx-column {
    background-color: #222;
}
.ghx-issue {
    background-color: #222;
    border-color: #555;
    color: #eee;
}
.ghx-issuetable {
    background-color: #111;
}
.ghx-issuetable .nav {
    border-color: #222;
}
.ghx-issuetable .issuerow:hover,
