    color: #ccc;
}
.g-favourite-filters #filter-list li,
.g-favourite-filters .config-options,
.g-favourite-filters .config-options span.secondary {
    border-color: #777;
}
#quicklinks-content {
    color: #777;
}
/**** Dropdowns ****/

.ajs-layer {
    background-color: #222;
    border-color: #555;
}
.aui-list .aui-list-section+h5,
.aui-list .aui-list-section+.aui-list-section {
    border-color: #555;
}
.aui-list a:link,
.aui-list a:visited {
    color: #999;
}
.aui-list a.active,
.aui-list .aui-list-item.active>a,
form#issue-filter .check-list-item.active,
form.aui .check-list-item.active,
.check-list-item.active,
.check-list-group-footer.active {
    background-color: #234365;
    color: #bbb;
}
/**** "Create Issue" Popup ****/

.jira-dialog {
    background: #333;
    border-color: #555;
}
.jira-dialog .jira-dialog-content {
    background: #222;
}
.jira-dialog .jira-dialog-heading,
.jira-dialog .buttons-container {
    background: #333;
    border-color: #555;
}
.issue-setup-fields {
