}
input,
textarea,
select {
    background-color: #111;
    border-color: #555;
    color: #fff;
}
::-webkit-input-placeholder {
    /* WebKit, Blink, Edge */
    
    color: rgba(119, 119, 119, 0.75);
}
:-moz-placeholder {
    /* Mozilla Firefox 4 to 18 */
    
    color: rgba(119, 119, 119, 0.75);
    opacity: 1;
}
