css:
@-moz-document domain("kshowonline.com") {

.page-header,
.navbar,
.panel,
#wrap,
.border-contain {
    background-color: #292828 !important;
}
html,
body {
    height: 100%;
    color: #0070c3;
}
.dropdown-content {
    background-color: #0d1725;
}
.thumbnail {
    background-color: #292828 !important;
}
.content-list {
    background-color: #292828;
}
.grey.darken-3 {
    background-color: #131417 !important;
}
footer {
    border-top: solid 1px #0070c3;
    color: #131417;
}
header {
    background-color: #131417;
}
.white {
    background-color: #131417 !important;
}
.content-info {
    background-color: rgba(40, 40, 41, 0.73);
}

.panel-episode {
    background-color: rgba(25, 26, 33, 0.13);
    border: solid 1px rgba(0, 0, 0, 0.66);
    border-radius: 5px;
    padding: 5px 10px;
    margin: 15px;
}
select {
    background-color: #0d1725;
}
input:not([type]),
input[type=text],
input[type=password],
input[type=email],
input[type=url],
input[type=time],
input[type=date],
input[type=datetime-local],
input[type=tel],
input[type=number],
input[type=search],
textarea.materialize-textarea {
    background-color: rgba(0, 0, 0, 0.84);
}
font,
a,
td,
th,
.border-bottom,
#view {
    color: #858A8C !important;
}
b {
    color: #74797A !important;
}
.panel-heading {
    background-color: #398ECE !important;
}
.panel-heading b {
    color: #2E2E2E !important;
}
.panel a {
    color: #428BCA !important;
}
.pager a,
.btn,
.form-group #keyword {
    background-color: #1A1A1A !important;
    color: #A9ACAE !important;
}
.episode span {
    color: #A9ACAE;
}
.dropdown.open a,
.dropdown-menu {
    background-color: #0B0B0D !important;
}

.menu-left {
    text-align: left;
    background-color: #131417;
    padding-bottom: 5px;
    margin-bottom: 10px;
    margin-top: 10px;
}
/*------------Disqus Thread-------------*/

#disqus_thread {
    position: relative !important;
    top: 35px !important;
    background-color: #51555a !important;
    border-style: solid !important;
}
/*--------End Of Disqus Thread---------*/
}
