css:
@-moz-document domain("intranet.iut-troyes.univ-reims.fr") {
.content {
	/*background-color: rgba(126, 204, 173, 0.54);*/
	
	background-color: rgb(223, 223, 223);
}
.navbar-default {
	background-color: #26638f !important;
}
.page-heading h3 {
	background-color: #26638f !important;
	color : white;
}
.widget {
	background-color: rgb(255, 255, 255);
	/*height: 110px;*/
}
.dropdown > .dropdown-toggle[data-original-title="Messagerie interne"] {
	display: none;
}
span[class=content] {
	background-color: #ffffff;
}
.topbar .topbar-left {
	float: left;
	height: 53px;
	position: relative;
	z-index: 1;
	width: 240px;
	background: rgba(231, 231, 231, 0.48);
}
.side-menu.left {
	background: #fff;
	position: absolute;
	top: 50px;
	border-right: none;
}
#sidebar-menu > ul > li > a.active,
#sidebar-menu > ul > li > a.active.subdrop {
	color: #fff;
	background: rgba(130, 162, 185, 0.58) !important;
	font-weight: 600;
	border-left: 0px solid rgba(0, 0, 0, 0.3);
}
.container {
    width: auto;
    background-color: rgba(130, 162, 185, 0.58);
   
}

.login-page {
	 background-image: url(http://195.83.128.55/~mmi15c07/images/background_intranet.jpg);
    background-size: cover;
}

.login-page .container {
	background-color : transparent;	
}

.alert-warning {
    color: #8a6d3b;
    background-color: #fffcd2;
	border-color: #fffcd2;
}

.login-wrap {
	margin: 20px 10%;
	text-align: left;
	background: rgb(130, 162, 185);
	padding: 20px 20px;
	color: #fff;
}
.btn-info {
	background-color: #659dd6;
	border-color: #659dd6;
	color: #FFFFFF;
}
.btn-danger {
	background-color: rgb(245, 74, 74);
	border-color: rgb(245, 74, 74);
	color: #FFFFFF;
}
.btn-warning {
	background-color: #ffb74d;
	border-color: #ffb74d;
	color: #FFFFFF;
}
.btn-info:hover,
.btn-info:focus,
.btn-info:active,
.btn-info.active,
.open .dropdown-toggle.btn-info {
	background-color: rgba(101, 157, 214, 0.62);
	border-color: rgba(101, 157, 214, 0.62);
	color: #FFFFFF;
}
.btn-warning:hover,
.btn-warning:focus,
.btn-warning:active,
.btn-warning.active,
.open .dropdown-toggle.btn-warning {
	background-color: #f8cb7c;
	border-color: #f8cb7c;
	color: #FFFFFF;
}
.btn-danger:hover,
.btn-danger:focus,
.btn-danger:active,
.btn-danger.active,
.open .dropdown-toggle.btn-danger {
	background-color: #ff7e7e;
	border-color: #ff7e7e;
	color: #FFFFFF;
}
.fa-power-off {
	cursor: pointer;
}
.navbar-default .navbar-nav > .open > a,
.navbar-default .navbar-nav > .open > a:hover,
.navbar-default .navbar-nav > .open > a:focus {
	color: #fff;
	background-color: #b6c9d6;
	height: 53px;
}
.btn-success {
	background-color: #4b7c9f;
	border-color: #4b7c9f;
	color: #FFFFFF;
}
.btn-success:hover,
.btn-success.active,
.open .dropdown-toggle.btn-success {
	background-color: #b6c9d6;
	border-color: #b6c9d6;
	color: #FFFFFF;
}
.btn-success:focus,
.btn-success:active,
.btn-success.active,
.open .dropdown-toggle.btn-success {
	background-color: #73a2c3;
	border-color: #73a2c3;
	color: #FFFFFF;
}

.btn-primary:hover, .btn-primary:focus, .btn-primary:active, .btn-primary.active, .open .dropdown-toggle.btn-primary {
    background-color: #3e6c8c;
    border-color: #3e6c8c;
    color: #FFFFFF;
}

#sidebar-menu > ul > li .has_sub:hover {
    background-color: #e6eff6;
}
#sidebar-menu > ul > li > a:hover {
    color: #555;
    background: #e6eff6;
    padding-left: 10px;
}

.td-edt {
   /* background-color: #406782 !important;*/
    color: #FFFFFF;
    padding: 0;
	-webkit-filter: hue-rotate(196deg);
	opacity: 0.75;
}

.list-unstyled {
    padding-left: 0;
    list-style: none;
    display: none;
}

.topbar-profile-image img, .profile-image img {
	filter: saturate(60%);
    -webkit-filter: saturate(60%);
    -moz-filter: saturate(500%);
    -o-filter: saturate(500%);
    -ms-filter: saturate(500%);	
}
}
