#Main div[class="cell item"] table,
#TopicsNode table {
	padding: 10px 0 10px 10px;
	text-align: left;
	border-radius: 4px;
	transition: background-color .2s;
}
#Main div[class="cell item"] table:hover,
#TopicsNode table:hover {
	background-color: #f3f6f9;
	transition: background-color .2s;
}
#Main div[class="cell item"] img.avatar,
#TopicsNode img.avatar {
	border-radius: 18px;
	width: 36px;
	height: 36px;
}
#Main div[class="cell item"] a,
#TopicsNode a {
	text-decoration: none;
	color: #111;
}
#Main div[class="cell item"] a:visited,
#TopicsNode a:visited {
	color: #7c7c7c;
}
#Main div[class="cell item"] td,
#TopicsNode td {
	position: relative;
	vertical-align: middle;
}
#TopicsNode td:last-child {
	padding-right: 20px;
}
#Main div[class="cell item"] span,
#TopicsNode span {
	margin: 5px 0;
	display: block;
}
#Main div[class="cell item"] .item_title,
#TopicsNode .item_title {
	width: 560px;
}
/* topic's info */

#Main div[class="cell item"] span[class="small fade"],
#TopicsNode span[class="small fade"] {
	font-size: 12px;
	color: #667d99;
	line-height: 120%;
}
#Main div[class="cell item"] span[class="small fade"] .node {
	border-radius: 6px;
	padding: 3px 10px;
	color: #fff;
	background-color: rgb(109, 187, 62);
	position: absolute;
	top: 20px;
	right: 0;
}
#Main div[class="cell item"] span[class="small fade"] .node:hover {
	color: #667d99;
}
#Main div[class="cell item"] span[class="small fade"] .node:active {
	color: #667d99;
}
#Main div[class="cell item"] span[class="small fade"] a:hover,
#TopicsNode span[class="small fade"] a:hover {
	color: #e7742e;
}
#Main div[class="cell item"] span[class="small fade"] a:active,
#TopicsNode span[class="small fade"] a:active {
	color: #F44336;
}
#Main div[class="cell item"] span[class="small fade"] strong a,
#TopicsNode span[class="small fade"] strong a {
	font-size: 12px;
	color: #667d99;
}
#Main div[class="cell item"] .count_livid,
#TopicsNode .count_livid {
	background-color: transparent;
	color: #667d99;
	font-size: 16px;
	font-weight: normal;
	display: block;
	position: absolute;
	top: 20px;
}
#Main div[class="cell item"] .count_livid:before,
#TopicsNode .count_livid:before {
	font-family: 'FontAwesome';
	content: '\f0e5';
	font-size: 16px;
	margin-right: 4px;
}
#Main .box:nth-child(2) .inner:last-child {
	margin-top: 20px;
}
#Main .box:nth-child(2) .inner:last-child .fr {
	display: none;
}
#Main .box:nth-child(2) .inner:last-child span {
	display: none;
}
#Main .box:nth-child(2) .inner:last-child a {
	background-color: #e7edf3;
	color: #667d99;
	padding: 5px 15px;
	border-radius: 4px;
	text-decoration: none;
}
#Main .box:nth-child(2) .inner:last-child a:hover {
	background-color: #d7dfea;
}
/* 节点不会出现在首页的提醒 */

#Main .box:last-child .inner {
	font-size: 0;
}
.cell_ops {
	margin: 10px 0;
	border: none;
}
/* 节点导航 */

a[href="/planes"] {
	position: absolute;
	top: 500px;
	left: -280px;
	text-decoration: none;
	color: #667d99;
	font-size: 16px;
	line-height: 120%;
}
a[href="/planes"]:before {
	font-family: 'FontAwesome';
	font-size: 16px;
	content: '\f067';
	color: #03A9F4;
	margin-right: 10px;
}
a[href="/planes"]:hover {
	color: #e7742e;
}
/*回复框*/

#Main .box:last-child .inner .fr {
	display: none;
}
#Main .box:last-child .cell:first-child .fade {
	display: none;
}
#Main .box:last-child form[method="post"] {
	margin-top: 20px;
}
#Main .box:last-child form[method="post"] .cell {
        font-size: 12px;
}
#Main .box:last-child #topic_title {
	border: 2px dashed #e7edf3;
	border-radius: 4px;
	transition: border-color .2s;
}
#Main .box:last-child #topic_title:hover {
	border: 2px dashed #d7dfea;
	transition: border-color .2s;
}
#Main .box:last-child #topic_title:focus {
	border: 2px solid #e7742e;
	transition: border-color .2s;
	box-shadow: 0 2px 6px rgba(0, 0, 0, 0.35);
}
#Main .box:last-child form[method="post"] .mll {
	margin-top: 20px;
	border: 2px dashed #e7edf3;
	border-radius: 4px;
	transition: border-color .2s;
}
#Main .box:last-child form[method="post"] .mll:hover {
	border: 2px dashed #d7dfea;
	transition: border-color .2s;
}
#Main .box:last-child form[method="post"] .mll:focus {
	border: 2px solid #e7742e;
	transition: border-color .2s;
	box-shadow: 0 2px 6px rgba(0, 0, 0, 0.35);
}
#Main .box:last-child form[method="post"] input[type="submit"] {
	margin-top: 10px;
	padding: 5px 20px;
	border: 1px solid #d7dfea;
	background-color: #fff;
	color: #667d99;
	font-weight: normal;
	text-shadow: none;
	transition: background-color .1s;
}
#Main .box:last-child form[method="post"] input[type="submit"]:hover {
	border: 1px solid #e7742e;
	background-color: #e7672e;
	box-shadow: 0 2px 6px rgba(0, 0, 0, 0.35);
	transition: background-color .2s;
}
#Main .box:last-child form[method="post"] input[type="submit"]:active {
	background-color: #e2571a;
	transition: background-color .2s;
}
/* 子节点及贴内header */

#Main .box:nth-child(2) .header a {
	text-decoration: none;
	color: #667d99;
}
#Main .box:nth-child(2) .header .sep10 {
	height: 10px;
}
#Main .box:nth-child(2) .header input {
	display: none;
}
#Main .box:nth-child(2) .header h1 {
	font-size: 20px;
}
#Main .box:nth-child(2) .header small[class="gray"] {
	display: inline;
}
#Main .box:nth-child(2) .header .fr img {
	width: 64px;
	height: 64px;
	border-radius: 64px;
}
/* 子节点分页 */

table[width="100%"] td[width="92%"] a {
	margin: 10px 5px;
}
/* 分页 */

#Main .box:nth-child(4) table {
	display: none;
}
#Main .box:nth-child(4) .cell:nth-child(2) table[width="100%"] td[width="92%"],
#Main .box:nth-child(4) .cell:nth-child(2) table[width="100%"] td[width="8%"] {
	display: none;
}
#Main .box:nth-child(4) .cell:last-child table[width="100%"] td[width="8%"] table {
	display: table;
}
/* 贴内回复 */

#Main .box:nth-child(4) .cell > table[width="100%"] {
	display: table;
	margin: 10px 0;
	padding: 20px 0;
	border-bottom: 1px solid #e7edf3;
	font-size: 12px;
}
#Main .box:nth-child(4) table[width="100%"] img.avatar {
	border-radius: 48px;
}
#Main .box:nth-child(4) table[width="100%"] td:nth-child(3) {
	padding: 0 10px;
}
#Main .box:nth-child(4) table[width="100%"] td:nth-child(3) .fr {
	margin-top: -6px;
}
#Main .box:nth-child(4) table[width="100%"] td:nth-child(3) .dark {
	color: #111;
}
img[src="//cdn.v2ex.com/static/img/reply.png"] {
	display: none;
}
#Main .box:nth-child(4) table[width="100%"] td:nth-child(3) .thank_area + a:before {
	text-decoration: none;
	font-family: 'FontAwesome';
	content: '\f112';
	font-size: 14px;
}
#Main .box:nth-child(4) table[width="100%"] td:nth-child(3) .no {
	padding: 4px 8px;
	font-size: 14px;
	color: #667d99;
}
#Main .box:nth-child(4) table[width="100%"] td:nth-child(3) .no:before {
	content: '#';
	font-size: 14px;
	letter-spacing: 2px;
}
#Main .box:nth-child(4) table[width="100%"] td:nth-child(3) .fade {
	color: #667d99
}
#Main .box:nth-child(4) table[width="100%"] td:nth-child(3) .sep5 {
	height: 30px;
}
#Main .box:nth-child(4) .cell .tag {
	display: none;
}
/* 底部信息 */

#Bottom {
	margin-top: 30px;
	border-top: 1px solid rgba(0, 0, 0, 0.22);
	background-color: #fff;
	color: #667d99;
	padding: 10px;
}
#Bottom a {
	text-decoration: none;
	color: #667d99;
}
#Bottom a:hover {
	color: #e7742e;
}
#Bottom a:active {
	color: #F44336;
}
#Bottom span {
	color: #667d99;
}
#Bottom strong {
	font-weight: normal;
	font-size: 14px;
	line-height: 200%;
}
/*备案信息*/

#Bottom .f12 {
	display: none;
}
/* 分页按钮 */

.page_current {
	display: inline-block;
	font-weight: normal;
	font-size: 14px;
	line-height: 14px;
	padding: 3px 15px;
	background-color: #e7742e;
	border-radius: 4px;
	margin: 0px 1px 0px 1px;
	color: #fff !important;
	transition: .2s
}
.page_current:hover {
	background-color: #9c2a00;
	transition: background-color .3s;
}
.page_normal:link,
.page_normal:visited,
.page_normal:active {
	display: inline-block;
	font-weight: normal;
	font-size: 13px;
	line-height: 13px;
	padding: 3px 5px;
	background-color: #fff;
	border-radius: 4px;
	margin: 0px 1px 0px 1px;
	text-decoration: none;
	transition: .2s
}
.page_normal:hover {
	background-color: #e7742e;
	color: #fff !important;
	padding: 3px 10px;
	transition: padding .2s ease-out, background-color .2s ease-out;
}
.super.button {
	background-image: none;
	background-color: #fff;
	padding: 4px 8px 4px 8px;
	border: 1px solid rgba(80, 80, 90, 0.2);
	border-bottom-color: transparent;
	border-radius: 4px;
	font-size: 14px;
	line-height: 12px;
	outline: none;
}
}

@-moz-document url("https://www.v2ex.com/settings") {
.cell {
	font-size: 14px;
}
#Rightbar .box:nth-child(4) {
	position: relative;
	left: -840px;
	top: 120px;
}
#Rightbar .box:nth-child(6) {
	display: block;
	position: relative;
	left: -840px;
	top: 200px;
}
#Main .box:last-child .inner {
	margin-top: 10px;
	font-size: 14px;
}
}

@-moz-document url("https://www.v2ex.com/settings/privacy") {
#Main .box:last-child .inner {
	font-size: 14px;
	line-height: 30px;
}
}

@-moz-document regexp("^(http|https)://(www.)*v2ex.com/([^\\?].*)(/)?.*") {
#Rightbar img {
	display: inline !important;
	max-width: 220px;
	height:auto;
}
#Rightbar .box:nth-child(4) {
	position: relative;
	display: block;
	left: -840px;
	top: 150px;
}
#Rightbar .box:nth-child(6) {
	display: block;
	position: relative;
	left: -840px;
	top: 180px;
}
#Rightbar .box:nth-child(6) .sep10 {
	height: 10px;
}


}

@-moz-document url("https://www.v2ex.com/new") {
#Rightbar .box:nth-child(2) {
	position: relative;
	left: -840px;
	top: 20px;
}
}

@-moz-document url("https://www.v2ex.com/signin") {
#Main .box:nth-child(2) .header + .cell table[width="100%"] {
	display: table;
}
}
