css:
@-moz-document domain("miniclip.com") {
html,
body {
    background: #000000!important;
}
div[class="notification-bar error"] {
    display: none!important
}
div[id="container"] {
    background-color: #FFFFFF!important
}
div[class*="sky-wrapper"] {
    display: none!important
}
html {
    zoom: 1.5;
}
header,
.menu,
#pool-menu-before {
    display: none;
}
}

@-moz-document url-prefix("http://www.miniclip.com/games/8-ball-pool-multiplayer/en/focus") {
body {
    overflow: hidden;
    background-color: #000000!important;
}
html[class^="wf-opensans-n"] {
    background: -moz-linear-gradient(left, #323540, #393B46, #464754, #393B46, #323540)!important
}
object[id="game-embed"] {
    background: -moz-linear-gradient(left, #323540, #454653, #464754, #454653, #454653, #323540)!important
}
body,
div[id="container"] {
    height: 520px!important;
    background: linear-gradient(#363843, #454653, #464754, #464754, #464754, #333540)!important
}
*[class^="wf-loading"] {
    background: linear-gradient(#363843, #454653, #464754, #464754, #464754, #333540)!important
}
section[id="game-container"] {
    margin: 0 auto;
    transform: scale(1.8);
    padding-top: 40px;
}
header[class="expert-mode"],
div[class^="game-toolbar"],
div[class="tools"],
#advert-holder {
    display: none!important
}
div[class="focus-game layout-aga"] {
    margin-left: 40px!important
}
a[rel="nofollow"] {
    display: none!important
}
div[id="pool-menu-before"] {
    display: none!important
}
nav[id="pool-menu-after"] {
    display: none!important
}
div[class="expert-game layout-aga"] {
    height: 520px!important;
    margin-top: 40px!important;
    margin-top: 160px!important;
    background: #000000!important
}
}
