css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joyreactor.cc"), domain("joy.reactor.cc"), domain("joyreactor.com"), url-prefix("http://reactor.cc") {
  
/*Замена заднего фона
------------------------------------------------------------------------------*/
  #container,
  #container::before, 
  #container::after  {
    background: url("https://img-fotki.yandex.ru/get/4612/103004821.0/0_16a1f5_cbd2850b_orig") repeat fixed;
    background-color: black;
  }
}

@-moz-document domain("joyreactor.cc"), domain("reactor.cc"), domain("joyreactor.com"), domain("pornreactor.cc") {
  body {
    background-color: black; 
  }

  

/*Изменение стиля поста и формы 'новый пост'
------------------------------------------------------------------------------*/
  .postContainer, 
  #contentinner form#add_post {
    background: #95908a !important;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);    
    padding: 8px;
    margin-bottom: 16px;
  }
  
  form.sfSignin,
  form.post_comment_form {
    background: #95908a !important;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);    
    padding: 8px;
  }

  #showCreatePost {
    display: block;
    text-align: center;
    width: 100%;
    margin-bottom: 16px;
  }
  
  #add_post span.super-tag, #sidebar .tabs_titles ul li a {
    background: #ccc7c2;
  }
  
  .add_post_button,
  #add_post_holder, 
  #tagArticle {
    width: 100%;
  }
  
  .article,
  .add_post_footer {
    margin-bottom: 0;  
  }

  input[name="signin[username]"],
  input[name="signin[password]"],
  input[name="tag"],
  input[name="header"],
  #add_post_text {
    background: #95908a !important;
    border: 1px solid #ccc7c2 !important;
    color: #211c17;
  }
  
  
  
/*Изменение цвета комментариев
------------------------------------------------------------------------------*/
  .comment {
    background: #ccc7c2;	
  }
  
  .comment_text {
    background: #95908a !important;
    border: 1px solid #ccc7c2;
    color: #211c17;
  }
  
  div[style="background: rgb(235, 235, 235) none repeat scroll 0% 0%;"], 
  .comment:hover {
    background: #dcd7d2 !important;	
  }
  
  .comment.new {
    background: #ded6af;
  }
  
  #contentinner .comment .addcomment form.post_comment_form {
    background: #ccc7c2 !important;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);    
    padding: 8px;  
  }
  
  .comment .addcomment form.post_comment_form .comment_text {
    background: #ccc7c2 !important;
    border: 1px solid #aca7a2;
    color: black;
  }
 
  .comment .addcomment {
    margin-top: 15px;
    margin-bottom: 8px;
  }
  
  .small_comments_button {
    background: #ccc7c2 ;
    border: 2px solid #dcd7d2;
  }
  
  .article .ufoot span a.response {
    background: #bab5b0;
  }
  
  .commentnum.comOpened {
    background: #FDDA97;
  }
  
  

/*Изменение цвета контейнера 
------------------------------------------------------------------------------*/
  #pageinner {
    background-color: #67625b !important;
    background: url("https://img-fotki.yandex.ru/get/108697/103004821.0/0_16a1f3_5ac4942a_orig") repeat;
    padding: 16px;
  }
  
  #content{
    background-color: #67625b !important;
    background: url("https://img-fotki.yandex.ru/get/108697/103004821.0/0_16a1f3_5ac4942a_orig") repeat;
  }

  .week_top,
  .user.week_top .userposition,
  .user.week_top a,
  .user .weekrating {
    background-image: none !important; 
    background: none !important; 
  }
  
  .sidebarContent,
  .mainheader {
    color: #211c17 !important;
  }
  
  #sidebar .sideheader {
    color: #211c17 !important;
    border-bottom: 3px solid #413c37;
  }
  
  #tagList {
    color: #211c17 !important;
    border-top: 3px solid #413c37;
    padding-top: 0px;
  }
  
  .post_poll_holder {
    background: #ccc7c2 !important;
    width: 100%;
  }

  iframe[style="position: relative; background-color: transparent; border: none; overflow: hidden; height: 600px;width: 300px;"] {
    opacity: 0.4;
    margin-bottom: 30px;
  }
  
  
 
/*Изменение стиля кнопок навигации
------------------------------------------------------------------------------*/
  .topbar_inner, .topbar_wr {
    background: #302a25 !important;
  }
  
  #navcontainer:after {
    opacity: 0.85;
    background: #302a25 !important;
  }

  .post_random a {
    background: #16110c !important;
  }
  
  .pagination .current,
  .pagination .next,
  .pagination .prev {
    box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);
  }
  
  .pagination_main .pagination_toggler,
  .pagination_expanded a,
  .pagination_expanded span,
  .pagination span {
    background: transparent;
    color: #302a25;
  }
  
  .sidebarContent p.login_link a {
    background: #fdb201;
    width: 100%;
    text-align: center;
  }
  
  input[value="Поддержать проект"] { 
    width: 100%;
    text-align: center;
  }

  
  
  
/*Компактизация содержимого
------------------------------------------------------------------------------*/  
  #navcontainer {
    padding: 13px 16px 0px;
  } 
  #navcontainer ul li {
    margin: 0px 0px 0px 0px;
  }
  
  .post_random a {
    border-radius: 12px;
    padding: 0px 12px;
  }
  
  #sidebar {
    width: 300px;
    float: left;
    margin-left: -300px;
  }
  
  #page {
    min-width: 1260px;
    width: 80%;  
  }
  
  #content {
    width: 100%;
    min-width: 911px;
    padding-right: 316px;
    margin-bottom: 0px;
  }
  
  .pagination_main {
    padding-top: 16px;
    padding-bottom: 11px;
  }
  
  .topbar_inner {
    width: 78%;
    min-width: 1220px;
    align-self: center;
  }

  #add_post > div > div.add_post_footer {
    margin: 0px 0px 0px;  
  }
  
  .article .ufoot span.comments {
    margin-right: 64px;
  }

  .comments_bottom .date {
    margin: 0px 5px;
  }
  
  .commentnum {
    border-radius: 12px;
    line-height: 24px !important;
    font-size: 12px;
  }
  
  .commentnumDelta {
    height: 26px;
    line-height: 25px !important;
    top: 0px;
    border: 1px solid #dcd7d2;
    border-radius: 12px;
    font-size: 12px;
  }
  
  .comment {
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 8px;
    padding-bottom: 4px;
  }
  
  .comments_bottom {
    margin-top: 0px;
  }

  .comment_date {
    margin: 0px 16px;
  }

  .article .ufoot span a.response {
    font-size: 12px;
    font-weight: bold;
    margin: 0px 20px;
    padding: 0px 12px;
    border-radius: 12px;
  }
  
  a img,
  #contentInnerHeader {
    width: 100%;
    height: 100%;
  }
  
  .article .post_content_expand, #tagArticle .post_content_expand {
    height: 30px;
    margin-bottom: 5px;
    background: #ccc7c2 ;	
  }
  
  .article .post_content_expand span, #tagArticle .post_content_expand span {
    text-align: center;
    line-height: 30px;
  }
  
  .article .post_content {
    margin-bottom: 0px;
  }
  
  .user.week_top {
    margin: 0px 0px 0px;
  }

  .addcomment {
    margin-bottom: 0px;
    padding: 0px;
  }
  
  .manage_post_wr,
  div.vote-minus,
  div.vote-plus {
    margin-top: 0px;
    vertical-align: middle;
  }
  
  span.comments {
    top: 5px;
  }  
  
  div.ufoot {
    margin-top: 5px;
  }  
  
  
  
}
