body {
    background: transparent !important;
}
.content {
    background: transparent !important;
}
#fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://lh3.googleusercontent.com/olSjW4ZTmBt0EmSB7ac_4sS5khQGPBGMP8tY1LP090tMMzjDCN3WPuicg4WWIZFXWgo=h900") !important;
    /* Background */
    
    background-size: cover !important;
    background-attachment: fixed !important;
}
body {
    -webkit-animation: fadein 0.4s;
    /* Safari, Chrome and Opera > 12.1 */
    
    -moz-animation: fadein 0.4s;
    /* Firefox < 16 */
    
    -ms-animation: fadein 0.4s;
    /* Internet Explorer */
    
    -o-animation: fadein 0.4s;
    /* Opera < 12.1 */
    
    animation: fadein 0.4s;
}
@keyframes fadein {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}
/* Firefox < 16 */

@-moz-keyframes fadein {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}
/* Safari, Chrome and Opera > 12.1 */

@-webkit-keyframes fadein {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}
/* Opera < 12.1 */

@-o-keyframes fadein {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}
