css:
@-moz-document url-prefix("https://digg.com/reader/") {

.col-left {
    background: rgb(52,44,18);
    border-right-color: #333;
}

.dr-label {
    color: #f1f1f1;
    text-shadow: none;
    font-weight: 500;
    font-size: 0.81em;
}

.dr-unread-count {
    margin-left: .5em;
    color: #f7f7f7;
    font-weight: bold;
    text-decoration: overline;
}

.dr-label:hover {
    background-color: rgba(255, 255, 255, 0.15);
    border-top-color: #000;
    border-bottom-color: #000;
    cursor: pointer;
}

.dr-feed-selected.nav-item-focused>.dr-label, .dr-feed-selected.nav-item-focused>.dr-label:hover {
    background-color: rgba(255, 255, 255, 0.15);
    border-top-color: #000;
    border-bottom-color: #000;
    cursor: pointer;
    outline: none !important;
}

.feed-stream-header{
    background-color: #333;
    border-bottom: none;
}

.feed-title a {
    color: #f1f1f1;
    border-bottom: none;
    font-size: 1.2em;
    font-weight: 600;
}

.story-detail-view-container {
    background-color: #333;
}
.detail-body {
    color: #e7e7e7;
    font-size: 0.947em;
    line-height: 1.5em;
    letter-spacing: 0.345px;
    font-weight: 400;
    text-shadow: none;
    font-family: "Libre Baskerville", Palatino, Georgia, serif;
	text-rendering: optimizeLegibility;
}


.feed-item-focused.expanded .story-detail-view-container .story-title a {
    color: rgb(0, 166, 240);
    font-family: "Overpass", Verdana, sans-serif;
    font-size: 32px;
}

.expanded .feed-item-focused.expanded {
    border-bottom:none;
}

.expanded .expanded {
    border-bottom: none;
}

.story-meta-item {
    color: #b1b1b1;
}

.story-action-item.story-action{
    transition: 0.5s transform ease;
}
.story-action-item.story-action:hover {
    transform: scale(1.42);
}

.btn-toggle-read-state {
    font-family: "Overpass", "Georgia", serif;
    color: #d7d7d7;
}

header.site-header {
    background-color: rgb(52,44,18);
    box-shadow: -3px 0px 4px 2px rgba(20, 20, 20, 0.40);
                                         
}

.site-header-logo-container a {
    filter:hue-rotate(400deg) invert(0.80);
}

.content-main {
    margin:55px auto 0;
}

.reader-nav .reader-nav-text-item a {
    color: #e7e7e7;
    text-decoration: none;
    font-size: 13px;
    font-weight: 600;
}

.reader-nav .reader-nav-mark-as-read {
    background-color: rgb(52,44,18);
    color: #e7e7e7;
    border-left: none;
}

.reader-nav .reader-nav-view-type-selector{
    background-color: rgb(52,44,18);
    color: #e7e7e7;
    border-left: none;
}

.reader-nav .reader-nav-settings {
    background-color: rgb(52,44,18);
    color: #e7e7e7;
    border-left: none;
}


.reader-nav .reader-nav-mark-as-read:hover {
    background-color: rgba(100, 100, 100, 0.6);
}

.reader-nav .reader-nav-view-type-selector:hover {
    background-color: rgba(100, 100, 100, 0.6);
}

.reader-nav .reader-nav-settings:hover {
    background-color: rgba(100, 100, 100, 0.6);
}

.view-add {
    background-color: rgb(52, 44, 18);
    border-color: #333;
    border-width: 2px;
    border-style: solid;
    transform: translateY(-3px) translateX(-2px) scaleY(1.15);
}

.view-add>div {
    -webkit-box-shadow: -3px 0px 4px 2px rgba(20, 20, 20, 0.40);
    box-shadow: -3px 0px 4px 2px rgba(20, 20, 20, 0.40);
}

.dr-add-btn {
    border-right: none;
}

                                      
}
