css:
@-moz-document domain("rpthreadtracker.com") {
    body.ng-scope {
        background: #333 !important;
    }
    p {
        color: grey;
        font-weight: bold;
    }
    #content ul li {
        color: gray;
    }
    p a,
    #content ul li a {
        color: orange;
    }
    p a:hover,
    #content ul li a:hover {
        color: #d88c01;
    }
    section h2,
    section h3 {
        color: #FFF;
    }
    #main-navbar {
        background: #333;
    }
    #main-navbar .navbar-inner {
        background: -moz-linear-gradient(top, rgba(50, 50, 50, 0.65) 0%, rgba(0, 0, 0, 0) 100%);
        background: -webkit-linear-gradient(top, rgba(50, 50, 50, 0.65) 0%, rgba(0, 0, 0, 0) 100%);
        background: linear-gradient(to bottom, rgba(50, 50, 50, 0.65) 0%, rgba(0, 0, 0, 0) 100%);
        border-bottom: solid 1px #222;
    }
}
