css:
@-moz-document domain('www.qconcursos.com'){@keyframes fade {
    0%, 50% {
        opacity: 0;
        transform: scale(0);
    }
}
@keyframes dot-anim {
    0% {
        top: -4vw;
    }
    50% {
        top: 4vw;
    }
    100% {
        top: -4vw;
    }
}
@keyframes checked-radio {
    0% {
        top: -10vw;
        transform: scale(0);
    }
    100% {
        top: 0;
        transform: scale(1);
    }
}
@keyframes unchecked-radio-3 {
    0% {
        bottom: 0;
        transform: scale(1);
    }
    100% {
        bottom: -10vw;
        transform: scale(0);
    }
}
@keyframes checked-radio-4 {
    0% {
        transform: rotate(0) translateY(-4.8vw) scale(.2);
    }
    83% {
        transform: rotate(360deg) translateY(-2.5vw) scale(1);
        transform-origin: 2vw;
    }
    88% {
        transform: translateY(.6vw) scale(1);
    }
    93% {
        transform: translateY(-.9vw) scale(1);
    }
    100% {
        transform: translateY(0) scale(1);
    }
}
@keyframes unchecked-radio-4 {
    25% {
        top: -6.5vw;
    }
    50% {
        top: 9vw;
    }
    75% {
        top: -10vw;
    }
    100% {
        top: -10vw;
        transform: scale(0);
    }
}
html,
body {
    background: #343434 !important;
    color: white !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
}
.resolvi-errado,
.resolvi-questao-errado {
    background-image: url("https://cdn1.iconfinder.com/data/icons/toolbar-signs/512/erase-512.png");
    background-size: 38px 36px;
}
.resolvi-certo,
.resolvi-questao-certo {
    background-image: url("https://cdn4.iconfinder.com/data/icons/flat-game-ui-buttons-icons-2/80/1-33-512.png");
    background-size: 38px 66px;
}
.fix-table,
.chosen-results {
    background: #343434 !important;
    color: white !important;
}
.qc-header,
.uma-questao-corpo,
.filtro-classico,
.questoes-alongadas,
.main-nav {
    background: #282828;
}
.select-images.hover-effect>li {
    border-color: #444;
}
.list-lesson-item.past .inner,
.select-images>li,
.text-to-translate,
textarea {
    background: #222;
}
.bot-laranja{
    width:525px;
   outline: none;
  border: none;
  cursor: pointer;
  font-size: 20px;
  background-color: transparent;
  border: 2px solid #fff;
  transition: all 0.5s ease;
  -webkit-transition: all 0.5s ease;
  -moz-transition: all 0.5s ease;
  -o-transition: all 0.5s ease;
  -ms-transition: all 0.5s ease;
}
.enunciado,
.enunciado-class,
.qc-menu,
.ng-scope,
.main-nav,
.questoes-texto,
.concurso {
    color: #aaa;
}
div >.uma-questao-corpo {
    border-radius: 45px;
    border-color: green;
    border-style: solid;
    border-width: 5px;
}
::-webkit-scrollbar {
    width: 11px;
}
::-webkit-scrollbar-track {
    background: #444 !important;
}
::-webkit-scrollbar-thumb {
    border-radius: 4px;
    background: rgba(0, 0, 0, 0.5) !important;
}
::-webkit-scrollbar-thumb:hover {
    background: orange !important;
}
::-webkit-scrollbar:window-inactive {
    visibility: hidden;
}
.input-hidden {
  position: absolute;
  left: -9999px;
}
select{
   -webkit-appearance: none;  /* Remove estilo padrão do Chrome */
   -moz-appearance: none; /* Remove estilo padrão do FireFox */
   appearance: none; /* Remove estilo padrão do FireFox*/
   background: #444;
   background-position: 218px center;  /*Posição da imagem do background*/
   width: 250px; /* Tamanho do select, maior que o tamanho da div "div-select" */
   height:30px; /* Altura do select, importante para que tenha a mesma altura em todo os navegadores */
   border:1px solid #ddd;
}

.caderno-selecione, .opcoes-conteudo {
    color: white;
  background: lightgray !important;
 }
}
