/* Google: Force Default Logo - https://userstyles.org/styles/125677 */
@-moz-document regexp("https?://www.google.(com|([a-z]{2}))(.[a-z]{2})?.*") {
body > #lga, #searchform.jhp ~ #main #lga {
	position: relative !important;
}
body > #lga:before, #searchform.jhp ~ #main #lga:before {
	content: "";
	display: block;
	width: 100%;
	height: 100%;
	box-sizing: border-box;
	background: url('data:image/png;base64,') 50% 100% no-repeat;
	border-bottom: 35px solid transparent;
}
body > #lga *, #searchform.jhp ~ #main #lga * { display: none !important; }

.logocont a {
	display: inline-block;
	background: url('data:image/png;base64,') 50% 25% no-repeat;
	background-size: 100%;
}
.logocont a img {
	opacity: 0;
}}

