css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain(eldarya.fr) {
  
/*Sylveon en familier*/
  
  
  /*Images - pictures*/
#pet-headerPortrait-base {
    background: url("http://static.zerochan.net/Sylveon.full.2028056.jpg") -15px top no-repeat !important;
    background-size: auto 110px !important;
}
#pet-portrait {
    background: url("http://static.zerochan.net/Sylveon.full.2028363.jpg") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
}
#pet-portrait > img[src*="/static/img/pet/icon/"] {
    display: none !important;
}
/*Infos*/

#pet-info-name:before {
    content: "Sylveon" !important;
    font-size: 20px !important;
}
#pet-info-name {
    font-size: 0 !important;
}
#pet-info-evolve > .on {
    font-size: 0 !important;
}
.pet-info-evolve-icon {
    font-size: 13px !important;
}
.pet-info-evolve-icon:after {
    content: " Sylveon ne peut plus evoluer";
}
#pet-info-racegender:before {
    content: "Pokemon " !important;
    font-size: 16px !important;
    line-height: 20px !important;
}
#pet-info-racegender {
    font-size: 0 !important;
    margin-top: 5px;
}
img[src*="/static/img/f.png"] {
    background: url("http://www.eldarya.fr/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 15px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
}
/*Nourriture - food*/

#pet-info-food > img {
    background: url("http://www.pokemontrash.com/images/pokemon-go/razzberry.png") !important;
    background-size: 48px 48px !important;
    margin-top: -5px !important;
    padding-left: 48px !important;
    padding-top: 48px !important;
    width: 0 !important;
    height: 0 !important;
}
#pet-info-food {
    font-size: 0 !important;
}
#pet-info-food:before {
    content: "Razzberry " !important;
    font-size: 16px !important;
}
#pet-info-food > span {
    font-size: 16px !important;
}

#pet-info-food > span {font-size : 16px !important ;}

}
