css:
@-moz-document domain("www.nyaa.se"), domain("sukebei.nyaa.se") {
/* Remove ads, let's hope they don't change there html markup */
div#main>div:first-of-type,
div#main>div:nth-of-type(2),
div#main>div:nth-of-type(4) {
    display: none;
}

/* Box-sizing border box, trust me it's better this way */
* {
    box-sizing: border-box;
}

body {
    background: rgb(219, 220, 220);
    min-width: 0; /* settings this to 1200px made the torrent list go off center. Also who does this kind of thing? */
}

div#main {
    width: 100%;
    max-width: 1000px;
    min-width: 900px;
    overflow: auto; /* trick to make sure the main div is wrapped around the content */
    padding-top: 40px;
}

div#topbar {
    background: #323742;
}

.content {
    width: 100%;
    border: none;
    margin: 0;
    padding: 0;
    border-radius: 0;
}

#tabnav a {
    line-height: 1em;
    padding: 14px 9px;
}

#navbar {
    width: auto;
    text-align: left;
    float: left;
    margin-left: calc(50% - 500px); /* trick to get this thing to align right iwth the main div properly */
}

div#searchcontainer {
    padding: 0;
    float: left;
    width: auto;
}

ul#tabnav {
    float: none;
    margin: 0px 12px 0px 8px;
}

select.inputsearchcategory,
input.inputsearchterm,
select[name="filter"] {
    margin-top: 5px;
    height: 30px;
    width: 150px;
    padding-left: 5px;
}

input.inputsearchsubmit {
    margin-left: 4px;
    margin-top: 5px;
    height: 30px;
    width: 60px;
    cursor: pointer;
}

a {
    color: #323742;
}

table.torrentcats {
    width: auto;
    padding: 1.5em 0 0 1em;
}

.torrentcats tr {
    display: inline-block; /* yup you can change the display property of a table row */
}

.torrentcats tr:first-of-type td:nth-of-type(2),
.torrentcats tr:first-of-type td:nth-of-type(3) {
    display: none;
}

.torrentcats td a {
    padding: 0.5em 1em;
    border: 1px solid #dbdcdc;
    border-radius: 3px;
    text-decoration: none;
}

.torrentcats td a:hover {
    border-color: #c1c1c1;
}

.torrentsubcatlist {
    padding-top: 1.5em;
}

.tlistsortorder {
    padding: 1.5em 1.1em;
}

.tlistsortorder tr {
    border-bottom: 1px solid #dbdcdc;
    display: inline-block;
    word-spacing: 0.5em;
}

.tlistsortorder tr td:nth-child(2) {
    padding: 0 1em;
    color: #dbdcdc;
}

th.tlistthone {
    padding-left: 1.2em;
    width: 96px;
}

td.tlisticon {
    padding-left: 1.2em;
}

.tlistrow {
    border-bottom: 1px solid #323742;
    border-top: 1px solid #323742;
}

td.tlistsize {
    border: none;
}

.page {
    border: 1px solid #dbdcdc;
}

.pagelink:hover {
    background: transparent;
    color: #323742;
    border: 1px solid #c1c1c1;
}

.leftpages,
.pages,
.rightpages {
    margin-bottom: 1.5em;
}

.tlisttheight {
    width: 4.6%;
}
}
