css:
@-moz-document domain("fjnbnpbmkenffdnngjfgmeleoegfcffe") {
/* stupid bullshit*/

/* size of popup */

body#stylish-popup {
    height:448px;
}

body:not(#stylish-popup) {
    font-family: century gothic;
}

body:not(#stylish-popup) {
    font-family: roboto;
    color: #cccccc;
    background: #202020;
}

button, input, select {
    outline: none;
    color: #bbbbbb;
    border-radius: 3px;
    border: 1px solid #444444;
    background: #202020;
    padding: 5px;
    margin: 3px;
    box-shadow: 1px 2px 5px rgba(0, 0, 0, 0.15);
    transition: background .3s ease-in-out, border .3s ease-in-out, padding .3s ease-in-out;
}

button:hover, input:hover, input:focus, select:hover {
    background: #333333;
    border: 1px solid #333333;
}

input:focus {
    padding: 7px;
}

*:not(#stylish-popup) {
    font-weight: normal;
}

body#stylish-popup img {
    opacity:1;
}
img {
    opacity: 0;
}

label:not(#stylish-popup) {
    font-size: 110%;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1.6em;
}

div#header {
    padding-right: 25px;
    border-right: 25px solid #262626;
}

#sections h2 {
    margin-left: 2.5rem;
}

#sections > div {
    margin-left: 2em;
}

.CodeMirror .CodeMirror-wrap .cm-s-mbo .CodeMirror-active {
    outline: none;
}

div.applies-to label {
    padding-top: 5px;
    padding-left: 5px;
    padding-right: 0px;
    margin-right: -15px;
}

#help-popup {
    border-radius: 3px;
    background: #333333;
    padding: 20px;
}

div#help-popup.big div.title {
    font-size: 1rem;
    font-weight: normal;
}

#help-popup .close-icon {
    background: linear-gradient(-45deg, transparent 4px, lightgrey 6px, lightgrey 4px, transparent 4px) no-repeat, linear-gradient(45deg, transparent 4px, lightgrey 6px, lightgrey 4px, transparent 4px) no-repeat;
}

div#installed {
    padding-left: 25px;
    border: none;
}

div.tabs-container, div.tabs-header {
    height:100%;
}
div.tab-header label {
    vertical-align:middle;
    cursor:pointer;
    padding-top:-20px;
    height:18px;
    line-height:1px;
    padding-bottom:5px;
}
a#url {
    background: url(https://s19.postimg.org/n3lzdlztf/ic_link_white_24dp_1x.png) no-repeat center;
    background-size:20px 20px;
}
img#to-mozilla-help {
    cursor:pointer;
    background: url(https://s19.postimg.org/pyz2kh3tf/ic_help_outline_white_24dp_1x.png) no-repeat center;
    margin-left:2px;
    background-size:18px 18px;
}
img#keyMap-help {
    cursor:pointer;
    background: url(https://s19.postimg.org/pyz2kh3tf/ic_help_outline_white_24dp_1x.png) no-repeat center;
    margin-left:2px;
    background-size:18px 18px;
}
}
