body {
    background-color: #444 !important;
    background: #444 !important;
    color: #01FF70 !important;
    font-family: arial;	
    filter: brightness(95%);
}


a {      
    color: #0d0;	
}
a:hover {      
    color: #0f0;
}

a:visited {      
    color: #0b0;
}

pre {
    background-color: #555 !important;
    color: LightBlue
}

h2 {      
    color: #fd0;	
}


.post_box .headline, .headline a {
    color: #FFDC00 !important;
}

