    color:#FF851B !important;
}

.container {
	background: #333;
	background-color: #333;

}

.code,.shell {
    background-color: #222 !important;
    color: white;
}

::-webkit-scrollbar {
    max-width: 10px !important;
    max-height: 10px !important;
    background: #1d1d1d !important;
}
::-webkit-scrollbar-track, ::-webkit-scrollbar-corner {
    background: #1d1d1d !important;
}
