css:
@-moz-document domain("nobelium.xyz") {
.alert-success{
background-color: #e6e6e6;
border-color: #e6e6e6;
color: #000000;
}
.alert-warning{
background-color: #151515;
border-color: #151515;
}
.card{
background: #242424;
}
.CodeMirror{
background: #242424;
color: #FFFFFF;
}
.container{
color: #C8C8C8;
}
.card-primary, .card-header, .panel, .well{
background-color: #121212;
border-color: #FFFFFF;
}
#app, .footer.container{
background: #1B1B1B;
}
body, #Body{
background: #1B1B1B;
}
.navbar-light.bg-faded, .navbar.navbar-inverse{
background-color: #121212;
border: #121212;
}
.navbar-light .navbar-brand, .navbar-light .navbar-toggler, .navbar-light .navbar-nav .nav-link, .navbar-inverse .navbar-brand, .navbar-inverse .navbar-nav>li>a, .navbar-inverse .navbar-text, .dropdown-menu>li>a, .editor-toolbar a{
color: #FFFFFF !important;
}
.dropdown-item, .dropdown-menu{
background-color: #242424
}
.dropdown-item:hover{
background-color: #363636;
color: #FFFFFF;
}
.form-control{
background-color: #3F3F3F;
color: #FFFFFF;
}
.form-control:focus{
background-color: #484848;
border-color: #FFFFFF;
color: #FFFFFF;
}
a[href]{
color: #FFFFFF;
}
.page-item.active, .page-item.disabled .page-link, .page-link, .btn{
color: #FFFFFF;
background-color: #242424;
border-color: #FFFFFF;
}
.page-item.active .page-link{
background-color: #484848;
border-color: #FFFFFF;
}
.page-link:hover, .page-item.disabled .page-link:hover, .btn:hover{
color: #FFFFFF;
background-color: #363636;
border-color: #FFFFFF;
}
.btn-primary.disabled, .btn-primary:disabled{
color: #FFFFFF;
background-color: #131313;
border-color: #FFFFFF;
}
.dropdown-menu>li>a:hover{
color: #C8C8C8;
}
.navbar-light .navbar-nav .nav-link:focus, .navbar-light .navbar-nav .nav-link:hover, .navbar-light .navbar-brand:hover, .navbar-inverse .navbar-brand:hover, .navbar-inverse .navbar-nav>li>a:hover, .navbar-inverse .navbar-text:hover, .navbar-inverse .navbar-nav>li>a:focus, .navbar-inverse .navbar-text:focus, .dropdown-menu>li>a:focus{
color: #C8C8C8;
}
.navbar-brandimg{
content:url("http://i.imgur.com/ywlx8jY.png");
}
.custom-control-indicator:checked, .custom-control-input:checked~.custom-control-indicator{
background-color: #000000;
border-color: #FFFFFF;
}
.jumbotron>.container{
background-color: hsla(0, 0%, 11%, .85);
}
}
