/*
Hello,

I didn't do all of this myself.
I don't remember where all of this code came from.
But I can thank acbn-nick on github for quite a bit of this code.
I have probably fucked this code up BAD but hey, it works.

Enjoy,	

  dultcers

 */
@-moz-document domain("4chan.org") {
  
body {
    background: #212121 !important;
    font-family: 'Open Sans', sans-serif;
    color: #f2f2f2 !important;
    font-size: 18px !important;
    margin-right: 15px !important;
    margin-left: 15px !important;
    padding-left: 20% !important;
    padding-right: 20% !important;
}

::selection {
background: #FFB9BC;
}

hr {
    padding-top: 5px !important;
    border: 0 !important;
}

#hoverUI {
    background-color: #212121;
}

/* Links */
a {
    color: #a5c4ed !important;
}

/* Foreground of the header bar */
#board-list,
#custom-board-list {
    color: #212121 !important;
    font-family: 'Ubuntu', sans-serif !important;
    font-weight: 400 !important;
    font-size: 18px !important;
    margin-right: 0px !important;
}
#header-bar a {
    color: #211221 !important;
}

/* Background of the header bar */
#header-bar.dialog {
    background: #5978ff !important;
    border: 0 !important;
}

/* Color of the selected board on the header bar. */
#header-bar a.current {
    color: #fff !important;
}
    .fixed #header-bar{
        padding: 1px 2px 0px
    }
    .fa::before{
        color: #5978ff !important;
    }
    .yotsuba-b:root #header-bar, .yotsuba-b:root #notification{
        color: #5978ff !important;
    }

/* Appearance of the menus. */
#menu.dialog,
.entry {
    background: #f2f2f2 !important;
    border: 0 !important;
    color: #fff !important;
    font-family: 'Open Sans', sans-serif;
    font-size: 18px !important;
}

.focused {
    border: 0 !important;
                                    }

/* Catalog search and page links, This is not ready. */
div.navLinks,
div.pagelist {
    background: transparent !important;
    font-family: 'Open Sans', sans-serif;
    font-size: 18px !important;
    border: 0 !important;
    display: inline-block !important;
    margin-bottom: 0 !important;
}

/* Appearance of the thread updater and thread stats. */
#index-search.field,
#updater.dialog,
#thread-stats.dialog {
    font-family: 'Open Sans', sans-serif;
    font-size: 18px !important;
    background: transparent !important;
    color: #f2f2f2 !important;
    border-style: none !important;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0) !important;
}

/* Thread updater menu */
#index-search.field:hover,
#updater.dialog:hover {
    background: #212121 !important;
    color: #f2f2f2 !important;
}

/* Clean up. */
.bottomCtrl.desktop,
#absbot.absBotText,
.navLinks.desktop,
.navLinks.navLinksBot.desktop,
.sideArrows{
    display: none !important;
}

/* Hide ads? */
div.center,
div.topad.center,
div.middlead.center,
div.bottomad.center,
div.ad-plea {
    display: none !important;
}
.boardBanner {
    display: none !important;
}
.post.reply {
    font-family: 'Open Sans', sans-serif;
    font-size: 18px !important;
    font-weight: 400 !important;
    font-style: normal;
    background: #212121 !important;
    color: #f2f2f2 !important;
    width: 100% !important;
    border: 0px solid #212121 !important;
    padding: 4px !important;
    text-decoration: none !important;
    margin-top: auto !important;
    margin-right: 0px !important;
    margin-left: 0px !important;
    line-height: 1.6em !important;
    text-align: left !important;
}
.thread {
    margin-top: -10px !important;
    margin-right: -17px !important;
    margin-left: 0px !important;
}
div.postContainer.opContainer {
    display: inline-block !important;
    width: 100% !important;
    margin: auto !important;
}
.inline {
    padding-left: 0px !important;
    padding-right: 5px !important;
    border: 0 !important;
}


.inline .post.reply {
    background-color: #212121 !important;
    border: 0px !important;
}

span.subject {
    color: #F0C674 !important;
}

span.postertrip, span.nameBlock {
    color: #B294BB !important;
}
a {
        text-decoration: none !important;
        color: #f2f2f2 !important;
    }

/*hover colors*/
    a:hover,
    div.post div.postInfo
    span.postNum a:hover,
    .posteruid .hand:hover {
        color: #B294BB !important;
    }
/*desperation*/
.postertrip,
            .useremail:not(:hover) .name:not(.capcode),
            .useremail:not(:hover) .postertrip:not(.capcode) {
                color: #fff !important;
font-family: 'Open Sans', sans-serif;
    font-weight: 700 !important;
font-size: 18px !important;
    }
/*email trips */
.postertrip:not(.capcode),.useremail:not(:hover),.useremail {
        text-decoration: none !important;
    }
/* Post numbers. */
span.postNum a {
    color: #B294BB !important;
}

/* Poster's name. */
span.name {
    font-size: 18px !important;
    color: #B294BB !important;
    font-family: 'Open Sans', sans-serif;
    font-weight: 700 !important;
}
/* Date and time of the posts. */
span.dateTime {
    color: #8eb2cf !important;
}

/* Quote links. */
a.quotelink {
    color: #8eb2cf !important;
    text-decoration: none !important;
}

/* Link to posts quoting the current post. */
a.backlink {
    float: right;
    color: #fff !important;
}

/* Greentext. */
span.quote {
    color: #afe39b !important;
}

/* Post menu button. */
.post .menu-button {
    float: right;
    position: relative;
    left: 0px;
}

/* Hide "hide reply" buttons. */
.postInfo > input,
a.hide-reply-button > span {
    display: none !important;
}

/* Relative to post info, i.e. Post name, tripcode, post date, etc. */
.postInfo a,
div.postInfo {
    color: #fff !important;
    font-size: 18px !important;
}

/* Relative to file info, i.e. filename, file size, resolution, etc. */
div.fileText {
    font-family: 'Segoe UI', sans-serif !important;
    font-size: 0px !important;
    color: #f2f2f2 !important;
}

/* Fix post previews. */
#qp {
    border: 0 !important;
    box-shadow: none !important;
}


/* Relative to the quick reply. */
#qr {
    background: #2f2f2f !important;
    color: #eee !important;
    border: none !important;
    border-radius: 2px !important;
    box-shadow: none !important;
    transition: all 0.2s ease 0s;
}
#qr form {
    bottom: 12px !important;
    right: 0% !important;
    width: 300px !important;
    padding: 4px !important;
    margin-top: 23px !important;
    padding-top: 10px !important;
    padding-bottom: 3px !important;
    background: #2f2f2f !important;
    border-color: #2f2f2f !important;
    border-radius: 4px !important;
}
#dump-button {
    background: #3d3d3d !important;
    border-color: #3d3d3d !important;
    color: #eee !important;
}

/* Color of the fields. */
#qr .field {
    background: #222 !important;
    border-color: #2f2f2f !important;
    color: #2f2f2f !important;
}

/* Movable QR? */
#qr span.move {
    display: none !important;
}

/* Remove menu */
#qr select {
    display: none !important;
}

/* Make the name field become invisible when there's something in it... */
#qr input.field:nth-child(2) {
    background: #222 !important;
    color: #222 !important;
}

/* ... And then make it visible again when you hover over it. */
#qr input.field:nth-child(2):hover {
    background: #222 !important;
    color: #2f2f2f !important;
}

/* Remove bar above the fields. */
#qr > div {
    position: fixed !important;
}

/* Relative to 4chan X's menu */
#fourchanx-settings.dialog,
fieldset legend,
a.tab-main.tab-selected {
    font-family: sans-serif !important;
    font-size: 18px !important;
    background: #212121 !important;
    color: #000000 !important;
}

/*** Animations ***/

/* Change backlinks to >> and animate them. */
a.backlink {
    text-decoration: none !important;
    font-size: 18px !important;;
}
.backlink:after {
    font-size: 18px !important;
    padding: 1px;
    opacity: 0.4;

    text-decoration: none !important;
}
.backlink:hover::after,
.backlink.inlined::after {
    opacity: 1;

}

div.inline {
background-color: #212121;
}

/* ADMIN */

.nameBlock.capcodeMod span.name, span.capcodeMod a span.name, span.capcodeMod span.postertrip, span.capcodeMod strong.capcode {
color: #DC4242!important;
}

img.identityIcon {
border: none;
display: none;
}

/*THREAD WATCHER - HIDDEN*/

.watch-thread-link {
display: none;
}

.watch-thread-link.watched {
display: none;
}

/* Uncomment to bring watching back. */

/* :root.yotsuba-b .watch-thread-link {
background-image: url(https://u.teknik.io/WCk4C.svg);
}

:root.yotsuba-b .watch-thread-link.watched {
background-image: url(https://u.teknik.io/2OBD7.svg);
} */

/* REPLY LINK */
a.replylink:not(:hover), div#absbot a:not(:hover) {
color: #ffffff! important;
}

.qmark-you {
color: #F06292;}

.backlink:not(:first-child) {
   margin-left: -1.0rem;
   transition: all .1s linear .2s;
   opacity: 0;
}
/* Post menu animations. */
.postContainer:hover .backlink {
    margin-left: 0rem;
    transition-delay: .1s;
    opacity: 100;
}
.post.reply a.menu-button {
    opacity: 0;
    transition: all .2s;
}
.post.reply:hover .menu-button {
    opacity: 100;
    transition: all .1s;
}
/* QR animations. */
#qr.dialog.has-captcha.autohide,
#qr.dialog.autohide {
    top: -300px !important;
    height: 21px !important;
    transition: all 0.7s ease 0s;
}
}
