css:
@media screen and (min-width: 1679px) {
    .right-col{
    	width: 300px;
    }
    .search-bar-friends{
    	width: 300px;
    }
    .mid-col.right-panel {
    	right: 300px;
    }
    .label-name {
    text-align: left;
    padding-left: 11px;
    }
    .add-friend-to-list{
        position: fixed;
        bottom: 0;
        background-color: #101010;
        font-size: 12px;
        height: 20px;
    	line-height: 27px;
    	z-index: 10000;
    }
    .add-friend-to-list .ic{
        font-size: 17px;
        width: 0;
        height: 0;
        line-height: 9px;
        border-width: 2px;
        margin-top: 9px;
        background-color: transparent;
        border: 0;
        margin-right: 25px;
        margin-left: 5px;                                   
    }
    .match-mode-modal .content-scroll {
        max-width: 1360px;
        margin: 0 auto;
	}
    #intercom-container#intercom-container .intercom-launcher-frame {
    	right: 315px !important;
	}
	#intercom-container#intercom-container .intercom-messenger-frame {
    	right: 315px !important;
	}
	ul.no-bullet.friends-list {
    	margin-bottom: 100px;
	}
	.right-col .content-scroll {
    	padding: 0 0 44px;
	}
    .more-chat-windows, .chat-window {
    	margin-right: 390px;
	}
}
