/*************************************
	START SARCASM STATION DARK MODE
**************************************/

@-moz-document domain("sarcasmstation.com") {

/**************************
	START GLOBAL STYLING
***************************/

/* Body Background */
html, body {
    background: #232323;
    font-family: "Roboto","Cairo",sans-serif;
}

/* Header and Navigation */
.container-fluid>header {
    background: #3c3c3c;
}

.container-fluid>header .bottom-border {
    background: #464646;
}

nav.menu-main ul li a {
    font-family: "Roboto",sans-serif;
    font-style: normal;
    font-size: 15px;
    transform: scale(1, 1);
    font-weight: 700;
}

/* Social Icons */
.social-header {
	border-right: #2b2b2b 1px solid;
}

.social-header a {
    color: #ffffff;
    margin-right: 10px;
}

/* Login Buttons */
.logn-wrap {
    font-family: "Roboto",sans-serif;
	font-weight: 700;
}

.logn-wrap a.sign-btn {
    background: #33cc33;
}

/* Notification Menu */
.noti-menu .noti-wrap, .mess-menu .mess-wrap {
    background: #151515;
	border: 1px solid #272727;
    box-shadow: rgba(0,0,0,0.1) 0px 2px 7px 3px;
    padding: 5px 0px;
	right: -130px;
	top: 45px;
    height: auto;
}

.noti-menu .noti-wrap:after, .mess-menu .mess-wrap:after {
	background: url("http://i.imgur.com/79CeapF.png") top center no-repeat;
    top: -10px;
	right: 127px;
}

.noti-pagination {
	display: none;
}

.noti {
	font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    color: #747474;
    background: none;
    border-top: 1px solid #232323;
    position: relative;
    font-size: 18px;
    padding-bottom: 15px;
}

.noti:hover {
    background: #0e0e0e;
}

.swiper-loop .noti:first-child {
     border-top: none;
}

.noti-box-wrap {
	padding-bottom: 0;
}

.noti-time {
    padding-left: 70px;
    text-align: left;
	color: #4a4a4a;
}

/* Comics View Control */
.posts-controls-wrap a {
    font-family: "Roboto",sans-serif;
	font-weight: 700;
    font-size: 15px;
    background: #151515;
}

.trend-posts {
    border-radius: 5px 0 0 5px !important;
}

.lame-posts {
    border-radius: 0 5px 5px 0 !important;
}

.normal-view, .masonry-view {
    background: #151515;
}

/* Comics List */
.main-data:not(.single) .masonry-layout.post-list-wrap article, .main-data:not(.single) .masonry-layout.post-list-wrap .ads-container, .post-list-wrap article, .post-list-wrap .ads-container {
    background: #151515;
    border: 1px solid #272727;
    box-shadow: rgba(0,0,0,0.1) 0px 2px 7px 3px;

}


/* White Box Restyling for Login and Upload */
.white-box {
	background: #0e0e0e;
}

.white-box .cont input[type=email], .white-box .cont input[type=password], .white-box .cont input[type=text], .white-box .cont input[type=url] {
    background: #191919;
    border: 1px solid #2f2f2f;
}

.white-box .cont .borderd {
	border-top: 1px solid #1f1f1f;
    margin-top: 40px;
    padding-top: 30px;
}

/* H Titles Restyling */
h1, .h1, h2, .h2, h3, .h3 {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
}

/************************
	END GLOBAL STYLING
*************************/


/***********************
	START LIST STYLE
************************/

/* Comic Header */
.post-list-wrap article header {
    background: #1f1f1f;
    border-radius: 5px 5px 0 0;
    border-bottom: 1px solid #272727;
}

/* Author Img */
.user-post .user-img {
    margin-bottom: 15px;
}

/* Author Name */
.user-post a>span {
	margin-top: 22px;
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    font-size: 18px;
    text-transform: capitalize;
}

/* Comic Title */
.user-post h2 {
    margin-top: 7px;
}

.post-caption {
    font-family: "Roboto", "Cairo", sans-serif;
    font-weight: 700;
}

/* Comic Time */
.post-list-wrap article header .time {
	font-family: "Roboto",sans-serif;
    text-transform: uppercase;
    font-size: 10px;
    background: #151515 !important;
    border: 1px solid #272727;
    padding: 5px 15px;
    border-radius: 5px;
    top: 35px;
    right: 20px;
}

/* Comic Buttons */
button.fav-star {
    background: #33cc33;
    color: #FFF;
    right: 10px;
    top: 0px;
    font-size: 17px;
    border-radius: 0 0 5px 5px;
    padding: 2px 7px;    
}

/* Comic Tools */
.post-tools {
    width: 210px;
    position: absolute;
    right: 0px;
    height: calc(100% - 115px);
    top: 115px;
}

/* Comic Rating Box */
.like-box {
	background: none;
    border-bottom: 1px solid #1f1f1f;
}

.main-data:not(.single) .masonry-layout .like button, .like button, .main-data:not(.single) .masonry-layout .unlike button, .unlike button {
	background: none;
}

.like button:after {
	content: "?";
    font-size: 40px;
}

.unlike button:after {
	content: "?";
    font-size: 40px;
}

.like input, .unlike input {
    font-family: "Roboto",sans-serif;
	font-weight: 700;
    padding-left: 12px;
}

/* Comic Comments Box */
.post-comments-wrap {
    top: 97px;
    bottom: 140px;
    background: none;
    border-bottom: 1px solid #1f1f1f;
}

.comment-block {
    background: none;
    border-bottom: 1px solid #1f1f1f;
}

.swiper-slide .comment-block:after {
    content: '';
    display: block;
    position: absolute;
    background: #232323;
    width: 5px;
    height: 100%;
    top: 0;
    left: 0;
    right: 0;
}

a .comment-user {
	padding-top: 0px;
    font-family: "Roboto","Cairo",sans-serif;
	font-weight: 700;
}

.comment-text {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 600;
    font-size: 12px;
    margin-top: -12px;
}

a.post-addcomment {
    font-family: "Roboto",sans-serif;
	font-weight: 700;
}

/* Comic Footer Box */
.post-footer {
    bottom: 15px;
}

/* Comic Share Buttons */
.post-social a {
    width: 85px;
    height: 35px;
	border-radius: 5px;
    margin: 0px;
}

.jssocials-share-link {
    padding: .6em .6em;
}

.jssocials-share.jssocials-share-facebook {
    padding-left: 0px;
}

/* Top Users Box */
.user-points {
    background: #151515;
    border: 1px solid #272727;
    border-radius: 5px;
    box-shadow: rgba(0,0,0,0.1) 0px 2px 7px 3px;
    height: 410px;
}

/* Top Users Tab */
.tab-head {
	font-family: "Roboto",sans-serif;
    font-weight: 700;
    background: #1f1f1f;
    color: #fff;
    border-bottom: transparent;
    border-radius: 5px 5px 0 0;
    height: 39px;
}

/* Top Users Active Tab */
.tab-head.active {
	background: #151515;
    color: #fff;
    border-bottom: #151515 5px solid;
    border-top: 1px solid #272727;
    border-left: 1px solid #272727;
    border-right: 1px solid #272727;
    height: 41px;
}

/* Top Users List */
.points-user-name {
    margin-top: 0px;
}

.points-user-name, .points-user-number {
	font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
}

a.user-points-link {
    padding: 15px 0;
}

a.user-points-link:after {
    background: #1f1f1f;
    right: 0px;
    left: 0px;
}
/*********************
	END LIST STYLE
**********************/


/***********************
	START GRID STYLE
************************/

/* [GRID] Fav a Comic */
.main-data:not(.single) .masonry-layout button.fav-star {
    background: #33cc33;
    color: #FFF;
    right: 10px;
    top: 10px;
    font-size: 17px;
    border-radius: 5px;
    padding: 2px 7px;
}

/* [GRID] Author Name */
.main-data:not(.single) .masonry-layout .user-post a>span {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    text-transform: capitalize;
	border: none;
	margin-top: 0;
    bottom: 45px;
    left: 80px;
}

/* [GRID] Author Img */
.main-data:not(.single) .masonry-layout .user-post .user-img {
	margin: 0 0 0 15px;
}

/* [GRID] Comic Title */
.main-data:not(.single) .masonry-layout .user-post h2 {
	left: 90px;
    bottom: 0px;
}

/* [GRID] Comic Time */
.main-data:not(.single) .masonry-layout.post-list-wrap article header .time {
	background: #151515 !important;
    border-top: 1px solid #272727;
    border-left: 1px solid #272727;
    border-bottom: none;
    border-right: none;
}

/* [GRID] Comic Rating Box */
.main-data:not(.single) .masonry-layout .like-box {
    margin-right: 50px;
    padding-bottom: 15px;
    border-bottom: 1px solid #1f1f1f;
}

.main-data:not(.single) .masonry-layout .like button:after {
    font-size: 25px;
}

.main-data:not(.single) .masonry-layout .unlike button:after {
    font-size: 25px;
}

/* [GRID] Comic Comment Count */
.main-data:not(.single) .masonry-layout a.comment-count {
	background: #1d1d1d !important;
    border: 1px solid #272727;
    padding: 5px 15px;
    border-radius: 5px;
    font-family: "Roboto",sans-serif;
    text-transform: uppercase;
    text-decoration: none !important;
    height: 30px;
    bottom: 120px;
}

/* [GRID] Comic Footer Box */
.main-data:not(.single) .masonry-layout .post-footer {
	height: 90px;
}

.main-data:not(.single) .masonry-layout .post-social {
    border-left: 1px solid #1f1f1f;
    height: 137px;
    bottom: 32px;
}
/*********************
	END GRID STYLE
**********************/


/***********************
	START COMIC VIEW
************************/

/* Edit Comic Box */
.edit-box-wrap {
    right: 45px;
    top: 0px;
}

.edit-box-wrap i {
    background: #3c3c3c;
    color: #FFF;
    font-size: 25px;
    border-radius: 0 0 5px 5px;
    padding: 2px 5px;
}

.edit-box {
    background: #3c3c3c;
    padding: 10px;
    border-radius: 4px;
}

.edit-box ul li {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    border-bottom: #333333 1px solid;
}

/* Comic Share and Rating */
.post-like-share {
	padding-top: 0px;
    background: #151515;
    border: 1px solid #272727;
}

/* Comic Add Comment Box */
.post-comments textarea {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    background: #151515;
    border: #272727 1px solid;
}

.has-error .help-block {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    color: #e61c1c;
}

.post-comments button.comment {
    font-weight: 700;
}

/* Comic Replies Box */
.single .post-comments .comments-loop {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    border: 1px solid #272727;
    border-radius: 5px;
}

.single .post-comments .comment-block {
    background: #151515;
    border-bottom: #272727 1px solid;
}

.single a .comment-user {
    padding-top: 0px;
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
}

.user-post h2, .comment-text, .single .comment-text {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
}

.comment-block .reply-wrap {
    border-left: #232323 5px solid;
}

.comment-block .new-reply {
    border: #232323 1px solid;
    background: #232323;
}

/*********************
	END COMIC VIEW
**********************/

/*************************
	START PROFILE VIEW
**************************/

/* User Profile Card */
.user-area {
	background: #151515;
    border: 1px solid #272727;
    box-shadow: rgba(0,0,0,0.1) 0px 2px 7px 3px;
}

.user-img {
    width: 200px;
    height: 200px;
}

.user-img img {
    height: 200px;
    border-radius: 50%;
    max-height: 200px;
    min-height: 200px;
    max-width: 200px;
    min-width: 200px;
}

.user-img span {
    display: none;
}

a.area-user-name {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    text-decoration: none;
    overflow: visible;
    width: 100%;
    text-align: center;
    font-size: 25px;
}

.divider {
    background: none;
    border-bottom: 1px solid #272727;
}

.user-area-points p {
	font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
}

.user-area-points p span {
	font-family: "Roboto","Cairo",sans-serif;
    font-weight: 900;
}

a.user-follow {
    display: inline-block;
    width: 45%;
    text-align: center;
    color: #c3c1c1;
	font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    font-size: 16px;
}

a.user-follow img {
    display: none;
}

/* User Cover Box */
.cover-wrap {
	background-color: #151515 !important;
}

.cover-wrap:after {
    content: "";
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    text-align: right;
    display: block;
    padding: 10px 15px;
    color: rgba(0, 0, 0, 0.36);
}

/* User Profile Tabs */
.pro-tabs-container {
    height: 45px;
	background: #3c3c3c;
    border-radius: 3px;
}

.pro-tabs-container a {
    line-height: 45px;
    color: #232323;
	font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    font-style: normal;
}

.pro-tabs-container a:after {
	background: none;
    border-left: 1px solid #272727;
}

/* User About Box */
.about-tab {
	background: #151515;
    border: 1px solid #272727;
    box-shadow: rgba(0,0,0,0.1) 0px 2px 7px 3px;
}

.about-tab>div {
    background: none;
    border-bottom: 1px solid #272727;
}

.about-tab>div:last-child {
    background: none;
    border-bottom: none;
}

.about-tab h2 {
	font-style: normal;
    text-decoration: none;
    font-size: 20px;
    text-transform: uppercase;
}

.about-tab p {
    padding: 0;
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
}

/* User Edit Profile Box */
.edit-tab {
	background: #151515;
    border: 1px solid #272727;
    box-shadow: rgba(0,0,0,0.1) 0px 2px 7px 3px;
}

.edit-tab>div {
    background: none;
    border-bottom: 1px solid #272727;
}

.edit-tab h2 {
	font-style: normal;
    text-decoration: none;
    font-size: 20px;
}

.edit-tab .about-edit {
	font-family: "Roboto",sans-serif;
    font-weight: 700;
    text-transform: uppercase;
    font-size: 10px;
    background: #151515 !important;
    border: 1px solid #272727;
    padding: 5px 15px;
    border-radius: 5px;
    color: #3c3;
}

.u-name, .u-pass {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    font-size: 20px;
}

form.interest label {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
}

.edit-tab input {
	font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    background: #232323;
    border: 1px solid #272727;
	border-radius: 3px;
}

form.edit-user input[type=submit], form.edit-user input[type=reset], form.interest input[type=submit], form.interest input[type=reset] {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    border-radius: 3px;
	text-transform: capitalize;
}

a.profile-up {
    color: #29b529;
    padding-top: 40%;
    background: rgba(0,0,0,0.7);
}

/***********************
	END PROFILE VIEW
************************/

/*************************
	START UPLOAD VIEW
**************************/

/* Upload Form */
.upload-form {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
}

/* Drop Image Box */
.up-cont {
    width: 100%;
    min-height: 300px;
    position: relative;
    border: 1px solid #272727;
    border-radius: 3px;
    padding: 5px;
    font-weight: 700;
}

/* Submission Type Tabs */
.white-box .nav-tabs>li>a {
	font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    text-transform: uppercase;
    border: 1px solid #272727;
}

/* Form Inputs for Article */
.form-control {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    color: #555;
    background-color: #151515;
    background-image: none;
    border: 1px solid #272727;
}

.up-cat {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    color: #555;
    background-color: #151515;
    border: 1px solid #272727;
}

.fr-box {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
    color: #555;
    background-color: #151515;
    border: 1px solid #272727;
}

.fr-box.fr-basic .fr-wrapper {
    background: #151515;
	border-radius: 0 0 5px 5px;
}

.btn.btn-primary {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
}

.white-box .cont input[type="submit"] {
    font-family: "Roboto","Cairo",sans-serif;
    font-weight: 700;
	text-transform: capitalize;
}

/***********************
	END UPLOAD VIEW
************************/

}

