css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.b92.net") {
body {
    background-color: #0d0d0d !important;
}
.container.main {
    background-color: #1a1a1a !important;
}
.important-news ul li h4 a,
.news-box4 .text h3 a,
.slick-slide div h3 a,
.right .module .news-box-list .box-item h3 a,
.tabs-news-list .panes ul li a,
.news-box-list .box-item .text h3 a,
.weather-box li div a,
.temperature span,
.article-header h1,
.article-header p,
.item-page .article-header small,
.item-page p,
.blog .item .text h2 a,
.blog .item .text p,
.right .module .news-box-list-2 li a,
#rate-nav .selected-tab a,
#rate-nav span,
.comments li,
.rate-up,
.rate-dn,
.right form,
.web-preporuke a,
.top-info span,    
.page-title h1,
.info ul li span,    
.current span,
.send-comment-page p,
.search-field label {
    color: #FFFFFF !important;
}
.article-header h1 a,
.tabs-news-list .tabs li.ui-tabs-active a {
    color: rgb(102, 0, 204);
    text-shadow: 1px 1px 0 #FFFFFF, -1px -1px 0 #FFFFFF, 1px -1px 0 #FFFFFF, -1px 1px 0 #FFFFFF;
}
.article-header h1 a:hover {
    text-decoration: none !important;
}

/* Pošalji */ 
.send-comment-page #komentar-forma input[type=submit] {
    background-color: #D50000 !important;
}
}
