css:
@-moz-document domain('vg.no') {
a:visited {
    color: #d00;
}

body {
    background-color: #000000!important;
    color: #d4d4d4;
}

.desktop .reg-article .article-main-lead .rsGCaption, .desktop .reg-article .article-main-lead figcaption,
.reg-article #byline ul {
    color: #d4d4d4;
}

.desktop #top-bar {
    background-color: #000;
    border-bottom: 6px solid #404040;
}

.desktop #top-bar .section-nav-topbar a, .desktop #top-bar .top-bar-content a {
    color: #d4d4d4;
}

.desktop #top-bar .login-form .logout-btn {
    color: #ed1f24;
}

.desktop #top-bar .login-form {
    background: #4c4c4c;
    padding: 0 0 0 7px;
}

.desktop.front #page-wrapper {
    background: #3a3a3a;
    color: #d4d4d4;
}

.desktop.section-forsiden .main-navbar-content {
    box-shadow: inset 0 13px 0 #3a3a3a, inset 0 64px 0 #2b2b2b;
}

.drfront .insert-title, .drfront .insert-title a, .drfront h3, .drfront h3 a {
    color: #d4d4d4;
}

.drfront .insert-title {
	color: #d4d4d4;
}

.desktop .navbar-content {
    border-color: #424242;
}

.desktop .section-nav-submenu li {
    border-color: #424242;
}

.desktop .section-nav-submenu li:hover {
    background: #dd0000;
}

.desktop .section-nav-submenu li.submenu-link+li, .desktop .section-nav-submenu li:first-child {
    border-color: #424242;
}

.direkte-front-box .items h4 {
    color: #d4d4d4;
}

.drfront .df-container.df-container-skin-default .article-extract.bg-default {
    border-top: 5px solid #565656;
    padding-top: 15px;
}

.reg-grid-main-content {
    background-color: #3a3a3a;
}

.desktop .section-nav-submenu a {
    color: #d4d4d4;
}

.drfront .df-container-skin-sporten .article-content .insert-title, .drfront .df-container-skin-sporten .article-content .insert-title a, .drfront .df-container-skin-sporten .article-content h3, .drfront .df-container-skin-sporten .article-content h3 a {
    color: #fff;
}

.drfront .df-container-skin-sporten {
    background: #3a3a3a;
    color: #d4d4d4;
}

.drfront .df-article h3 a, .drfront .df-article h4 a {
    color: #fff;
}

.df-container-inner {
    background: #3a3a3a;
}

#tvGuide .channel {
    border-color: #424242;
    background: #212121;
}

#tvGuide .channel h3, #tvGuide #tvfavorites h3 {
    color: #d4d4d4;
}

#content .proglist .channel {
    background: #212121;
    border-color: #424242;
    color: #fff;
}

#content .proglist .channel li {
    border-color: #424242;
}

#content .proglist .channel li:hover {
    background: #525252;
}

.ad-wrapper,
.desktop .reg-grid-main .ad,
.desktop .ad[id*=topboard],
.pag-front .reg-grid-aside .ad,
.finn-dist,
.inline-ad-text,
#wid-aside-taxonomy .ad {
    display: none!important;
}

.desktop .reg-article .reg-grid-aside {
    margin-top: 0;
}

.desktop .article-body-text .tip-box, .desktop .article-body-text .widget {
    border-color: #3a3a3a;
    background: #7b7b7b;
    color: #fff;
}

.direkte-front-box .items.item-vgtv .content,
.section-sport .reg-grid-container, .section-sport .reg-grid-main-content,
.section-sport.desktop aside .widget {
    background-color: #3a3a3a;
}

.section-sport.desktop aside .widget div>div:hover, .section-sport.desktop aside .widget li:hover {
    background-color: #3a3a3a;
}

.widget h2,
aside .widget a, .wid-special .widget a,
.wid-articles-video-list div.video-unit h4 {
    color: #d4d4d4;
}

aside .widget, .wid-special .widget {
    border-color: #3a3a3a;
    background: #7b7b7b;
    color: #fff;
}

aside .widget li, .wid-special .widget li {
    border-color: #3a3a3a;
}

aside .widget li:hover, .wid-special .widget li:hover {
    background: #3a3a3a;
}

.widget a:visited p.title, .widget a:visited span.preamble, .widget a:visited time,
.widget.list article a, .widget.image-list article a,
.desktop #article-bottom-elements .related article a {
    color: #d4d4d4;
}
}
