css:
@-moz-document domain("app.asana.com") {

#bg_pattern {
    background: #e6e5e3;
}
a,
.react-mount-node a,
.react-mount-node .RichHelpMenuItem-title {
    color: #987545;
}
a:hover,
.ancestry-container .ancestor-projects .token:hover,
.react-mount-node a:hover,
.react-mount-node a:focus,
.react-mount-node .topbar-myTasksButton:hover:not(.is-loading),
.react-mount-node .topbar-notificationsButton:hover:not(.is-loading),
.react-mount-node .topbar-myDashboardButton:hover:not(.is-loading),
.react-mount-node .topbar-settingsMenuButton:not(.is-loading):hover,
.project-owner.set:hover .person-card-view .person-info .name,
.members-dropdown-member-row:hover .member-name,
.calendar-range-menu-dropdown-menu .months .month.active,
.detailsPaneProjectTokenizerView .placeholder:hover,
.react-mount-node .RichHelpMenuItem-title:hover,
.react-mount-node .RichHelpMenuItem-title:focus {
    color: #c7a577;
}
.redesign-assigned-to-container .property.value-set:hover .taskDueDateView-calendarIcon, 
.redesign-due-date-container .property.value-set:hover .taskDueDateView-calendarIcon {
    border-color: #c7a577;
}

.details-followers .followers.token-area:not(.focused) .tokenView .tokenView-remove:hover {
    background-color:  #c7a577;
}

.buttonView.buttonView--secondary,
.form .footer .new-button,
.react-mount-node .Button.Button--secondary:not(.Button--disabled),
.buttonView.buttonView--primary.is-disabled,
.buttonView.buttonView--primary {
    background: #fff;
    border-color: #c7a577;
    color: #c7a577;
    fill: #c7a577;
    border-radius: 3px;
    line-height: 35px;
}
.buttonView.buttonView--secondary:focus,
.buttonView.buttonView--secondary:hover,
.buttonView.buttonView--primary:focus, 
.buttonView.buttonView--primary:hover,
.react-mount-node .Button.Button--secondary:not(.Button--disabled):focus:not(.Button--disabled), 
.react-mount-node .Button.Button--secondary:not(.Button--disabled):hover:not(.Button--disabled),
.calendar-range-menu-dropdown-menu .months .month:hover {
    border-color: #c7a577;
    background-color: #c7a577;
    box-shadow: none;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    color: #ffffff;
    fill: #c7a577;
}

.assign-from-grid-link:focus, 
.assign-from-grid-link:hover {
    border-color: #c7a577;
    cursor: pointer;
    box-shadow: none;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    color: #c7a577;
    fill: #c7a577;
}
.project-owner.set:hover,
.details-followers .followers.token-area:not(.focused) .tokenView:hover {
    box-shadow: 0 0 0 3px #c7a577;
    -moz-box-shadow: 0 0 0 3px #c7a577;
    -webkit-box-shadow: 0 0 0 3px #c7a577;
}
.react-mount-node .topbarSettingsMenu-upgrade {
    border-color: #c7a577;
    color: #c7a577;
    fill: #c7a577;
    display: none;
}
.react-mount-node .topbarSettingsMenu-upgrade:hover {
    border-color: #c7a577;
    background-color: #c7a577;
    color: #ffffff;
    fill: #c7a577;
}
.react-mount-node .Sidebar {
    background-color: #333333;
}
.react-mount-node .SidebarHeader {
    margin-bottom: 15px;
}
.react-mount-node .SidebarItemRow,
.react-mount-node .SidebarTeamMembershipHeader-teamName,
.react-mount-node .SidebarDefaultSearchRow {
    color: #d4d4d4;
    fill: #d4d4d4;
}
.react-mount-node .CustomScrollbarContainer-thumb {
    background-color: #222;
}
.react-mount-node .SidebarTeamDetailsProjectsList-header,
.react-mount-node .SidebarTeamsList-header,
.react-mount-node .SidebarTeamMembershipHeader {
    color: #ffffff;
    margin-bottom: 10px;
    border-bottom: 1px solid #636363;
    padding-bottom: 15px;
    margin-top: 25px;
}
.react-mount-node .SidebarTeamMembershipHeader {
    border-bottom-width: 3px;
    margin-bottom: 15px;
}
.react-mount-node .SidebarTeamsList-header {
    margin-left: 0;
    margin-right: 0;
    padding-left: 15px;
    padding-right: 15px;
}
.react-mount-node .SidebarTeamDetailsProjectsList-addProjectButton,
.react-mount-node .SidebarTeamsList-addButton,
.react-mount-node .SidebarTeamMembersList-addButton,
.react-mount-node .SidebarTeamSettingsMenu-button,
.react-mount-node .SidebarTeamDetailsProjectsList-showMoreProjectsLink,
.react-mount-node .Sidebar-browseMoreTeamsLink,
.react-mount-node .SidebarRecentsAndFavorites-showMoreOrLessButton,
.react-mount-node .SidebarHeader-closeIcon,
.react-mount-node .SidebarTeamMembersExpandedList-closeButton,
.react-mount-node .SidebarTeamMembersExpandedList-pencilButton {
    border-color: #d4d4d4;
    color: #d4d4d4;
    fill: #d4d4d4;
}
.react-mount-node .SidebarItemRow:active,
.react-mount-node .SidebarItemRow:hover,
.react-mount-node .SidebarTeamDetailsProjectsList-showMoreProjectsLink:hover,
.react-mount-node .SidebarTeamDetailsProjectsList-showMoreProjectsLink:focus,
.react-mount-node .Sidebar-browseMoreTeamsLink:hover,
.react-mount-node .Sidebar-browseMoreTeamsLink:focus,
.react-mount-node .SidebarRecentsAndFavorites-showMoreOrLessButton:hover,
.react-mount-node .SidebarRecentsAndFavorites-showMoreOrLessButton:focus,
.react-mount-node .SidebarDefaultSearchRow:active,
.react-mount-node .SidebarDefaultSearchRow:focus,
.react-mount-node .SidebarDefaultSearchRow:hover,
.redesign-assigned-to-container .property:hover .token_name,
.redesign-due-date-container .property:hover .token_name {
    color: #c7a577;
}
.react-mount-node .SidebarTeamDetailsProjectsList-showMoreProjectsLink:hover,
.react-mount-node .SidebarTeamDetailsProjectsList-showMoreProjectsLink:focus,
.react-mount-node .Sidebar-browseMoreTeamsLink:hover,
.react-mount-node .Sidebar-browseMoreTeamsLink:focus,
.react-mount-node .SidebarRecentsAndFavorites-showMoreOrLessButton:hover,
.react-mount-node .SidebarRecentsAndFavorites-showMoreOrLessButton:focus {
    border-bottom: 1px dotted #c7a577;
}
.react-mount-node .SidebarItemRow:hover,
.react-mount-node .DragContainer--isDragging .SidebarItemRow,
.react-mount-node .SidebarDefaultSearchRow:active,
.react-mount-node .SidebarDefaultSearchRow:focus,
.react-mount-node .SidebarDefaultSearchRow:hover {
    background-color: transparent;
}
.react-mount-node .SidebarTeamMembershipHeader-zipIcon {
    margin-right: 3px;
}
.react-mount-node .CircularButton.CircularButton--xxsmall .icon {
    height: 10px;
    width: 9px;
}
.react-mount-node .SidebarTeamMembership.is-expanded+.SidebarTeamMembership:not(.is-expanded)::after,
.react-mount-node .SidebarTeamMembership:not(.is-expanded)+.SidebarTeamMembership.is-expanded::after,
.react-mount-node .SidebarTeamsList-header {
    display: none;
}
.react-mount-node .SidebarItemRow,
.react-mount-node .SidebarDefaultSearchRow {
    height: 32px;
    line-height: 30px;
}
.react-mount-node .SidebarItemRow.is-selected,
.react-mount-node .SidebarDefaultSearchRow.is-selected {
    background-color: #4c4c4c;
    color: #fff;
}
.react-mount-node .SidebarTeamDetailsProjectsList-showMoreProjectsLink {
    margin-top: 10px;
}
.react-mount-node .SidebarItemRow:hover .SidebarItemRow-icon,
.react-mount-node .SidebarTeamMembersExpandedList-headerText {
    color: #fff;
    fill: #fff;
}
.react-mount-node .SidebarTeamMembersExpandedList-header.is-org {
    border: 0;
}
.react-mount-node .menuItem-button {
    line-height: 35px
}
.react-mount-node .menuItem-button:hover,
.dropdown .dropdown-menu a.menu-item:hover,
.dropdown .dropdown-menu a.menu-item.hovered,
.dropdown .dropdown-menu .item:hover,
.dropdown .dropdown-menu .item.hovered {
    background-color: transparent;
    color: #c7a577;
}
.react-mount-node .lunaUiDropdown,
.dropdown {
    border: 0px solid #a1a4aa;
    border-radius: 1px;
    box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.6);
}
.sidebar-mountNode {
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.5);
    z-index: 1;
}
.floatingSelectView-anchor:hover,
.floatingSelectView-anchor:focus,
.ancestry-container .ancestor-link:hover,
.projectMembersSummaryView-moreLink:hover,
#edit_photo .add-picture .picture-action-label:hover {
    border-bottom: 1px solid #c7a577;
    color: #c7a577;
    fill: #c7a577;
}
.ancestor-link:hover .taskDetailsView-ancestorArrow,
.grid_cell_drag_handle:hover,
.redesign-assigned-to-container .property:hover:not(.focused) .taskDueDateView-calendarIcon,
.redesign-assigned-to-container .property:hover:not(.focused) .redesignTaskAssignedToView-unassignedIcon,
.redesign-due-date-container .property:hover:not(.focused) .taskDueDateView-calendarIcon,
.redesign-due-date-container .property:hover:not(.focused) .redesignTaskAssignedToView-unassignedIcon,
.closeDetailsPaneX:hover,
.borderless-button:hover,
.react-mount-node .WaitingOnBanner-removeButton:hover,
.header-row-action-menu-icon:hover,
.react-mount-node .TrackAnythingPromotion-hideIcon:hover,
.calendar-range-menu-dropdown-menu .years .year-button-icon:hover, 
.calendar-range-menu-dropdown-menu .years .year-button:hover,
.cal-row.cal-row-week .cal-col-today:not(.cal-col-project-due) .date .calendar-today-star-icon {
    fill: #c7a577;
}
.today_group .group_header .drop-indicator .expandable:hover,
.upcoming_group .group_header .drop-indicator .expandable:hover,
.new_tasks_group .group_header .drop-indicator .expandable:hover,
.project_group .group_header .drop-indicator .expandable:hover,
.project_group+.null_group .group_header .drop-indicator .expandable:hover,
.react-mount-node .topbarHelpMenuButton-textButton:hover {
    color: #c7a577;
    fill: #c7a577;
}
.itemRowView-detailsButton:hover {
    color: #987545 !important;
    fill: #987545 !important;
}
.react-mount-node .topbar-settingsMenuButton:not(.is-loading):hover .topbar-settingsMenuAvatar {
    box-shadow: 0 0 0 3px #c7a577;
}
.grid-uncompact #grid .task-row:hover .grid_cell_drag_handle,
.grid-uncompact #grid .grid_pseudorow_no_prioritized:hover .grid_cell_drag_handle,
.grid-uncompact #grid .bar-row:hover .grid_cell_drag_handle {
    border-left: 1px solid #c7a577;
    border-left: 0;
}
.grid-uncompact #grid .task-row:hover>td,
.grid-uncompact #grid .grid_pseudorow_no_prioritized:hover>td,
.grid-uncompact #grid .bar-row:hover>td,
.grid-uncompact #grid .grid-row-selected.focused>td {
    border-bottom: 1px solid #c7a577;
    border-top: 1px solid #c7a577;
}
.grid-uncompact #grid .task-row:hover .grid_cell_show_details,
.grid-uncompact #grid .grid_pseudorow_no_prioritized:hover .grid_cell_show_details,
.grid-uncompact #grid .bar-row:hover .grid_cell_show_details {
    border-right: 1px solid #c7a577;
    border-right: 0;
}
.grid-uncompact .grid_cell_drag_handle {
    border-bottom-left-radius: 1px;
    border-top-left-radius: 1px;
}
.grid-uncompact .grid_cell_show_details {
    border-bottom-right-radius: 1px;
    border-top-right-radius: 1px;
}
.grid-uncompact #grid .grid-row-selected:not(.focused)>td,
.grid-uncompact #grid .grid-row-selected:not(.focused)>td .grid-tags-and-date,
.grid-uncompact #grid .grid-row-selected:not(.focused)>td .grid-custom-fields {
    background-color: #efefef;
}
.taskCheckboxNodeView:hover.circularToggleButtonView--toggledOff .circularToggleButtonView-button {
    background: #fff;
    border-color: transparent;
    cursor: pointer;
    box-shadow: 0 0 0 3px #c7a577;
    -moz-box-shadow: 0 0 0 3px #c7a577;
    -webkit-box-shadow: 0 0 0 3px #c7a577;
    color: #c7a577;
    fill: #c7a577;
}
.circularButtonView--active.circularButtonView--onWhiteBackground:focus,
.circularButtonView--active.circularButtonView--onWhiteBackground:hover {
    box-shadow: 0 0 0 3px #c7a577;
    -moz-box-shadow: 0 0 0 3px #c7a577;
    -webkit-box-shadow: 0 0 0 3px #c7a577;
    color: #c7a577;
    fill: #c7a577;
}
.redesign-assigned-to-container .property.value-set:hover,
.redesign-due-date-container .property.value-set:hover {
    box-shadow: 0 0 0 3px #c7a577;
    -moz-box-shadow: 0 0 0 3px #c7a577;
    -webkit-box-shadow: 0 0 0 3px #c7a577;
}
.redesign-assigned-to-container .property:hover,
.redesign-due-date-container .property:hover {
    border-color: #c7a577;
    border-style: dashed;
    color: #c7a577;
}
.genericTokenX:hover {
    background: #c7a577;
    fill: #fff;
}
.react-mount-node .SidebarItemRows-menuItemHighlightCell {
    margin-top: 9px;
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorRed:hover {
    border-left-color: #e8384f
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorOrange:hover {
    border-left-color: #fd817d
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorYellowOrange:hover {
    border-left-color: #fdae33
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorYellow:hover {
    border-left-color: #eecc16
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorYellowGreen:hover {
    border-left-color: #a4c61a
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorGreen:hover {
    border-left-color: #62bb35
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorBlueGreen:hover {
    border-left-color: #37a862
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorAqua:hover {
    border-left-color: #208ea3
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorBlue:hover {
    border-left-color: #4178bc
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorIndigo:hover {
    border-left-color: #7a71f6
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorPurple:hover {
    border-left-color: #aa71ff
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorMagenta:hover {
    border-left-color: #e37cff
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorHotPink:hover {
    border-left-color: #ea4e9d
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorPink:hover {
    border-left-color: #fca7e4
}

.react-mount-node .SidebarItemRow.SidebarItemRow--colorCoolGray:hover {
    border-left-color: #8d9f9b
}

}
