css:
@-moz-document url-prefix(https://bandcamp.com), url-prefix(http://bandcamp.com) {

/*
* url: https://userstyles.org/styles/138360
* rev: 2.4 - 07.Oct.2017
* author: Brendan 'br3n065'
*/

/*[[fbh]]*/
  
.fan-container
.fan-bio-wrapper,
.fan-container .tabs,
.fan-container .owner-controls {
    padding-bottom: 4px !important;
}
#collection-search {
    top: -4px !important;
}

/*[[s]]*/

.collection-item-container .item_link_play_bkgd {
    width: 54px !important;
    height: 34px !important;
}

/*[[fh]]*/

.collection-item-art-container {
    outline: none !important;
    margin-bottom: 4px !important;
}
.collection-item-art {
    border-color: #00ff00 !important;
}
.collection-item-container .item_link_play {
    top: -112px !important;
}
.collection-item-container .item_link_play_widget {
    top: 81px !important;
}
ol.collection-grid .drag-thumb {
    top: 0px !important;
    right: 0px !important;
}
.collection-item-fav-track {
    margin-bottom: 0px !important;
}

/*[[rh]]*/

/*[[ch]]*/
}

