css:
@-moz-document domain('flame.shsh.tw'){
[type~=submit]
{ 
	background-color: #f7e1e1;
    color: #000;
  	cursor: pointer;
}
[type~=submit]:hover
{ 
	background-color: #665b5b;
    color: #fff;
}
.r-box {
    background-color: #fff9f9 !important;
    margin: 100px auto !important;
    color: #444444 !important;
    border-radius:5px !important;
}
.navtool{
    color: #000;
    font-size:1.4em;
}
body{
	text-align:center;
	background: #fff;
    color: #fff !important;
    font-family: "Open Sans","Noto Sans TC","Meiryo","微軟正黑體","Microsoft JhengHei",sans-serif;
}
.navtools{
    width:850px;
    margin: 0px auto;
    padding:15px 20px;
    border-radius:5px;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.16), 0 0 0 1px rgba(0,0,0,0.08);
}

.navtools:first-child{
    width:850px;
    margin: 30px auto 0px auto;
    padding:15px 20px;
    border-radius:5px;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.16), 0 0 0 1px rgba(0,0,0,0.08);
}
input {
    transition: box-shadow .1s ease-in-out;
    line-height: 1em;
    border: 1px solid #e6e6e6;
    padding: .3em .5em;
    border-radius: .28571rem;
    color: #646464;
    background-color: #fff;
    border-color: #e6e6e6;
    overflow: visible;
    box-sizing: border-box;
    resize: none;
    display: inline-block;
    vertical-align: middle;
    background: none;
}
td{
	background: #f7f7f7;
    color: #000;
}
.error{
	background: #f7f7f7;
    color: #000;
}
.navtools{
    color: #fff;
}
tr:last-child>td:last-child{
	background: #000;
    color: #fff;
}
tr:nth-child(1)>:nth-child(odd),
tr:nth-child(2)>td:nth-child(1),
tr:nth-child(2)>td:nth-child(2),
tr:nth-child(2)>td:nth-child(5),
tr:nth-child(2)>td:nth-child(6),
tr:nth-child(2)>td:nth-child(9),
tr:nth-child(2)>td:nth-child(10),
tr:nth-child(2)>td:nth-child(13),
tr:nth-child(2)>td:nth-child(14),
tr:nth-child(2)>td:nth-child(17),
tr:nth-child(2)>td:nth-child(18),
tr:nth-child(2)>td:nth-child(21),
tr:nth-child(2)>td:nth-child(22),
tr:nth-child(3)>td:nth-child(1),
tr:nth-child(3)>td:nth-child(2),
tr:nth-child(3)>td:nth-child(5),
tr:nth-child(3)>td:nth-child(6),
tr:nth-child(3)>td:nth-child(9),
tr:nth-child(3)>td:nth-child(10),
tr:nth-child(3)>td:nth-child(13),
tr:nth-child(3)>td:nth-child(14),
tr:nth-child(3)>td:nth-child(17),
tr:nth-child(3)>td:nth-child(18),
tr:nth-child(3)>td:nth-child(21),
tr:nth-child(3)>td:nth-child(22){
	background:#3f3f3f;
    color: #fff;
}
table{
	margin-left:auto; 
	margin-right:auto;
}


}
