@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
/* font Family. */

#urlbar,
#TabsToolbar .tabbrowser-tab,
menu,
.autocomplete-richlistbox,
#menubar-items,
statuspanel[type="overLink"] .statuspanel-label,
toolbarbutton,
#placeContent,
#placesList,
.editBMPanel_rowLabel,
.textbox-input-box,
.viewer-title.label.toolbar,
.downloadTarget,
.downloadDetails,
#bookmarks-view-children,
#sidebar-search-label,
#sidebar-title,
.menubar-text {
    font-family: 'Open Sans', Verdana, Arial, sans-serif !important;
    /*font-size: 12px !important;*/
}
/* Hide favicons. */

.tabbrowser-tab:not([pinned]) .tab-icon-image {
    display: none !important;
}
/* Remove bookmark icons and add my own */

#bookmarks-menu-button .toolbarbutton-icon {
    display: none !important;
}
#bookmarks-menu-button > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0, 0, 0, 0) !important;
    border: none !important;
    background: transparent !important;
    padding: 0 !important;
    margin-right: 4px !important;
    margin-left: 8px !important;
}
#bookmarks-menu-button .toolbarbutton-menubutton-dropmarker:after,
#bookmarks-menu-button .toolbarbutton-menubutton-dropmarker:before {
    display: none !important;
}
#bookmarks-menu-button:hover .toolbarbutton-menubutton-dropmarker > .dropmarker-icon {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0, 0, 0, 0) !important;
    border: none !important;
    background: transparent !important;
    padding: 0 !important;
    margin-right: 4px !important;
    margin-left: 8px !important;
}
/* Remove browser border */

#main-window {
    margin-top: -2px!important;
    margin-left: -1px!important;
    margin-right: -1px!important;
    margin-bottom: -1px !important;
}
/* Hide tab close button. 

tab .tab-close-button {
    visibility: hidden !important;
}*/
/* Show close tab button on tab hover. 

tab:not([pinned]):hover .tab-close-button {
    visibility: visible !important;
}*/
/* Fix white flash when opening new tabs. */

browser,
browser.display.background_color,
tabbrowser tabpanels {
    background-color: #222 !important;
}
/* Remove seperators between inactive tabs. */

#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before {
    display: none !important;
}
/* Tab font size and padding. */

#TabsToolbar .tabbrowser-tab {
    /*font-size: 12px !important;*/
}
/* Selected tab. */

#TabsToolbar .tabbrowser-tab[selected] {
    background-color: #2F343F !important;
    color: white;
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 0.6);
}
/* Center tab text. */

.tab-text {
    opacity: 0.8 !important;
    text-align: center !important;
}
/* No unsorted bookmarks folder. */

#BMB_unsortedBookmarks + menuseparator,
#BMB_unsortedBookmarks,
#bookmarksMenuPopup > .hide-if-empty-places-result,
#menu_unsortedBookmarks {
    display: none !important;
}
/* Remove some icons. */

toolbarseparator,
menuseparator,
#BMB_bookmarksShowAll,
#BMB_bookmarksToolbar,
#BMB_bookmarksShowAllTop,
#BMB_viewBookmarksSidebar,
#BMB_recentBookmarks,
#edit-controls,
#PanelUI-help,
#PanelUI-update-status,
#PanelUI-footer-fxa,
#nav-bar-overflow-button,
#reader-mode-button,
.urlbar-history-dropmarker,
.urlbar-history-dropmarker:hover,
#identity-box,
#PanelUI-quit,
#urlbar-reload-button,
#urlbar-go-button,
.scrollbutton-up,
.scrollbutton-down,
#urlbar-stop-button {
    display: none !important;
}
.panel-arrowcontent {
    /*background: #272b35 !important;*/
}
#PanelUI-footer-inner,
#PanelUI-customize {
    /*background: #272b35 !important;*/
}
/* Sound icon opacity */

.tab-icon-sound {
    opacity: .8 !important
}
/* Navbar basics */

#nav-bar-customization-target {
    width: 100% !important;
    height: 40px !important;
}
#nav-bar {
    height: 45px !important;
    z-index: 1 !important;
    left: 25% !important;
    right: 25% !important;
    max-width: 50% !important;
    position: fixed !important;
    border-top: 0 !important;
    border-left: 1px solid #545b6b !important;
    border-right: 1px solid #545b6b !important;
    border-bottom: 1px solid #545b6b !important;
    background: #393f4c !important;
    border-bottom-left-radius: 5px !important;
    border-bottom-right-radius: 5px !important;
    -webkit-box-shadow: 0px 10px 14px -11px rgba(0, 0, 0, 0.75);
    box-shadow: 0px 3px 5px 0px rgba(0, 0, 0, 0.75);
}
/* Disable nav button border. */

#back-button#back-button#back-button>image.toolbarbutton-icon {
    box-shadow: none !important;
    border: none !important;
    background: none !important;
}
#forward-button#forward-button#forward-button>image.toolbarbutton-icon {
    box-shadow: none !important;
    border: none !important;
    background: none !important;
}
#back-button:hover,
#forward-button:hover {
    opacity: 0.5 !important;
}
/* Center text in url bar. Centers right click menu in url bar too, need a fix. */

#urlbar {
    margin-left: 10px !important;
    background: none !important;
    border: none !important;

}
.textbox-input-box.urlbar-input-box {
    margin-right: -5px !important;
    margin-left: -5px !important;
    text-align: center !important;
    background: -moz-radial-gradient(circle, #353a48, #1b1e25);
    border: 1px solid #545b6b !important;
    border-radius: 5px !important;
}
/* Remove fog. */

#TabsToolbar:not(:-moz-lwtheme)::after {
    display: none;
}
/* General settings for tabs bar. */

#TabsToolbar {
    border-bottom: 1px solid #545b6b !important;
    position: relative !important;
    z-index: 2 !important;
    margin-left: -5px !important;
    margin-right: -30px !important;
    background-color: #272b35 !important;
    -webkit-box-shadow: 0px 10px 14px -11px rgba(0, 0, 0, 0.75);
    box-shadow: -17.5px 3px 20px 0px rgba(0, 0, 0, 0.6);
}
/* Remove australis. */

#TabsToolbar .tab-background {
    background: none !important;
}
#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
    background: none !important;
    background-image: none !important;
}
#TabsToolbar .tab-background-middle {
    background: none !important;
}
#TabsToolbar .tab-background-start::before,
#TabsToolbar .tab-background-end::before {
    background: none !important;
}
.tab-background-start[selected=true]:-moz-locale-dir(ltr)::after,
.tab-background-end[selected=true]:-moz-locale-dir(ltr)::after {
    background-image: none !important;
}
/* Stretch tabs to full width. */

.tabbrowser-tabs:not([drag=detach]) > .tabbrowser-tab:not([pinned])[fadein] {
    min-width: 100px !important;
    max-width: -moz-available !important;
}
/* Menu button. */

#PanelUI-button {
    background: url('data:image/png;base64,') !important;
    background-color: transparent !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
    box-shadow: none !important;
    margin-left: -3px !important;
    width: 18px !important;
    height: 18px !important;
    border: none !important;
    position: relative !important;
}
#PanelUI-button:hover {
    background: url('data:image/png;base64,') !important;
    background-color: transparent !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
    box-shadow: none !important;
    margin-left: -3px !important;
    width: 18px !important;
    height: 18px !important;
    border: none !important;
}
#PanelUI-button .button-icon,
#PanelUI-button .button-text {
    display: none !important;
}
#PanelUI-menu-button {
    opacity: 0 !important;
    margin-top: 2px !important;
}
/* Autohide nav

#navigator-toolbox > #nav-bar {
    visibility: hidden !important;
    width: 50% !important;
    opacity: 0 !important;
    transition: opacity .3s linear .7s, visibility 0s linear 1s !important;
}
#navigator-toolbox:hover > #nav-bar {
    visibility: visible !important;
    opacity: 1 !important;
    transition-delay: 0s !important;
}*/

#navigator-toolbox > #nav-bar {
    visibility: hidden !important;
    width: 50% !important;
    margin-top: -45px !important;
    transition: margin-top .2s ease-out .7s, visibility 0s ease-in 1s !important;
}
#navigator-toolbox:hover > #nav-bar {
    visibility: visible !important;
    margin-top: 0px !important;
    transition-delay: .2s !important;
}

@-webkit-keyframes bounceInLeft {
    from, 60%, 75%, 90%, to {
        -webkit-animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
        animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
    }
    0% {
        opacity: 0;
        -webkit-transform: translate3d(-3000px, 0, 0);
        transform: translate3d(-3000px, 0, 0);
    }
    60% {
        opacity: 1;
        -webkit-transform: translate3d(25px, 0, 0);
        transform: translate3d(25px, 0, 0);
    }
    75% {
        -webkit-transform: translate3d(-10px, 0, 0);
        transform: translate3d(-10px, 0, 0);
    }
    90% {
        -webkit-transform: translate3d(5px, 0, 0);
        transform: translate3d(5px, 0, 0);
    }
    to {
        -webkit-transform: none;
        transform: none;
    }
}
@keyframes bounceInLeft {
    from, 60%, 75%, 90%, to {
        -webkit-animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
        animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
    }
    0% {
        opacity: 0;
        -webkit-transform: translate3d(-3000px, 0, 0);
        transform: translate3d(-3000px, 0, 0);
    }
    60% {
        opacity: 1;
        -webkit-transform: translate3d(25px, 0, 0);
        transform: translate3d(25px, 0, 0);
    }
    75% {
        -webkit-transform: translate3d(-10px, 0, 0);
        transform: translate3d(-10px, 0, 0);
    }
    90% {
        -webkit-transform: translate3d(5px, 0, 0);
        transform: translate3d(5px, 0, 0);
    }
    to {
        -webkit-transform: none;
        transform: none;
    }
}
@-webkit-keyframes bounce {
    from, 20%, 53%, 80%, to {
        -webkit-animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
        animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
        -webkit-transform: translate3d(0, 0, 0);
        transform: translate3d(0, 0, 0);
    }
    40%,
    43% {
        -webkit-animation-timing-function: cubic-bezier(0.755, 0.050, 0.855, 0.060);
        animation-timing-function: cubic-bezier(0.755, 0.050, 0.855, 0.060);
        -webkit-transform: translate3d(0, -30px, 0);
        transform: translate3d(0, -30px, 0);
    }
    70% {
        -webkit-animation-timing-function: cubic-bezier(0.755, 0.050, 0.855, 0.060);
        animation-timing-function: cubic-bezier(0.755, 0.050, 0.855, 0.060);
        -webkit-transform: translate3d(0, -15px, 0);
        transform: translate3d(0, -15px, 0);
    }
    90% {
        -webkit-transform: translate3d(0, -4px, 0);
        transform: translate3d(0, -4px, 0);
    }
}
@keyframes bounce {
    from, 20%, 53%, 80%, to {
        -webkit-animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
        animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
        -webkit-transform: translate3d(0, 0, 0);
        transform: translate3d(0, 0, 0);
    }
    40%,
    43% {
        -webkit-animation-timing-function: cubic-bezier(0.755, 0.050, 0.855, 0.060);
        animation-timing-function: cubic-bezier(0.755, 0.050, 0.855, 0.060);
        -webkit-transform: translate3d(0, -30px, 0);
        transform: translate3d(0, -30px, 0);
    }
    70% {
        -webkit-animation-timing-function: cubic-bezier(0.755, 0.050, 0.855, 0.060);
        animation-timing-function: cubic-bezier(0.755, 0.050, 0.855, 0.060);
        -webkit-transform: translate3d(0, -15px, 0);
        transform: translate3d(0, -15px, 0);
    }
    90% {
        -webkit-transform: translate3d(0, -4px, 0);
        transform: translate3d(0, -4px, 0);
    }
}
tab {
    -webkit-animation-name: bounce;
    animation-name: bounce;
    -webkit-transform-origin: center bottom;
    transform-origin: center bottom;
    -webkit-animation-duration: 1s;
    animation-duration: 1s;
    -webkit-animation-fill-mode: both;
    animation-fill-mode: both;
}
#titlebar-buttonbox {
    -webkit-animation-name: bounceInLeft;
    animation-name: bounceInLeft;
    -webkit-animation-duration: 1s;
    animation-duration: 1s;
    -webkit-animation-fill-mode: both;
    animation-fill-mode: both;
}
#tab-view-deck:not(:hover):not(#navigator-toolbox) #content-deck {}
