css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("about:(?!(crashes|debugging|networking|performance|profiles|sessionrestore|support|telemetry|welcomeback))[a-z]*$") {
	html {
		background: #393f4c;
		color: #ccc;
	}

	body:not([class]):not([id]) h1 {
		color: #eee;
	}
}

@-moz-document
url("about:newtab"),
url("about:blank") {
	body {
		background: #393f4c;
	}
}
