css:
@-moz-document url-prefix("http://127.0.0.1:51004"), domain("github.com") {

.markdown-body * { font-family: '游ゴシック', 'Yu Gothic', '游ゴシック体', YuGothic, sans-serif; word-break: break-all; word-wrap: break-word; }
.markdown-body a, .markdown-body a:visited { color: #0088cc; text-decoration: none; }
.markdown-body a:hover { color: #005580; text-decoration: underline; }
.markdown-body p { font-weight: 500; margin: 1em 0; line-height: 1.5em; }
.markdown-body small { font-size: 85%; }
.markdown-body strong { font-weight: bold; }
.markdown-body em { font-style: italic; }
.markdown-body h1, .markdown-body h2, .markdown-body h3, .markdown-body h4, .markdown-body h5, .markdown-body h6 { font-weight: bold; line-height: 1.5em; }
.markdown-body h1 > a { color: #000 !important; }
.markdown-body h2 > a { color: #000 !important; }
.markdown-body h3 > a { color: #074f67 !important; }
.markdown-body h4 > a { color: #143548 !important; }
.markdown-body h5 > a { color: #565656 !important; }
.markdown-body h6 > a { color: #77848e !important; }
.markdown-body h1 > a:hover, .markdown-body h2 > a:hover, .markdown-body h3 > a:hover, .markdown-body h4 > a:hover, .markdown-body h5 > a:hover, .markdown-body h6 > a:hover { text-decoration: none !important; }
.markdown-body h1 { font-size: 24px; padding: 0; margin: 2.0em 0 0.8em 0; }
.markdown-body h2 { font-size: 22px; padding: -0.3em 0 0.1em 0; border-bottom: 1px solid #333; margin: 2.0em 0 0.8em 0; }
.markdown-body h3 { color: #074f67; font-size: 20px; padding: 0 0 0.1em 0; margin: 1.4em 0 0.4em 0; border-bottom: 1px dashed #074f67; }
.markdown-body h4 { color: #143548; font-size: 18px; padding: 0 0 0.1em 0; margin: 1.0em 0 0.8em 0; }
.markdown-body h5 { color: #565656; font-size: 16px; padding: 0; margin: 1.0em 0 0.8em 0; }
.markdown-body h6 { color: #77848e; font-size: 16px; padding: 0; margin: 1.0em 0 0.8em 0; }
.markdown-body ul, .markdown-body ol { font-weight: 500; margin: 0.2em 0 0.2em 0.5em; padding: 0; list-style: disc; }
.markdown-body ol { list-style: decimal; }
.markdown-body li { margin: 0.2em 0 0.2em 0.5em; padding: 0; line-height: 1.5em; }
.markdown-body ul ul, .markdown-body ul ol, .markdown-body ol ol, .markdown-body ol ul { list-style: circle; }
.markdown-body ul ul ul, .markdown-body ul ul ol, .markdown-body ul ol ul, .markdown-body ol ul ul, .markdown-body ol ul ol, .markdown-body ul ol ol, .markdown-body ol ol ul, .markdown-body ol ol ol { list-style: square; }
.markdown-body hr { margin: 1em 0; border: 0; border-top: 1px solid #eee; border-bottom: 1px solid #fff; }
.markdown-body img, .markdown-body video { max-width: 66%; margin: 1em 0; padding-left: 0.1em; }
.markdown-body iframe { max-width: 100%; margin: 1em 0; }
.markdown-body blockquote { font-weight: 500; color: #074f67; line-height: 1.5em; margin: 1em 0 1em 0; padding: 0 0 0 0.8em; border-left: 4px solid #ccc; }
.markdown-body code, .markdown-body pre { font-family: 'Ricty', monospace; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; }
.markdown-body code { font-size: 14px; line-height: 19px; padding: 0.1em 0.2em; }
.markdown-body code { color: #c7254e; background-color: #f9f2f4; border: 1px solid #CAAFB5; border-radius: 4px; }
.markdown-body pre { display: block; margin: 1em 0; padding: 1em; }
.markdown-body pre code { padding: 0; border: 0; background-color: transparent; }
.markdown-body pre span { font-family: 'Ricty', monospace; }
.markdown-body table { font-weight: 500; width: auto; margin: 1em 0 1.5em 0em; border-collapse: collapse; }
.markdown-body thead { font-weight: bold; background-color: #eee; }
.markdown-body th, .markdown-body td { padding: 0.3em 1em; border-top: 1px solid #ddd; border-left: 1px solid #ddd; }

}
