css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dl.acm.org"), domain("delivery.acm.org") {
  
  /*** Palette ***/
  :root {
    --default-color: #aaaaaa;
    --default-bkgd-color: #282828;
    --brighter-bkgd-color: #333333;
    --bkgd-color-3: #444;
  }
  
  body {
    color: var(--default-color);
    background-color: var(--default-bkgd-color);
  }
  
/*   / * top logo * /
  img[alt="ACM DL"] {
    filter: invert(84%);
  } */
  
  /* all images */
  img {
    filter: invert(84%);
  }
  
/*   / * PDF * /
  a[title="FullText PDF"] img {
    background: url(data:image/png;base64,) no-repeat !important;
  } */
  
  /* Links */
  a:link {
    color: #358d1b;
    text-decoration: none; /* underline; */
  }
  
  a:visited {
    color: #009966; /* #cc0044; */
  }
  
  a:hover { /* apply to visited as well */
    color: #129bfa;
  }
  
  /* Heading */
  .text12, .text14 {
    color: var(--default-color);
  }
  
  /* Title, 'Authors', 'Published In' box */
  #divmain {
    border-color: var(--brighter-bkgd-color) !important;
    padding: 20px;
  }
  
  /* footer */
  DIV.footerbody {
    color: var(--default-color);
  }
  
  /* Middle Strip of 'Abstract', 'Source materials', 'Authors', etc. */
  .x-tabs-strip-wrap {
    background-color: var(--default-bkgd-color);
  }
  
  .x-tabs-strip .x-tabs-right, .x-tabs-strip .x-tabs-left, .x-tabs-strip .on .x-tabs-left {
    background-image: none;
  }
  
  .x-tabs-strip .x-tabs-text {
    color: #176a7d;
  }
  
  .x-tabs-strip .on .x-tabs-text {
    color: #1f8fa8;
  }
  
  .x-tabs-strip .on .x-tabs-right {
    background-color: var(--brighter-bkgd-color);
    background-image: none;
  }
  
  /* Tab container */
  .x-tabs-body {
    border-color: var(--bkgd-color-3);
  }
  
  .x-tabs-wrap {
    border-bottom-color: var(--bkgd-color-3);
  }
  
  /*** Conference main page ***/
  
  /* Heading */
  .mediumb-text {
    color: var(--default-color);
  }
  
  /* Description */
  .medium-text {
    color: var(--default-color);
  }
  
  #divtools {
    background-color: var(--default-bkgd-color) !important;
  }
  
  /* footer - 'Powered by' */
  .small-text {
    color: var(--default-color) !important; /* imp for 'Author Tags' */
  }
  
  /*** Journal/Transaction Main Page ***/
  
  /* Top 10 most downloaded */
  #toShowTop10 > ol {
    background-color: var(--default-bkgd-color) !important;
  }
  
  /* Top 10 most cited */
  #toHideTop10 > ol {
    background-color: var(--default-bkgd-color) !important;
  }
  
  /*** Author Page ***/
  
  /* Papers */
  #results {
    color: var(--default-color);
  }
  
  #resfound, #resultmenu select, #resultmenu option {
    background-color: var(--brighter-bkgd-color);
  }
  
  .publicationDate {
    color: #c3c3c3;
    background-color: var(--brighter-bkgd-color);
  }
  
  /*** Paper Page ***/
  
  /* Authors - Bibliometrics */
  .smaller-text {
    color: var(--default-color);
  }
  
  img[alt="Cover Image"] {
    filter: invert(0%);
  }
  
  /* Author */
  td[bgcolor="#cccccc"] {
    background-color: var(--default-bkgd-color);
  }
  
  /* Table of Contents heading */
  span[style="background-color:#ECE9D8"] {
    background-color: var(--default-bkgd-color) !important;
  }
  
  /* 'Recent authors with related interests' */
  .header-text a:hover, .person-name a:hover, 
  .person-concept a:hover, .person-concept-explanation a:hover {
    color: #129bfa;
  }
  
  /* 'Concepts in this article' */
  #related_concepts h2 {
    color: var(--default-color);
    background-color: var(--brighter-bkgd-color);
  }
  
  /*** Institutional Profile ***/
  
  /* Bibliometrics */
  .text10 {
    color: var(--default-color);
  }
  
  
  /*** Main Page ***/
  
  /* Description */
  .small-link-text {
    color: var(--default-color);
  }
  
  /*** Special Interest Group Page */
  
  /* 'Authors', 'Affiliations' */
  .x-grid-cell-text, .x-grid-hd-text {
    color: var(--default-color);
  }
  
  .x-grid-hd-row > td {
    background-color: var(--default-bkgd-color);
    border-bottom: 2px solid #cccccc;
  }
  
  .x-grid-row {
    background-color: var(--default-bkgd-color);
  }
  
  .x-grid-row.x-grid-row-alt > td {
    background-color: var(--brighter-bkgd-color);
  }
  
  .x-grid-row.x-grid-row-over > td {
    background-color: #7c0000;
  }
  
  .x-grid-row.x-grid-row-selected > td {
    background-color: #28278d;
  }
  
  .x-toolbar {
    background: var(--default-bkgd-color);
  }
  
  /*** Recent and Upcoming Conferences ***/
  div#rss_list {
    background-color: var(--default-bkgd-color) !important;
  }
  
  /*** Hosted Content e.g. Linux Journal ***/
  
  /* Top Description */
  .large-text {
    color: var(--default-color);
  }
  
  /*** Magazines e.g. CACM ***/
  
  /*** About Box ***/
  table.text12 td {
    border-color: var(--default-color) !important;
  }
  
  /*** ACM Books ***/
  
  /* 'How to access the  ACM BOOKS' block */
  div[style=" font-size:12px; display:block; margin-top: 10px; margin-left:640px; width: 300px;  height:450px; padding-top: 5px; background-color: #ECE9D8"] {
    background: var(--default-bkgd-color) !important;
  }
  
  /* Table of Contents */
  tr[style="vertical-align:top; background-color:#EFEFEF"] {
    background-color: var(--default-bkgd-color) !important;
  }
  
  tr[style="vertical-align:top; background-color:#FFFFFF"] {
    background-color: var(--brighter-bkgd-color) !important;
  }
  
  /*** ebooks - dl.acm.org/ebooks.cfm ***/
  #thisone {
    background-color: var(--default-bkgd-color) !important;
    color: var(--default-color) !important;
  }
  
  /* 'eBook Subjects' table */
  div[class=" x-grid-container"] {
    border-color: var(--bkgd-color-3) !important;
  }
  
  .x-grid-hd-row > td {
    border-bottom-color: var(--bkgd-color-3);
  }
  
  .x-grid-split {
    background-image: none;
  }
  
  .x-grid-col {
    border-right-color: var(--default-bkgd-color);
    border-bottom-color: var(--default-bkgd-color);
  }
  
  /* Thumbnails */
  img[src*="/thumb/"] { /* contains substring */
    filter: invert(0%);
  }
  
  /* Sub-lists e.g. In 'Algorithms', for C++, Java, Machine Learning */
  #thelist > span[style="margin-left:10px; display:inline-block; vertical-align:top; background-color:#FFFFFF; padding-left:5px; padding-right:5px;"] {
    background-color: var(--default-bkgd-color) !important;
  }
  
  div[class="text12"][style="margin-bottom:10px;  border-bottom: 1px solid #EAEAEA;"] {
    border-bottom-color: var(--bkgd-color-3) !important;
  }
  
  /*** Search results ***/
  
  #theevents {
    background-color: var(--default-bkgd-color);
  }
  
  /*** 'Did you know' pop-up pane ***/
  .x-dlg .x-dlg-dlg-body {
    background-color: var(--default-bkgd-color);
    border-color: var(--brighter-bkgd-color);
  }
  
  .x-dlg .x-dlg-hd, .x-dlg .x-dlg-hd-left, .x-dlg .x-dlg-hd-right {
    background-image: none;
    background-color: var(--brighter-bkgd-color);
  }
  
  img[src="images/3phones.gif"], 
  img[src="images/googleplay.png"], 
  img[src="images/appstore.png"], 
  img[src="images/winphone.png"] {
    filter: invert(0%);
  }
  
  #letemknow_title > div {
    color: var(--default-color) !important;
  }
  
  .x-dlg .x-dlg-close, .x-dlg div.x-resizable-handle-southwest, .x-dlg div.x-resizable-handle-southeast {
    filter: invert(80%);
  }
  
  /*** Account Creation ***/
  .small-link-text {
    background-color: var(--default-bkgd-color);
  }
  
  /*** User Page ***/
  
  /* Create Binder */
  span#crbinder {
    background-color: var(--default-bkgd-color) !important;
  }
  
  /* Save to Binder */
  .TB_ToolbarSet, .TB_Expand, .TB_Collapse {
    background-color: var(--default-bkgd-color);
  }
  
  /* Middle Vertical separator */
  td[style="width:1px; background-color:#99c5e8;"] {
    background-color: var(--brighter-bkgd-color) !important;
  }
  
  /* Binder Entries list */
  li[style="background-color:#FFF;"] {
    background-color: var(--default-bkgd-color) !important;
  }
  
  /* Binder Entry double-ended arrow */
  .ui-icon {
    filter: invert(40%);
  }
  
  /* 'Drag entries to reorder...' msg */
  hr {
    color: #111;
    opacity: 0.5;
  }
  
  /* 'view/edit annotation' Text area */
  span[style*="rgb(236, 233, 216)"] {
    background-color: var(--default-bkgd-color) !important;
  }
  
  .TB_ToolbarSet {
    border-top-color: var(--brighter-bkgd-color);
    border-bottom-color: var(--brighter-bkgd-color);
  }
  
  .TB_Button_On, .TB_Button_Off, .TB_Button_On_Over, .TB_Button_Off_Over, .TB_Button_Disabled {
    border-color: var(--brighter-bkgd-color);
  }
  
  .TB_Button_On {
    background-color: #444;
  }
  
  .TB_Button_On_Over, .TB_Button_Off_Over {
    background-color: #666;
  }
  
  /* Binder Entry while re-ordering */
  li[class=""], li.ui-sortable-helper {
    background-color: var(--brighter-bkgd-color) !important;
  }
  
  /*************** delivery.acm.org ***************/
  
  /*** PDF (in Firefox) ***/
  .textLayer {
    background-color: var(--default-bkgd-color);
  }

}
