@-moz-document domain("twitch-viewerbot.com") {
body {
    background-color: #282828;
    background-image: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-32873.png);
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    background-attachment: fixed;
}
.centerblockbig {
    width: 650px;
    height: 550px;
    position: static;
    top: 150px;
    bottom: 0;
    left: 0;
    right: 0;
    margin: auto;
    padding-top: 100px;
}
.logodiv {
    background-image: url(http://i.imgur.com/My6TBW6.png);
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    background-size: contain;
    background-repeat: no-repeat;
    margin-left: auto;
    margin-right: auto;
    margin-top: 10px;
    margin-bottom: 10px;
}
.tb-tabs-framed .tab-content {
    background: #333;
    border-color: #333;
    border-image: none;
    border-right: none;
    border-style: none;
    border-width: 0;
    padding: 20px;
    border-top: none;
    color: #e5eef4;
    overflow: auto;
    border-radius: 5px;
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.3);
}

.regulartextbox {
    border-radius: 2px;
}
.nav-tabs > li.inactive {
    background: #333;
    background-color: #333;
    text-decoration: none;
    margin-left: 0px;
    color: #E8EFF4;
    margin-right: 3px;
    border-radius: 5px 5px 0 0;
}
.nav-tabs > li > a {
    border: 0px solid transparent;
    border-radius: 5px 5px 0 0;
    line-height: 1.42857;
}
.nav-tabs > li > a:hover {
    border-color: #fff #fff #fff;
}
.navtabs> li > a:hover,
.nav > li > a:focus {
    background-color: #fff;
    transition: all 0.4s ease 0s;
    text-decoration: none;
    color: #000;
    box-shadow: 0px 5px 5px rgba(0, 0, 0, 0.3);
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:focus {
    background-position: 30% 20%;
    background: #fff;
    border-color: #000 #000 transparent;
    border-image: none;
    border-style: none;
    border-width: 1px;
    color: #000;
    cursor: default;
    margin-left: 0px;
    margin-right: 3px;
    box-shadow: 0px 5px 5px rgba(0, 0, 0, 0.3);
}
.navtabs {
    list-style-type: none;
    margin-bottom: 0;
    padding-left: 0px;
    font-family: 'Play', sans-serif;
    width: 100%;
}
.alert-yellow {
    background-color: #F2DEDE;
    border-color: #EED3D7;
    color: #AA4342;
}
.regulartextbox {
    width: 100%;
    text-align: center;
}
.tab-content h3 {
    color: #fff;
    font-weight: 700;
    line-height: 1;
    margin: 10px 0;
    cursor: default;
    text-rendering: optimizelegibility;
    text-align: center;
}

