/*Skin CSS pour CommentCaMarche.net*/
/*Nom : CCM Modernized*/
/*Auteur: Snox5*/
/*Version: Release*/
/*Licence : CC BY-NC-SA*/
/*Leechez pas ! => Proposez moi des modifs sur le topic du theme sur CCM : http://www.commentcamarche.net/forum/affich-33916971-theme-css-ccm !*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.commentcamarche.net/"), url-prefix("http://ccm.net/"), url-prefix(https://secure.commentcamarche.net/) {
    
    :root{
        --topbar-color: #303030;
        --bleu-color: #497bb7;
        --green-color: #46AF4D;
        --red-color: #e53735;
    }
    
@font-face {
  font-family: 'Roboto Slab';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto Slab Regular'), local('RobotoSlab-Regular'), url(https://fonts.gstatic.com/s/robotoslab/v6/y7lebkjgREBJK96VQi37Zo4P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
/****** Font-Awesome FONT-AWESOME ********/
@font-face {
	font-family: 'FontAwesome';
	src: url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?v=4.7.0');
	src: url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?#iefix&v=4.7.0') format('embedded-opentype'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff2?v=4.7.0') format('woff2'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff?v=4.7.0') format('woff'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.ttf?v=4.7.0') format('truetype'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.svg?v=4.7.0#fontawesomeregular') format('svg');
	font-weight: normal;
	font-style: normal
 }

/**************************/
@keyframes bounce-anim {
    0%   {transform:translateX(0);}
    25%  {transform:translateX(0px);}
    30%  {transform:translateX(-5px);}
    32%  {transform:translateX(3px);}
    34%  {transform:translateX(0px);}
    50%  {transform:translateX(0px);}
    75%  {transform:translateX(0px);}
}

/* => MODE KIKOO
Décommenter les lignes suivantes pour activer le mode "pseudos de Jean Kevin" */
