.member a.ftBlue.ftNone::before {
    content: 'Xx-Dark'!important;
}
.member a.ftBlue.ftNone::after {
    content: '-Officiel-xX';
}
*/
/***/

/*DESACTIVATION DES ANIMATIONS*/
#newanswer_ctn[style] {
    background:transparent!important;
    transition-duration: 1s;
}
    
/*TESTS TOPIC*/

div.questionInfos {
    font-size: 12px;
}
/*BODY*/

body {
    font-family: Open Sans, sans-serif;
    color: #424242!important;
    background-image: url('http://api.thumbr.it/whitenoise-361x370.png?background=fafafaff&noise=626262&density=100&opacity=5');
    background-repeat: repeat;
}
/*LARGEUR DES PAGES*/
header.ccm_header[role="banner"] .layout {
    min-width: 80%;
    max-width:1250px;
}
.app--rwd .layout.layout--offcanvas {
    width: auto;
    min-width: 1070px;
    max-width: 1250px;
}
/*BOX SHADOW SUR LA PAGE*/

.app--rwd .layout.layout--offcanvas {
}
.layout_ct {
    background-color:transparent;
}
/* CCM TOPBAR*/
header.ccm_header.ccm_header--light[role="banner"] .ccm_middle_bar .ccm_logo_ccm img {
    transition-duration:1s;
}
header.ccm_header.ccm_header--light[role="banner"] .ccm_middle_bar .ccm_logo_ccm img:hover {
    transform:scale(1.15);
}
header.ccm_header[role="banner"] .ccm_top_bar li#user a.newMsg span {
    position: relative;
    top: -5px;
    right: -27px;
    padding: 0 5px;
    background: #E53735;
    border-radius: 13px;
    font-size: 11px;
    font-family: 'Roboto condensed';
    font-weight: 700;
}
header.ccm_header[role="banner"] .ccm_top_bar {
    background: #303030;
}
header.ccm_header[role="banner"] .ccm_top_bar li > a:hover, header.ccm_header[role="banner"] .ccm_top_bar #user:hover .boxButton, header.ccm_header[role="banner"] .ccm_top_bar .ccm_usermenu_language:hover .ccm_usermenu_language__btn,
header.ccm_header[role="banner"] .ccm_top_bar li.selected a {
    background: var(--red-color);
}
/*CCM HEADER*/

header.ccm_header.ccm_header--light[role="banner"] .ccm_middle_bar {
    background:var(--red-color);
}
header.ccm_header.ccm_header--light[role="banner"] nav[role="navigation"] .layout_ct > ul > li.selected a {
    background: rgba(255,255,180,.1);
}
header.ccm_header.ccm_header--light[role="banner"] nav[role="navigation"] .layout_ct > ul > li > a:hover {
    background: rgba(255,255,180,.055);
}
header.ccm_header.ccm_header--light[role="banner"] nav[role="navigation"] .layout_ct > ul > li > a {
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.0);
}
/*BREADCRUMB LIKE BS*/
.ccmcss_breadcrumb {
    background-color: var(--red-color);
    padding: 5px 10px 2px 10px;
    border-radius: 20px;
    font-family: 'Open Sans',sans serif;
    font-weight: 700;
    }
div.ccm_breadcrumb > a {
    color:#fff !important;
    }
.ccmcss_breadcrumb div + div::before {
    color: #FFF;
    }
/* STATISTIQUES CHARTE RACCOURCIS*/
  #toolsLinks {
    float: right;
    background-color: var(--red-color);
    padding: 5px 10px 4px 10px;
    border-radius: 20px;
    font-family: 'Open Sans',sans serif;
    font-weight: 700;
}
    #toolsLinks > li > a {
    color: #fff;
}
    .app--rwd #toolsLinks {
    margin-top: 0px;
    }
/*correction marge bizzar*/

#ivgaabtm {
    border-left: 0px solid transparent;
}
/*S'APPLIQUE A TOUS LES BOUTTONS*/

.ccmcss_btn {
    border-radius:3px;
    transition-duration:.3s;
}
.ccmcss_btn:hover {
    transition-duration: .05s;
}
/*BOUTTON TOP ET AVANCE*/

#gotop a,
#gonext .goLogo,
#goprev .goLogo {
    border-radius: 0px;
}
/*TOPIC LIST ZONE*/
/*TOPIC LIST TABLE*/
/*Boutons "Répondre" DANS LA TOPIC LIST !*/
a.ccmcss_btn--info::before {
	font-family: 'FontAwesome';
	content:"\f1cd   ";
	font-weight:normal;
}
a.ccmcss_btn--s::before {
	content:''!important;
}
.ccmcss_btn--info {
    text-shadow: 0px 0px 3px rgba(0, 0, 0, .3);
    font-weight: bold;
    border: 1px solid rgba(0, 0, 0, 0.1);
    background-color:#497bb7;
}
.ccmcss_btn--info:hover {
    text-shadow: 0px 0px 3px rgba(0, 0, 0, .3);
    font-weight: bold;
    border: 1px solid rgba(0, 0, 0, 0.1);
    background-color:#518bd1;
}
/*boutons "répondre" généraux hover FX*/
.ccmcss_btn.ccmcss_btn--info.ccmcss_btn--s {
    border:1px solid rgba(0,0,0,.1);
}
.ccmcss_btn.ccmcss_btn--info.ccmcss_btn--s:hover,
.ccmcss_btn.ccmcss_btn--info.ccmcss_btn--s:focus {
    background-color:#5996e0!important;
    border:1px solid rgba(0,0,0,.1)!important;
    color:#fff!important;
    
}
/* boutons "répondre" dans la topic list hover FX*/
.tableTemplate table {
    border: solid 1px #ddd !important;
}
.tableTemplate .noAnswers .answers:hover .ccmcss_btn {
    cursor: pointer;
    background: #4193c9;
}
/*bordure sujet courrant*/

.tableTemplate tbody tr td.subject {
    border-left: 3px solid transparent;
}
/**/

.tableTemplate tbody td.theme,
.tableTemplate tbody td.author,
.tableTemplate tbody td.answers,
.tableTemplate tfoot td.answers,
.tableTemplate tbody td.nbAnswers {
    font-family: 'Open Sans', sans-serif;
    font-size: 11px;
}
.tableTemplate tbody td {
    border-top: 0px solid #fffdfd;
    padding: 10px;
}
/*COLORATION TOPICLIST*/
.tableTemplate tbody tr:nth-child(odd) {
    /*background-color: #fcfcfc;*/
}
/**/
.container .tableTemplate {
    border-collapse: collapse;
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.2);
    margin-bottom: 20px;
    border-radius: 0 0 4px 4px;
}
.tableTemplate {
    font-family: 'Open Sans', sans-serif;
}
.tableTemplate .bgColorForumCol {
    background: transparent;
}
.unpair {
    background: transparent;
}
.container .tableTemplate tbody {
    background: #fff;
}
.ccmcss_btn--ico:hover {
    border: 1px solid rgba(0,0,0,.2);
}
#searchOpt {
    padding: 5px;
    background-color:transparent!important;
}
.ccm_search_form form {
    background-image: url('http://api.thumbr.it/whitenoise-361x370.png?background=ffffffff&noise=626262&density=100&opacity=5');
    background-repeat: repeat;
}
.ccm_forum__opt.grid_line.ccmcss_ft_s div.grid_left {
    color: #303030;
}
/*Boutton Répondre*/

td.answers {
    background-color: transparent !important;
}
td.answers:hover {
    background-color: #4C7996 !important;
}
/*JCUSTOM*/
    .ccmcss_form select.jCustomSelect { 
    /* This is the default class that is used */
    /* Put whatever custom styles you want here */
        border:1px solid #ccc!important;
        border-radius:3px!important;
    }
    .customSelect.customSelectHover {
    /* Styles for when the select box is hovered */
        border:1px solid #ccc!important;
        border-radius:3px!important;
    }
    .customSelect.customSelectOpen {
    /* Styles for when the select box is open */
        border:1px solid #ccc!important;
        border-radius:3px!important;
    }
    .customSelect.customSelectFocus {
    /* Styles for when the select box is in focus */
        border:1px solid #ccc!important;
        border-radius:3px!important;
    }
    
/*BOUTONS QUAND PREVISUALISATION VALIDATION*/
    /*bouton répondre*/
.ccmwg_newpost input[type="submit"] {
    background: #497BB7;
    border: 1px solid rgba(0,0,0,.1);
    color: #fff;
    font-weight: bold;
}
.ccmwg_newpost input[type="submit"]:hover {
    text-shadow: 0px 0px 2px rgba(0, 0, 0, .3);
    background: #5089ce;
    border: 1px solid rgba(0,0,0,.1);
    font-size: 17px;
    color: #fff!important;
    font-weight: bold;
}
    /*prévisualisation*/
.ccmwg_newpost input[type="button"] {
    border: 1px solid rgba(0,0,0,.1);
    background: #A285E9;
}
.ccmwg_newpost input[type="button"]:hover {
    text-shadow: 0px 0px 2px rgba(0, 0, 0, .3);
    border: 1px solid rgba(0,0,0,.1);
    background: #ad8ff7;
    color:#fff!important;
}
/*USERNAMES DANS LA TOPICLIST*/
    td.answers > a.ftBlue, td.author > a.ftBlue {
   
    text-decoration: underline;
}
/*BOUTON LISTE DES SUJETS*/

a.ccmcss_btn.ccmcss_btn--warning {
    background-image: none;
    text-shadow: 0px 0px 3px rgba(0, 0, 0, .3);
    font-weight: bold;
    border: 1px solid rgba(0, 0, 0, 0.1) !important;
}
/*Couleur de background du titre des sujets sans réponses DANS LA TOPIC LIST*/

tr.noAnswers {
    background: #ffffff;
}
.tableTemplate tbody tr.noAnswers td.subject {
    border-left-color: #497BB7;
}
/*HEAD TABLE TOPIC*/

.tableTemplate thead tr th.subject {
    border-left: 0px solid transparent;
}
.tableTemplate .noAnswers .subject a {
    color: #537aba;
}
.tableTemplate thead tr {
    background: #fcfcfc;
    color: #525259;
    border-top: 3px solid var(--red-color);
}
/* sujets supprimés */
tr.delete > td.subject {
    border-left: 3px solid var(--red-color) !important;
}
.tableTemplate tbody tr.delete td.subject a,.tableTemplate tbody tr.delete td span.ftRed,.tableTemplate tbody tr.delete td a.ftBlue{
    color:#777!important;
}
.tableTemplate tbody tr.delete {
    background: #f7f7f7;
}
/*s'applique au Head des colonnes "Forum" "Date" Et "Réponses"*/

.tableTemplate thead th {
    border: none;
}
/*Box Shadow derriere la topic list*/

.colMiddle .container {
    border: 0px solid transparent;
    background: transparent;
}
.ccmcss_form .customSelect .customSelectInner::before {
    content: "";
    border-left: none;
    background-color: #fcfcfc;
    border-radius: 0 5px 5px 0;
    border-left: solid 1px rgba(0,0,0,.15);
}
.ccmcss_form .customSelect .customSelectInner::after {
    border-color: #424242 transparent transparent transparent;
}
div.gutter > div.grid_col.w50 > input, div.gutter > div.grid_col.w50 > span.customSelect {
    border: solid 1px #ccc;
    background-color: #fff;
    border-radius:5px;
    font-family: 'Open Sans', sans serif;
    font-size: 13px;
    box-shadow: inset 0 0px 3px 0 rgba(0, 0, 0, 0.05);
}
.container .ccm_search_form {
    padding: 0px;
}
fieldset {
    margin-top: 20px;
    width: 100%;
}
/*STATS DE RECHERCHE*/

.tabMenu {
    border: none;
}
.tabMenu span {
    height: 33px;
    margin-right: 0;
    border: none;
    top: 0px;
}
.tabMenu span > a:hover {
    background-color:#E84B42!important;
    color:#fff!important;
    transition-duration:.2s;
    }
.tabMenu .selected {
    border-bottom: none;
    border-top: none;
    line-height:0;
}
.tabMenu .selected a {
    padding-top: 5px;
    line-height:24px;
    color: #e53735 !important;
    /*border-bottom: 2px solid #e53735 !important;*/
}
.tabMenu > span > a {
    transition-duration:.25s;
    height: 34.5px;
    background: #fff;
    font-weight: bold;
    color: #3d3d3d!important;
    font-size: 12px;
    text-decoration:none;
    
    border-top: 1px solid rgba(0, 0, 0, 0.2);
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
    border-left: 1px solid rgba(0, 0, 0, 0.2);
    }
.tabMenu > span:first-child > a {
    border-radius: 4px 0 0 4px;
    border-left: 1px solid rgba(0, 0, 0, 0.2);
    border-top: 1px solid rgba(0, 0, 0, 0.2);
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
    }
.tabMenu > span:nth-last-child(-n+2) > a {
    border-radius:0 4px 4px 0;
    border-right: 1px solid rgba(0, 0, 0, 0.2);
    border-top: 1px solid rgba(0, 0, 0, 0.2);
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
    }
/*Pagination sous la topic list*/

.pagination2 {
    padding:0;
    border-top: none;
    background: transparent;
}
.pagination2 .pageNb a, .pagination2 .ico a {
    padding: 4px 6px;
    background: #fff;
    border: 1px solid /*#9b9b9b*/#bbb;
    text-decoration: none;
    border-radius: 3px;
    box-shadow: 0 1px 2px rgba(0,0,0,.2);
    color: #4A4A4A;
}
.pagination2 .pageNb a.selected {
    border-color: rgba(0,0,0,.2);
    background: #e53735;
    font-weight: bold;
    color: #fff;
    }
.pagination2 .pageNb a:hover, .pagination2 .pageNb a.selected:hover{
    transition-duration:0s;
    border-color: rgba(0,0,0,.2);
    background: #e53735;
    font-weight: bold;
    color: #fff;
}
.pagination2 .pageNb a {
    transition-duration:.25s;
    padding: 5px 7px;
    background: #fff;
}
.pagination2 .icoPrevious {
    display: none;
}
.pagination2 .ico {
    width: 0px;
    display:none;
}
/*Button Next et Previous*/

.bottomLink {
    padding-left: 0px;
}
.grid_line.gutter {
    margin-left: 0px;
}
.pagination2 .prev a,
.pagination2 .next a {
    font-weight: bold;
    text-decoration: none !important;
    background-color:#FFFFFF;
    border:1px solid #CCCCCC;
    color:#3D3D3D;
    text-shadow:none;
    padding: 4px 20px;
    border-radius: 2px;
    transition-duration: .1s;
}
.pagination2 .prev a::before {
		font-family:'FontAwesome';
	content:'\f060   ';
	font-weight:normal;
}
.pagination2 .next a::after {
		font-family:'FontAwesome';
	content:'   \f061';
	font-weight:normal;
}
.pagination2 .prev a:hover,
.pagination2 .next a:hover {
    background-color: #ffffff;
    border:1px solid rgba(0,0,0,.1);
    color:#333;
    text-shadow:none;
    transition-duration: .1s;
}
/*COULEUR TITRE SUJETS RESOLUES*/
.grid_left, .left {
    color: #fff;
}
.tableTemplate tbody tr.resolved td.subject,
.tableTemplate tfoot tr.resolved td.subject {
    border-left-color: var(--green-color);
}
.tableTemplate .resolved .subject a {
    color: var(--green-color);
}
    tr.resolved td span
/*SUBMIT FORM FORUM*/

.container .ccm_search_form {
    margin: 0px 0 0;
    background: transparent;
}
/*boutton de recherche*/

.ccmcss_btn--primary {
    background-color: var(--red-color);
    border-color: var(--red-color);
}
/*TOPIC LIST :HOVER FX & EFFET FADEOUT HOVER*/
.tableTemplate tbody tr:first-child {
   border-top:1px solid #efefef;
}
.tableTemplate tbody tr {
    transition-duration: .1s;
   border-bottom:1px solid #efefef;
}
.tableTemplate tbody tr:hover {
    transition-duration: .5s;
    background-color: #fff!important;
}
.tableTemplate tbody tr:hover td {
    background-color:#fff!important;
}
/*LINK TOPIC :HOVER FX*/

.tableTemplate tbody td.subject a:hover,
.tableTemplate tfoot td.subject a {
    text-decoration: none;
}
/*TOPIC LIST END END END END END END /*
  /*MES DISCUSSIONS SUIVIES*/
/*coloration résolu*/

#myi_content a[style] {
    color: var(--green-color) !important;
}
/**/

.genForm .validMsg .formMsg p {
    color: var(--green-color);
}
.asideBox span.arrowDown,
.asideBox span.arrowUp {
    display: none;
    width: 0px;
}
.asideBox h3 {
    /*font-size: 1.05em;*/
    
    font-family: Open Sans, sans serif;
}
.asideBox .boxTitle,
.asideBox .boxTitle .title {
    color: #3F3F3F;
    font-weight: normal;
    font-size: 16px;
}
.asideBox {
    background: #ffffff;
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.2);
    border-top: 3px solid var(--red-color);
    border-radius: 1px 1px 4px 4px;
}
.buttonAsideBox {
    background: url(https://upload.wikimedia.org/wikipedia/commons/c/ce/Transparent.gif) no-repeat, #e4e4e7;
}
.buttonAsideBox span {
    background: url(https://upload.wikimedia.org/wikipedia/commons/c/ce/Transparent.gif) -226px -321px no-repeat;
}
.asideBox ul li a:hover {
    color: #D9434E;
    text-decoration: none;
}
.asideBox .buttonAsideBox {
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.3);
    background: #fff;
    width: 100%;
    text-align: center;
}
.asideBox .buttonAsideBox:hover {
    background: #FBFBFB;
}
#myi_content strong {
    display: block;
    animation-duration: 2s;
    animation-delay: 0s;
    animation-direction: normal;
    animation-iteration-count: infinite;
    animation-play-state: running;
    animation-name: bounce-anim;
}
/*nouveaux messages*/
/*FT THEME*/

.ftTheme,
.ftTheme a {
    color: var(--red-color)!important;
}
/*FORM SEARCH*/

.ccmcss_form .ccmcss_form__input,
.ccmcss_form input[type="text"],
.ccmcss_form input[type="email"],
.ccmcss_form input[type="search"],
.ccmcss_form input[type="tel"],
.ccmcss_form input[type="url"],
.ccmcss_form input[type="password"],
.ccmcss_form input[type="datetime"],
.ccmcss_form input[type="datetime-local"],
.ccmcss_form input[type="date"],
.ccmcss_form input[type="month"],
.ccmcss_form input[type="week"],
.ccmcss_form input[type="time"],
.ccmcss_form input[type="number"],
.ccmcss_form input[type="color"],
.ccmcss_form textarea,
.ccmcss_form select,
.ccmcss_form .customSelect {
    border-radius: 2px;
}
.ccmcss_form .ccmcss_form__input,
.ccmcss_form input[type="text"],
.ccmcss_form input[type="email"],
.ccmcss_form input[type="search"],
.ccmcss_form input[type="tel"],
.ccmcss_form input[type="url"],
.ccmcss_form input[type="password"],
.ccmcss_form input[type="datetime"],
.ccmcss_form input[type="datetime-local"],
.ccmcss_form input[type="date"],
.ccmcss_form input[type="month"],
.ccmcss_form input[type="week"],
.ccmcss_form input[type="time"],
.ccmcss_form input[type="number"],
.ccmcss_form input[type="color"],
.ccmcss_form textarea,
.ccmcss_form select {
    border: 1px solid #BBBBBB;
}
.ccmcss_form .ccmcss_form__input,
.ccmcss_form input[type="text"],
