    border-style: solid;
    border-width: 8px;
    border-color: transparent;
    border-top-color: #fff;
    content: '';
    height: 0;
    left: 50%;
    margin-left: -177px;
    pointer-events: none;
    position: absolute;
    top: 100%;
    width: 0;
}

