        border-radius:0;
        margin:0 2.5px;
    }
    .tabMenu > span > a:hover {
        border:1px solid rgba(0,0,0,.1);
    }
    .ccm_page--profile .topCol {
        background-size:100% 100%!important;
    }
    .ccm_page--profile .topCol .myAccount .accountTitle {
        padding: 20px 20px 20px 10px;
    }
}
@media (max-width:979px) {
  .app--rwd.ccmcss_rwd_header.ccm_rwd_header--light .ccmcss_rwd_header__top {padding: 2px; background: #e53735; } 
}
