css:
@-moz-document url-prefix("http://www.motobit.com/util/") {
body ,
input ,
select ,
textarea {
    border : 1px solid #C0C0C0;
    background-color : #000;
    color : #FFF;
}
#dcenter {
    border-right     : #333 10px solid;
    border-bottom    : #333 20px solid;
    border-left      : #444 10px solid;
}
div[style*="border: 4px solid #cc9966;"] {
	border : 4px solid #222 !important;
}
div[style*="border: 4px solid #ddaa77;"] {
	border : 4px solid #333 !important;
}
#dcenter ,
div[style*="background-color: #cc9966;"] ,
div[style*="background-color: #eebb88;"] {
	background : #222 !important;
}
.dmain {
	background-color : #555;
}
.dh1zb {
	background-color : #666;
}
font[style*="color:red"] {
	color : #a98484 !important;
}
a:link ,
a {
	color : #86a984;
}
a[style*="color:red"] {
	color : #86a984 !important;
}

/* http://www.motobit.com/util/quoted-printable-encoder.asp */
.dh2 {
    background-color : #333;
}

/* http://www.motobit.com/util/gmail-smtp-pop-outlook.asp */
input[style*="color:gray"] {
	color : #DDD !important;
}

/* http://www.motobit.com/util/base64/css-images-to-base64.asp */
.simpleform {
	border-color : #333;
    background-color : #222;
}

