@-moz-document domain("motobit.com") {
body {
	background-color : #000;
    color            : #FFF;
}

table[bgcolor="white"] {
    background-color : #000;
}
a, a:link, a:visited {
    color : #a98484;
}
h3 a, h3 a:link, h3 a:visited {
	color : #86a984;
    border-color : #86a984;
}
h3 a:hover {
	border-bottom : 0px;
}
.foot {
	background-color : #333;
}
.foot a {
	color : #FFF;
    border-bottom : 1px solid #FFF;
}
.foot a:hover {
	border-bottom : 0px;
}

#header #logo , /* http://www.motobit.com/util/base64/css-images-to-base64.asp */
#header .back , /* http://www.motobit.com/util/base64/css-images-to-base64.asp */
h1 a /* http://www.motobit.com/ */ {
    -webkit-filter: grayscale(1);
}
h1 {
	background-color : #333;
    color            : #FFF;
}
.subtitle {
	background-color : #333;
}
.subtitle a {
	color : #FFF;
}

img ,
iframe {
    -webkit-filter: grayscale(1);
    filter: grayscale(1);
    opacity : 0.7;
    -webkit-transition: all 2s ease;
    -moz-transition: all 2s ease;
    -ms-transition: all 2s ease;
    -o-transition: all 2s ease;
    transition: all 2s ease;
}
img:hover ,
iframe:hover {
    -webkit-filter: inherit;
	opacity : 1;
}
}

@-moz-document url-prefix("http://www.motobit.com/help/") {
/* http://www.motobit.com/help/scptutl/upload.asp */
h3 , h4 {
	color : #86a984;
}
/* http://www.motobit.com/help/scptutl/sa246.htm */
tr[bgcolor="SILVER"] {
	background-color : #444;
}
th[bgcolor="lightyellow"] {
	background-color : #333;
}
/* http://www.motobit.com/help/asp-dictionary-object/default.htm */
h2 {
	-webkit-filter: grayscale(1);
    filter: grayscale(1);
}
}

@-moz-document url("http://www.motobit.com/about.htm") {
h2 {
	background-color : #d4d4d4;
}
img[src="zav-pruhledny.gif"] {
	background-color : #d4d4d4;
    opacity : 1;
    filter : inherit;
    -webkit-transition: none;
    -moz-transition: none;
    -ms-transition: none;
    -o-transition: none;
    transition: none;
}
}

@-moz-document url("http://www.motobit.com/help/ScptUtl/cl88.htm") {
h2, h2 a, h2 a:link, h2 a:visited {
	background-color : #d4d4d4;
}
}
