css:
@-moz-document domain("youtube.com") {
/* YOUTUBE REDESIGN 2017 - ORANGE YOUTUBE THEME!/COPY CODE PROHIBITED! */
/* ======================= AUTHOR: PAUL APPAU ======================= */


.yt-lockup-action-menu,
#masthead-search-terms .gsib_b,
#results .yt-lockup-description,
.yt-uix-button-subscription-container .yt-uix-button-subscribe-branded::before,
.yt-uix-button-subscribed-branded::before {
    display: none !important;
}
#page-container {
        top: 8px !important;
    }
    .guide-section-separator {
        border: 5px solid #ffffff !important;
    }
    body {
        background: transparent !important;
    }
    #page {
        background: #f1f1f1 !important;
}

/* ======================= upper part ======================= */

#guide-container .guide-toplevel li h3 a {
    color: #FF6200 !important;
}

.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    box-shadow: 3px 5px 20px 4px rgba(0, 0, 0, .1);
}
/*[leftside_menu_shadow]*/

#appbar-guide-menu {
    transition: ease-in-out 0.3s;
}
/*[leftside_menu]*/

#guide-container {
    width: 230px;
    padding: 13px 22px;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border-right: 1px solid #ddd;
}
/*[left_menu]*/

.yt-thumb-clip img {
    border-radius: 3px 3px 3px 3px !important;
}
/*[avatar_user]*/

#progress {
    background: #fff !important;
}
#progress dd,
#progress dt {
    background: #fff !important;
    box-shadow: #fff 1px 0 6px 1px !important;
}
/*[Ð·rogress_line]*/

.feed-item-container,
#yt-masthead-container,
#appbar-guide-menu {
    border-top: 0 !important;
    border-right: 0 !important;
    border-bottom: 0 !important;
    border-left: 0 !important;
}
#masthead-appbar {
    border-bottom: 1px solid #ddd;
}
.appbar-nav-menu li a {
    border-bottom: 3px solid #ffffff !important;
    transition: all .2s ease-in-out;
    -moz-transition: all .2s ease-in-out;
    -webkit-transition: all .2s ease-in-out;
}
.appbar-nav-menu li a:hover {
    border-color: #FF6200 !important;
}
.yt-uix-button {
    border-radius: 3px 3px 3px 3px !important;
    border: 0 !important;
    box-shadow: none !important;
}
/*[button_all]*/

.yt-uix-button-subscription-container .yt-uix-button.yt-uix-button-subscribe-unbranded {
    padding: 0 12px 0 3px !important;
}
.yt-uix-button .yt-uix-button-size-default,
.yt-uix-button-expander {
    background: #f2f2f2 !important;
    border-radius: 3px 3px 3px 3px !important;
    padding: 15px 20px 25px 20px !important;
    font-weight: bold;
}
/*[more_content]*/

.resume-playback-progress-bar
{
    background: #FF6200;
    opacity: 1;
}

#appbar-content {
    padding-left: 0 !important;
    width: 100% !important;
}
.yt-uix-button-icon-appbar-guide {
    background: url(data:image/png;base64,) 0 0 !important;
    background-size: 15px 15px !important;
}
.yt-uix-button-icon-bell {
    background: no-repeat url(data:image/png;base64,) 4px 6px !important;
    background-size: 19px 22px !important;
    opacity: 1 !important;
}
#yt-masthead-container {
    height: 35px !important;
    background: #FF6200 !important;
    padding-top: 9px !important;
}
#appbar-guide-menu {
    margin-top: 52px !important;
}
#yt-masthead-container input {
    padding: 5px 5px 5px 5px !important;
    color: #636363 !important;
    font-size: 10.5pt;
    font-family: Calibri;
}
.masthead-logo-renderer-logo {
    background: no-repeat url(data:image/png;base64,) 0 0 !important;
    background-size: 74px 30px !important;
}
.content-region {
    color: #ffffff !important;
}
/* ======================= Site search ======================= */

#masthead-search-terms {
    width: 584px !important;
    background: #fff !important;
    box-shadow: none !important;
    border: 0 !important;
    border-radius: 3px 3px 3px 3px;
}
#masthead-search {
    border-bottom: 0px !important;
    height: 28px !important;
    margin-top: 4px !important;
}
#masthead-search > div,
#masthead-search > button {
    height: 28px !important;
}
.search-button {
    border: 0 !important;
    border-radius: 0 !important;
    background: transparent !important;
    opacity: 1 !important;
    width: 25px !important;
}
.search-button:hover {
    background-color: rgba(255, 255, 255, 0.25) !important;
    border-radius: 3px 3px 3px 3px !important;
}
.search-button > span {
    opacity: 1 !important;
    background: no-repeat url(data:image/png;base64,) 0 0 !important;
    background-size: 15px 15px !important;
    margin: 0 5px !important;
}
#yt-masthead {
    position: relative !important;
}
#yt-masthead-content {
    width: 650px !important;
    position: absolute !important;
    left: 50% !important;
    transform: translate(-50%, 0) !important;
    -webkit-transform: translate(-50%, 0) !important;
    -moz-transform: translate(-50%, 0) !important;
    -o-transform: translate(-50%, 0) !important;
    -ms-transform: translate(-50%, 0) !important;
}
body > table.gstl_50 {
    left: 50% !important;
    top: 52px !important;
    transform: translate(-50%, 0) !important;
    -webkit-transform: translate(-50%, 0) !important;
    -moz-transform: translate(-50%, 0) !important;
    -o-transform: translate(-50%, 0) !important;
    -ms-transform: translate(-50%, 0) !important;
    transition: ease-in-out 0.3s;
}
/* [result_search] */

#yt-masthead-user > a {
    background: #fff !important;
    border-radius: 3px 3px 3px 3px !important;
    color: #FF6200 !important;
}
.shelf-title-row .yt-thumb-simple > img {
    border-radius: 50px !important;
}
.watched-badge {
    left: 0 !important;
    top: 0 !important;
    width: 100% !important;
    height: 100% !important;
    text-align: center !important;
    line-height: 114px !important;
    font-size: 20px !important;
}
#channel-navigation-menu li > a > span {
    padding: 7px 0 !important;
    border-bottom: 3px solid #FFFFFF !important;
}
#channel-navigation-menu li > a > span:hover {
    border-color: #FF6200 !important;
    transition: all .2s !important;
    -webkit-transition: all .2s !important;
    -moz-transition: all .2s !important;
    -o-transition: all .2s !important;
    -ms-transition: all .2s !important;
}
.addto-watch-later-button {
    background-color: transparent !important;
    background: rgba(255, 255, 255, 0.2) !important;
}
.addto-watch-later-button:hover {
    background: rgba(255, 255, 255, 0.8) !important;
}
.guide-item-selected span,
.guide-channel:hover span {
    color: #ffffff !important;
}
.channel-header-profile-image-container {
    left: 10px !important;
    top: 10px !important;
}
#guide-subs-footer-container .display-name span {
    color: #222!important;
    font-size: 11px;
}
#guide-subs-footer-container .guide-count {
    font-size: 9px;
    color: #FF6200 !important;
}
#guide-subs-footer-container .guide-item:hover .display-name span {
    color: #ffffff !important;
}
/* ======================= Search results Channel ======================= */

#results .yt-lockup-channel {
    background: #FF6200 !important;
    margin-bottom: 10px !important;
    color: #fff !important;
}
/* [chanel_user] */

#results .yt-lockup-channel .yt-uix-sessionlink {
    color: #fff !important;
}
#results .yt-lockup-channel .yt-uix-sessionlink a {
    color: #fff !important;
}
#results .yt-lockup-channel .yt-uix-sessionlink a:hover {
    text-decoration: none;
    cursor: pointer;
}
/* [chanel_user] */

#results .yt-lockup-meta-info > li:last-child {
    opacity: 1 !important;
}
#results .yt-lockup-byline {
    line-height: 16px !important;
}
#results .yt-badge {
    color: #555 !important;
    background: #fff !important;
    border-radius: 3px 3px 3px 3px !important;
}
/* [badge_chanel] */

#results .yt-badge {
    color: #555 !important;
    background: #fff !important;
    border-radius: 3px 3px 3px 3px !important;
}
/* [badge_chanel] */

#results .subscribe-label,
.subscribed-label,
.unsubscribe-label {
    color: #555 !important;
}
/* [button_sub_chanel] */

#results .yt-lockup-byline > a:hover {
    color: #FF6200 !important;
}
#results .yt-lockup-badges li {
    background: #F2F2F2 !important;
}
#results .yt-lockup-badges li span {
    border: 0 !important;
    padding: 4px !important;
}
#results .yt-lockup-channel .yt-lockup-title a {
    background: transparent !important;
}
#results .yt-lockup-channel .yt-lockup-badges > * {
    display: block !important;
    float: left !important;
    height: 22px !important;
}
#results .yt-lockup-channel .yt-lockup-badges li {
    background: #B8B8B8 !important;
}
#results .yt-lockup-channel .yt-thumb {
    border: 5px solid #fff;
    border-radius: 3px 3px 3px 3px;
}
.yt-lockup-meta-info {
    font-size: 11px !important;
}
#results .yt-lockup-meta-info {
    display: flex;
    display: -webkit-flex;
    justify-content: flex-start;
    -webkit-justify-content: flex-start;
    flex-direction: column-reverse;
    -webkit-flex-direction: column-reverse;
    margin: 10px 0 0 0 !important;
}
#results .yt-lockup-playlist-items > li {
    margin: 2px 0 !important;
    color: #B8B8B8 !important;
    border: none !important;
    font-size: 10px !important;
    background-color: #f2f2f2 !important;
}
#results .yt-lockup-playlist-items li:nth-child(1) {
    padding: 0 0 2px 0 !important;
    border-bottom: 1px solid #ffffff !important;
}
#results .yt-lockup-meta-info > li:first-child {
    display: block !important;
}
.yt-lockup-dismissable:hover .yt-lockup-meta-info > li:last-child {
    opacity: 1;
}
.yt-lockup-byline,
.yt-lockup-meta {
    line-height: 10px !important;
}
.yt-lockup-title {
    margin-bottom: 5px !important;
    margin-top: 5px !important;
    font-weight: 100 !important;
}
.yt-lockup-title > a {
    margin-bottom: 4px !important;
}
.watch-sidebar-body .view-count {
    color: #b8b8b8 !important;
    padding: 2px 0 0 0 !important;
}
.yt-thumb {
    background: transparent !important;
}
/* ======================= Button Subcribe ======================= */

#results .yt-uix-button-subscription-container button {
    border-radius: 3px 3px 3px 3px !important;
    color: #ffffff !important;
    height: 21px !important;
}
#results .yt-uix-button-subscription-container > span {
    height: 21px !important;
    border: 0px !important;
    line-height: 21px !important;
    border-radius: 0px 3px 3px 0px !important;
}
#results .yt-uix-button-subscribed-branded .subscribed-label,
.unsubscribe-label .yt-uix-button-subscribe-branded, .yt-uix-button-subscribe-branded[disabled], .yt-uix-button-subscribe-branded[disabled]:hover, .yt-uix-button-subscribe-branded[disabled]:active, .yt-uix-button-subscribe-branded[disabled]:focus {
    color: #222 !important;
}

.yt-uix-button-subscribe-branded {
    background-color: #FF6200 !important;
}

.yt-subscription-button-subscriber-count-branded-horizontal {
    height: 24px;
    line-height: 24px;
}

.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal {
    display: none;
    margin-left: -2px;
    border: 0px solid #ccc;
    background-color: #eee;
    vertical-align: middle;
    border-radius: 0 2px 2px 0;
}

.appbar-guide-notification {
    background: #FF6200;
    color: #fff;
    font-weight: 500;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
}

/* ======================= The page with the video ======================= */

.multirow-shelf .yt-uix-expander-head {
    background: #f5f5f5;
    padding: 15px 15px 15px 15px;
    position: relative;
    z-index: 1;
    margin-right: 10px;
    text-align: center;
    color: #636363 !important;
    font-size: 10pt;
    font-weight: 500;
}
/* [more_video_main_page] */

.video-time {
    background: rgba(248, 248, 255, 1.0) !important;
    padding: 2px 4px 2px 4px !important;
    border-radius: 4px 4px 4px 4px !important;
    color: #000 !important;
    font-size: 10px;
}
/* [video_time] */

.yt-card.yt-card-has-padding {
    padding: 20px 20px 30px 20px;
}
#content-container,
#body-container {
    padding-bottom: 0px !important;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border-top: 1px solid #ddd;
}
/*[body_container]*/

.shelf-title-row {
    margin-bottom: 30px !important;
}
/* [title_main_page_video] */

.shelf-title-row h2 {
    height: 30px !important;
    font-size: 11pt !important;
    font-weight: 500 !important;
    color: #636363!important;
}
/* [title_main_page_video] */

.shelf-title-row h2 a {
    color: #636363 !important;
}
/* [title_main_page_video] */

.shelf-title-row h2 a:hover {
    color: #636363 !important;
}
/* [title_main_page_video] */

.video-extras-sparkbar-likes {
    float: left;
    height: 2px;
    background: #FF6200 !important;
}
/* [like_bar] */

.yt-lockup-title a {
    color: #636363;
    font-size: 10pt !important;
    font-weight: 500 !important;
}
.yt-lockup:hover a,
.yt-lockup:hover {
    color: #FF6200;
    text-decoration: none;
}
/* [title_video] */
/* ======================= Bottom part ======================= */

#footer-container {
    border-top: 0 !important;
    margin-top: 0 !important;
}
#footer-container .yt-uix-button {
    background: #ededed !important;
}
  
#footer-logo .footer-logo-icon {
    vertical-align: middle;
    background: no-repeat 0px 0px;
    background-size: 376px 453px;
    width: 0px;
    height: 0px;
}

/* ======================= Comment section ======================= */
  
.comment-renderer-author-comment-badge.creator {
    background-color: #ff6200;
    border-radius: 9px;
    padding: 1px 6px;
    vertical-align: middle;
}
  
.comment-author-text {
    color: #ff6200;
    font-weight: 500;
    text-decoration: none;
    word-break: break-all;
}
  
.comment-renderer-like-count {
    margin-right: 8px;
    color: #ff6200;
    font-size: 9pt;
    vertical-align: top;
}
  
.comment-replies-renderer-view, .comment-replies-renderer-hide {
    margin: 6px 0 0;
    color: #ff6200;
    font-weight: 500;
}
  
.comment-replies-renderer-paginator {
    display: block;
    margin: 6px 0 0;
    color: #ff6200;
    font-weight: 500;
}
  
/* ======================= Background ======================= */

a {
    color: #FF6200;
    cursor: pointer;
    text-decoration: none;
}
a:hover {
    color: #FF6200;
}
/* [title_video] */

#guide-container .guide-item-selected {
    background: #FF6200 !important;
}
.epic-nav-item-heading {
    border-color: #FF6200 !important;
}
a.yt-uix-button.yt-uix-button-epic-nav-item,
button.yt-uix-button-epic-nav-item,
.epic-nav-item {
    transition: all .2s ease-in-out;
    -moz-transition: all .2s ease-in-out;
    -webkit-transition: all .2s ease-in-out;
}


.yt-thumb-20,
.yt-thumb-34,
.yt-thumb-48,
#yt-masthead-user .yt-thumb-27,
.comments-list img.user-photo,
.ytp-player-content .branding-img-container,
.channel-header-profile-image,
.channel-header-profile-image-container,
.watch-card-lockup-container .yt-thumb {
    border-radius: 0px !important;
}
#pl-video-table .pl-video {
    border: 1 !important;
}
#pl-video-table tbody tr:nth-child(2n+2) {
    background-color: #FAFAFA !important;
}
.pl-video .watched-badge {
    display: none !important;
}
.pl-video-thumbnail .video-thumb {
    opacity: 1 !important;
}
.yt-card {
    box-shadow: none !important;
}
/* ======================= AdBlock! ======================= */

#ad_creative_1 {
    height: 0;
    padding-top: 20px;
    visibility: hidden;
}
#homepage-sidebar-ads {
    height: 0!important;
    min-height: 0!important;
    visibility: hidden;
}
#watch-channel-brand-div {
    height: 0!important;
    visibility: hidden;
}

/* ======================= Player Section ======================= */

/* scrubber button */
.html5-scrubber-button:hover, .ytp-scrubber-button:hover, .ytp-swatch-background-color, .ytp-swatch-background-color-secondary {
   background: #FF6200 !important;
}
/* progress bar */
.html5-play-progress, .ytp-play-progress {
   background: #FF6200 !important; 
}
.ytp-volume-slider-track, .ytp-volume-slider-handle:before {
   background: #FF6200 !important;
   z-index: -117;
}
.ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after, .ytp-settings-button.ytp-3d-badge::after {
   background-color: #FF6200 !important;
}
.ytp-swatch-color {
   color: #FF6200 !important;
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
   background: #FF6200 !important;
}
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
   fill: #FF6200 !important;
}
.ytp-cued-thumbnail-overlay:hover .ytp-large-play-button-bg, .ytp-large-play-button.ytp-touch-device .ytp-large-play-button-bg {
   fill: #FF6200 !important;
}

/* ======================= Thin Scrollbar ======================= */

::-webkit-scrollbar{
    width:7px;
    background-color:#f1f1f1;

}

body::-webkit-scrollbar{
    width:8px!important;
    background-color:#EEEEEE;
}

::-webkit-scrollbar-track{
    border: 7px;
}

body::-webkit-scrollbar-track{
    
    border: none;
}

::-webkit-scrollbar-thumb{
    background-color:#424242!important;
    border-radius:0px!important;
    border: none;
}
::-webkit-scrollbar-thumb:hover{
    background-color:#333!important;
    
}
::-webkit-scrollbar-thumb:active{
    background-color:#616161!important;
    
}

/* ======================= Large Youtube Player Section ======================= */

html .watch-stage-mode .player-width,
html .watch-stage-mode #player {
  width: 100% !important;
  width: calc(100vw - 18px) !important; /* 18px for the scrollbar */
  margin-left: 0 !important;
  left: 0 !important;
}
html .watch-stage-mode #placeholder-player .player-width {
  width: 100% !important;
}
html .watch-stage-mode .player-height {
  height: 95vh !important;
  height: calc(100vh - 52px - 18px) !important; /* YouTube top bar && scrollbar. */
}

html .watch-stage-mode .ad-container,
html .watch-stage-mode .html5-video-container,
html .watch-stage-mode video.video-stream.html5-main-video {
  left: 0 !important;
  top: 0 !important;
  width: 100% !important;
  height: 100% !important;
}

html .watch-stage-mode .html5-video-container > .video-ads > .ad-container {
  /* Fix for ads bottom bar at the old player. */
  bottom: 30px !important;
  height: auto !important;
}
html .watch-stage-mode .ad-container.ad-overlay {
  /* Fix for banner-style ads. */
  top: auto !important;
  height: auto !important;
}

html .watch-stage-mode #player-playlist .watch-playlist {
  /* Fixing the playlist sidebar (when watching a video from a playlist). */
  top: calc(100vh - 52px - 18px - 360px) !important;
  height: 480px !important;
}

html .watch-stage-mode .ytp-chrome-bottom {
  /* Video control bar for the new player. */
  width: 100% !important;
  width: calc(100% - 24px) !important;
}

#masthead-positioner {
  position: fixed !important;
}

/* Why is this here anyway? */
/* .player-unavailable {
  float: none !important;
} */

#movie_player > .html5-video-controls > .html5-player-chrome {
  /* Making the old player look similar to the new player. */
  background: linear-gradient(to bottom, rgba(27,27,27,0) 0%, rgba(27,27,27,0.5) 100%);

}
}
