css:
@-moz-document domain("beegunslingers.com"), domain("vt.alttp.run") {
body {
  background-color: #000000 !important;
}
#navbar,
.navbar,
.well,
.panel-heading-btn,
.panel-body .panel-heading,
.panel-body,
.toggle-off,
.custom-rules .tab-content .active,
.spoiler-toggle,
#navbar a, #navbar a:hover, #navbar a:active, #navbar a:visited,
#custom-items-advancement input, #custom-items-extra input {
  background-color: #333333;
  color:            #999999;
}

#custom-items-advancement input, #custom-items-extra input {
  font-weight: bold;
  text-align:  center;
}

.toggle-on, .bg-success, .bg-success * {
  background-color: #00aa00;
  color:            #ffffff;
}

.toggle-off, .bg-danger, .bg-danger * {
  background-color: #aa0000;
  color:            #ffffff;
}

.custom-rules .tab-content .active {
  border:       1px solid #ffffff;
  border-width: 0 1px 1px 1px;
}

.custom-rules .tab-content .active input {
  text-align:right;
}

a {
  text-decoration: underline;
}

input, button {
  border: 1px solid #ffffff;
}

input {
  cursor: text;
}

option {
  background-color: #d9edf7;
  color:            #ffffff;
}

}
