css:
@-moz-document domain("hangouts.google.com") {
/* mute icon */
.NnPBgc .pmk5cc {
    background-color: rgb(255, 0, 0);
    width: 52px;
    height: 52px;
}
/* mute icon padding */
.NnPBgc .pmk5cc .DPvwYc {
    padding: 15px;
}
/* camera is muted text hider */
.FtCne {
    visibility: hidden;
	position: relative;
}
/* camera is muted text change to cam is disabled */
.FtCne:after {
    position: absolute;
    top: 15px;
    right: 10px;
    visibility: visible;
    font-size: 9px;
    font-weight: bold;
    content: "Camera is disabled";
}
/* audio levels bar */
.NnPBgc .bMHH1c, .NnPBgc .JPGJdc, .NnPBgc .ygzKnc{
 background-color: #FF9900;
 height: 15px;
 width: 15px;
}
/* Screenshare bar text hider */ 
.qhKRGc {
    visibility: hidden;
	position: relative;
}
/* screenshare text modified */
.qhKRGc:after {
    visibility: visible;
    font-size: 20px;
    font-weight: bold;
    content: "⚠ WARNING: Screenshare is enabled!";
}
/* screenshare bar colour */
.oNuiKf {
    background-color: #1000ff;
}
/* screenshare bar stop button */
.o10jLc {
    background: #FF9900;
}
/* Messages slideout bar */
.jcaQle {
    background-color: #090088;
}
/* message line selected */
.jcaQle .OabDMe {
    background-color: #FF9900;
}
/* message send button active */
.h496Ec {
    color: #FF9900;
}
/* Messages button */
.Daj3g  {
    background-color: #1000ff;
}
/* message bubble */
.WPhfe {
     background: #FF9900;
}
/* message bubble point */
.A14hmf {
    border-color: #FF9900 transparent transparent transparent;
}
/* message send name color */
.zn6G2b {
    color: #1000ff;
    font-weight: bold;
}
/* message body color */
.iknqmd {
    color: #ffffff;
}
/* Message send time */
.HUCXgd {
   color: #ffffff;
}
/* Invite people email input box color bar */
.ut0cCc .JEKSPd {
    background-color: #FF9900;
}
/* Invite button no hover no select color */
.fLLMJf {
    background-color: #1000ff;
}
/* Invite box invite button hover color */
.fLLMJf.u3bW4e .ZFr60d, .fLLMJf:hover .ZFr60d {
    background-color: #FF9900;
}
/* hangouts settings active tab header color */
.PVegMb .kaAt2.KKjvXb {
    color: #1000ff;
}
/* hangouts settings active tab header bar color */
.PVegMb .s7bIcf {
    background-color: #1000ff;
}
/* hangouts settings page general page option headers */
.pP1tgf {
    color: #FF9900;
}
/* hangouts settings page bandwidth option headers */
.PzvrUb {
    color: #FF9900;
}
/* hangouts settings diagnostic data checkbox color */ 
.B0tnMd.N2RpBe {
    border-color: #1000ff;
}
/* hangouts settings diagnostic data checkbox color check effect */
.B0tnMd.i9xfbb>.MbhUzd, .B0tnMd.u3bW4e>.MbhUzd {
    background-color: rgba(255, 153, 0, 0.28);
}
/* hangouts settings tab select effect */
.PVegMb .kaAt2>.MbhUzd {
    background-image: radial-gradient(circle farthest-side,rgba(255, 177, 0, 0.34),rgba(255, 177, 0, 0.16) 80%,rgba(15,157,88,0) 100%);
}
/* hangouts call settings done button */
.xKNPvb .CwaK9 {
    color: #1000ff;
}
/* done button click effect */
.xKNPvb .Vwe4Vb {
    background-image: radial-gradient(circle farthest-side,rgba(0, 31, 255, 0.2),rgba(0, 3, 255, 0.2) 80%,rgba(15,157,88,0) 100%);
}
/* Join hangout button */
.SdWGFe {
    background-color: #1000ff;
}
/* Join hangout button hover */
.SdWGFe.u3bW4e .ZFr60d, .SdWGFe:hover .ZFr60d {
    background-color: #FF9900;
}
/* hangouts sound level bubble */
.ac5LHf .umw7gd::after {
    background-color: rgb(0, 55, 255);
}
/* hangup button color */
.L0HJ1e>.nfdz6d, .L0HJ1e>.nfdz6d .ZFr60d {
    background-color: #FF9900;
}
/* mic mute/ camera mute button color */
.L0HJ1e>.xdiH8e, .L0HJ1e>.xdiH8e:hover .ZFr60d, .L0HJ1e>.WGQfVc, .L0HJ1e>.WGQfVc:hover .ZFr60d {
    background-color: #1000ff;
}
/* hangouts mute button pressed */
.pczahe {
    color: rgb(255, 0, 0);
}
body {
    background: #000;
}
}
