    background-size: cover;
}
.app-main {
    overflow: hidden;
    background: rgba(255, 255, 255, 0);
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
}
#main_col {
    background: rgba(255, 255, 255, 0);
}
.warp__item--anchor {
    background: rgba(32, 28, 43, 0);
    padding: 1rem 0 1.5rem;
    position: relative;
    z-index: 999999;
}
.warp__drawer {
    background: rgba(32, 28, 43, 0);
    box-shadow: 0 -1px 0 rgba(44, 37, 65, 0);
    padding-bottom: 1rem;
    position: absolute;
    bottom: 100%;
    overflow: hidden;
    max-height: 100vh;
    transform: translate3d(0, 0, 0);
    transition: all .25s;
    width: 100%;
}
.chat-container,
.ember-chat-container {
    background: rgba(40, 30, 60, 0);
    color: inherit;
}
#right_col {
    position: absolute;
    right: 0;
    top: 0;
    bottom: 0;
    width: 340px;
    z-index: 3;
    background: rgba(0, 0, 0, 0.66);
}
.leaf {
    color: #ffffff;
    background-color: rgba(0, 0, 0, 0.56);
    box-shadow: rgba(44, 37, 65, 0) 0 0 0 1px inset;
    font-size: 1.4rem;
    line-height: 3rem;
    height: 3rem;
    border: 0;
    padding: 0 1rem;
    padding-left: 4rem;
    outline: 0;
    width: 100%;
    transition: .3s ease;
}
.warp__item>a:hover {
    color: #faf9fa;
    background: #000000;
    text-decoration: none;
}
.channel-stats .stat {
    display: inline-block;
    margin-left: 10px;
    color: #ffffff;
}
#channel .player-column #broadcast-meta .info .title {
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    line-height: 2.5rem;
    font-size: 2.4rem;
    margin-bottom: -.45801rem;
    padding-top: .45801rem;
    position: absolute;
    right: 0;
    top: 0;
    height: 35px;
    left: calc(60px + 1.5rem);
    color: #ffffff;
    z-index: 3;
    display: block;
    min-height: 35px;
}
#channel .player-column #broadcast-meta .info .title {
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    line-height: 2.5rem;
    font-size: 2.4rem;
    margin-bottom: -.45801rem;
    padding-top: .45801rem;
    position: absolute;
    right: 0;
    top: 0;
    height: 35px;
    left: calc(60px + 1.5rem);
    color: #ffffff;
    z-index: 3;
    display: block;
    min-height: 35px;
}
input.text,
input.countries-input,
.recurly input,
input.string,
textarea {
    margin: 0 0 20px;
    line-height: 28px;
    padding: 0 7px;
    height: 30px;
    font: inherit;
    letter-spacing: inherit;
    color: #ffffff;
    vertical-align: top;
    border: 1px solid rgba(218, 216, 222, 0.2);
    background-clip: padding-box;
    -webkit-background-clip: padding-box;
    -moz-background-clip: padding;
    box-sizing: border-box;
    background: rgba(255, 255, 255, 0);
    }
* {
    margin: 0;
    padding: 0;
    color: LightSlateGrey ;
}
.special-message {
    background: rgb(35, 35, 35);
}
.panel-formatting .panel h3 {
    margin-bottom: 10px;
    line-height: 30px;
    color: #ffffff;
}
#channel-prompt,
.upsell-banner {
    margin: 30px 0;
    padding: 20px;
    background: rgba(0, 0, 0, 0.56);
    position: relative;
    border-left: 5px solid #000000;
}
.activity-card__status {
    padding: 2rem;
    position: relative;
    background: rgba(0, 0, 0, 0.56);
    word-break: break-word;
}
.activity-card {
    width: 100%;
    border: 1px solid #000000;
    box-shadow: 0 2px 1px rgba(0, 0, 0, .08);
}
.conversations-content .conversations-list-bottom-bar {
    background: rgba(0, 0, 0, 0.56);
    border: 1px solid #000000;
    float: right;
    height: 40px;
    pointer-events: auto;
}
.conversations-list {
    background: rgba(0, 0, 0, 0.78);
    border: 1px solid #000000;
    padding: 0;
    height: 245px;
    width: 300px;
    position: absolute;
    right: 0;
    bottom: 39px;
    z-index: 10;
    pointer-events: auto;
    display: none;
}
.conversations-list .conversations-list-item {
    border-bottom: 1px solid #000000;
    position: relative;
    cursor: pointer;
}
.conversations-list .conversations-list-header {
    padding: 10px;
    background: #000000;
    border-bottom: 1px solid #000000;
    font-weight: 700;
}
.conversations-list .conversations-list-item:hover {
    background-color: #000000;
}
.ember-chat .chat-header {
    background: rgba(40, 30, 60, 0);
    color: inherit;
}
.cn-bar {
    background: rgba(40, 30, 60, 0);
    color: inherit;
}
.activity-card__comments {
    background: rgba(40, 30, 60, 0);
    color: inherit;
}
.cn-bar__displayname {
    color: LightSlateGrey ;
}
.cn-metabar .cn-card .cn-card__title {
    color: LightSlateGrey;
}
}
