@-moz-document domain("www.hackerrank.com") {
html, body { background-color: #272822 !important; }
.container, .page-header, .page_header, #page_header {min-width:initial !important;}
.container * { color: #ddd; border-color: #333 !important; }
/*Dark Background - #272822 - Monokai color*/
.component-wrapper, .container, .main_content, .nav, .nav li.active a, .codeshell-footer, .page_footer, .content-header, .page-header, .fill-light, .jobs-landing-header a, .nav-tabs li,.left-sidebar, .right-sidebar, .submission-model-panel, #pref-pane
{ background-color: #272822 !important; color: #eee !important; border-color: #333 !important; }
/*Dark contents - #333*/
.contests-list-view, .leaderboard-overall-list-view, .recommend-challenge-click, .individual-challenge-card, #commonapp-promo, .chapter-cards, .pagination li a, .job-box-ab, .table-body, .leaderboard-overall-list-view, .leaderboard-menu, .light-wrap, .nav-tabs li a, .single-comment, .discussion-comments, .discussion-root-node-knob, #custominput, blockquote
{ background: #333 !important; box-shadow: 0 0 5px -1px #000; }
/*Lighter over elements - #444*/
.icon-ok.success{color:green !important;} /* Test case pass icon */
.error-output{background: #444 !important}
.btn { background: #444; border: 1px solid #555 !important;}
.btn.btn-primary, .btn-primary-new{background:#2ec866;color:#fff;}
.btn.difficulty.active { background: #222; }
.btn-neutral-new, .btn-line-new { color: #bbb !important; }
.badge-container:hover { background: #444 !important; }
.nav { border: 1px solid #333 !important; }
.hackdown-content pre { background: #333; color: #eee; }
.hackdown-content pre code { color: #eee; }
.nav li.active a { border: 1px solid #333 !important; border-bottom-color: transparent !important; }
.nav li:hover a { color: #fff; }
.pagination ul li a{color:#ccc !important;}.pagination ul li.active a{color:#fff !important; font-weight: bold;}
.pagination a .caret.left, .pagination a .double-caret.left{border-right-color:#ccc !important;}.pagination ul li a .caret.right, .pagination ul li a .double-caret.right{border-left-color:#ccc !important;}
.pagination a:hover .caret.left, .pagination a:hover .double-caret.left{border-right-color:#eee !important;}.pagination ul li a:hover .caret.right, .pagination ul li a:hover .double-caret.right{border-left-color:#eee !important;}
.tooltip{box-shadow:0 0 10px -1px #000; border-radius:5px; padding:0px !important;}
/*Border Colors to be green*/
.page-header .nav-links>.nav-links-active>li>a:not(.logo_mark):not(.login).active, .page-header .nav-links #profile-menu a.active, .page_header .nav-links>.nav-links-active>li>a:not(.logo_mark):not(.login).active, .page_header .nav-links #profile-menu a.active, #page_header .nav-links>.nav-links-active>li>a:not(.logo_mark):not(.login).active, #page_header .nav-links #profile-menu a.active{border-color:#2ec866 !important;}
.page-header{border:none !important; box-shadow:0 0 10px 0px #000}
.social-buttons .social-btn{background:#444;}
.btn-text, .btn-link{padding:10px;}
.code-checker{box-shadow:0 5px 10px 0px #000;}.grey-header{box-shadow:0 0px 10px 0px #000;}
#fullscreen-wrapper .challenge-text{background:none}
.submission-model-panel{position:fixed;z-index:10000;top:100px;right:0;border-radius:5px;box-shadow:0 0 50px -10px #000; transition:0.5s}

/* Based on Sublime Text's Monokai theme */
/*Problem code*/
.highlight .n { color: #66D9EF !important; }
.highlight .k, .highlight .kt { color: #C45CA2 !important; }
.highlight .na { color: #8AE228 !important; }
.highlight .o, .highlight .nf, .highlight .nn { color: #eee !important; }
.highlight .s { color: #F03737 !important; }
/*Codemirror theme*/
.CodeMirror { border-bottom-color: #5C5C5B; border-top-color: #5C5C5B }
.CodeMirror-cursor { transition: 1s; -webkit-transition: 1s; -moz-transition: 1s }
.CodeMirror-sizer{border-right:none;}
.CodeMirror-scroll{overflow:hidden !important}
/*Editor Statusbar*/.codeeditor_statusbar { background: #272822; color: #f8f8f2; border-bottom-left-radius: 5px; border-bottom-right-radius: 5px; }
/*Editor Header*/#codeshell-wrapper .grey-header { background: #272822; color: #f8f8f2; border-top-left-radius: 5px; border-top-right-radius: 5px; }
/*Editor lang select*/#s2id_select-lang .select2-choice { background: #272822 !important; color: #f8f8f2; border: 1px solid #5C5C5B !important; }
/*Editor lang options*/#s2id_select-lang .select2-choice div { background: #272822 !important; }.select2-drop, .select2-input { background: #272822 !important; color: #f8f8f2 !important; }

/*Change default ones too*/
.cm-s-ambiance.CodeMirror, .code-editors .loading-mode { background: #272822; color: #f8f8f2; }
.cm-s-ambiance div.CodeMirror-selected { background: #49483E; }
.cm-s-ambiance .CodeMirror-line::selection, .cm-s-ambiance .CodeMirror-line > span::selection, .cm-s-ambiance .CodeMirror-line > span > span::selection { background: rgba(73, 72, 62, .99); }
.cm-s-ambiance .CodeMirror-line::-moz-selection, .cm-s-ambiance .CodeMirror-line > span::-moz-selection, .cm-s-ambiance .CodeMirror-line > span > span::-moz-selection { background: rgba(73, 72, 62, .99); }
.cm-s-ambiance .CodeMirror-gutters { background: #272822; border-right: 0px; box-shadow: 5px 0 10px -2px #000; padding-right: 5px; height:100% !important;}
.cm-s-ambiance .CodeMirror-guttermarker { color: white; }
.cm-s-ambiance .CodeMirror-guttermarker-subtle { color: #d0d0d0; }
.cm-s-ambiance .CodeMirror-linenumber { color: #d0d0d0; }
.cm-s-ambiance .CodeMirror-cursor { border-left: 2px solid #f8f8f0 !important; }
.cm-s-ambiance .CodeMirror-code pre { padding-left: 10px; }
.cm-s-ambiance span.cm-comment { color: #75715e; }
.cm-s-ambiance span.cm-atom { color: #ae81ff; }
.cm-s-ambiance span.cm-number { color: #ae81ff; }
.cm-s-ambiance span.cm-property, .cm-s-ambiance span.cm-attribute { color: #a6e22e; }
.cm-s-ambiance span.cm-keyword { color: #f92672; }
.cm-s-ambiance span.cm-builtin { color: #66d9ef; }
.cm-s-ambiance span.cm-string { color: #e6db74; }
.cm-s-ambiance span.cm-variable { color: #f8f8f2; }
.cm-s-ambiance span.cm-variable-2 { color: #9effff; }
.cm-s-ambiance span.cm-variable-3 { color: #66d9ef; }
.cm-s-ambiance span.cm-def { color: #fd971f; }
.cm-s-ambiance span.cm-bracket { color: #f8f8f2; }
.cm-s-ambiance span.cm-tag { color: #f92672; }
.cm-s-ambiance span.cm-header { color: #ae81ff; }
.cm-s-ambiance span.cm-link { color: #ae81ff; }
.cm-s-ambiance span.cm-error { background: #f92672; color: #f8f8f0; }
.cm-s-ambiance .CodeMirror-activeline-background { background: #373831; }
.cm-s-ambiance .CodeMirror-matchingbracket { text-decoration: underline; color: white !important; }

.cm-s-default.CodeMirror { background: #272822; color: #f8f8f2; }
.cm-s-default div.CodeMirror-selected { background: #49483E; }
.cm-s-default .CodeMirror-line::selection, .cm-s-ambiance .CodeMirror-line > span::selection, .cm-s-ambiance .CodeMirror-line > span > span::selection { background: rgba(73, 72, 62, .99); }
.cm-s-default .CodeMirror-line::-moz-selection, .cm-s-ambiance .CodeMirror-line > span::-moz-selection, .cm-s-ambiance .CodeMirror-line > span > span::-moz-selection { background: rgba(73, 72, 62, .99); }
.cm-s-default .CodeMirror-gutters { background: #272822; border-right: 0px; box-shadow: 5px 0 10px -2px #000; padding-right: 5px;  height:100% !important;}
.cm-s-default .CodeMirror-guttermarker { color: white; }
.cm-s-default .CodeMirror-guttermarker-subtle { color: #d0d0d0; }
.cm-s-default .CodeMirror-linenumber { color: #d0d0d0; }
.cm-s-default .CodeMirror-cursor { border-left: 2px solid #f8f8f0 !important; }
.cm-s-default .CodeMirror-code pre { padding-left: 10px; }
.cm-s-default span.cm-comment { color: #75715e; }
.cm-s-default span.cm-atom { color: #ae81ff; }
.cm-s-default span.cm-number { color: #ae81ff; }
.cm-s-default span.cm-property, .cm-s-ambiance span.cm-attribute { color: #a6e22e; }
.cm-s-default span.cm-keyword { color: #f92672; }
.cm-s-default span.cm-builtin { color: #66d9ef; }
.cm-s-default span.cm-string { color: #e6db74; }
.cm-s-default span.cm-variable { color: #f8f8f2; }
.cm-s-default span.cm-variable-2 { color: #9effff; }
.cm-s-default span.cm-variable-3 { color: #66d9ef; }
.cm-s-default span.cm-def { color: #fd971f; }
.cm-s-default span.cm-bracket { color: #f8f8f2; }
.cm-s-default span.cm-tag { color: #f92672; }
.cm-s-default span.cm-header { color: #ae81ff; }
.cm-s-default span.cm-link { color: #ae81ff; }
.cm-s-default span.cm-error { background: #f92672; color: #f8f8f0; }
.cm-s-default .CodeMirror-activeline-background { background: #373831; }
.cm-s-default .CodeMirror-matchingbracket { text-decoration: underline; color: white !important; }
.code-head.not-allowed .CodeMirror-lines, .code-tail.not-allowed .CodeMirror-lines{background: #272822 !important;}
@keyframes flickerAnimation { 0% { opacity: 1; }	50% { opacity: 0; }	100% { opacity: 1; }	}
@-o-keyframes flickerAnimation { 0% { opacity: 1; }	50% { opacity: 0; }	100% { opacity: 1; }	}
@-moz-keyframes flickerAnimation { 0% { opacity: 1; }	50% { opacity: 0; }	100% { opacity: 1; }	}
@-webkit-keyframes flickerAnimation { 0% { opacity: 1; }	50% { opacity: 0; }	100% { opacity: 1; }	}
.animate-flicker { -webkit-animation: flickerAnimation 1s infinite; -moz-animation: flickerAnimation 1s infinite; -o-animation: flickerAnimation 1s infinite; animation: flickerAnimation 1s infinite; }
}
