css:
@-moz-document domain("dojin.co") {
body {
 	background-color: #1d1d1d;
    color: #fafafa;
}

a:link, a:visited {
    color: #ff1732 !important;
}

.button_fields {
 	border-bottom: 1px solid #7a7a7a;   
}

.singlebutton_active, .singlebutton_passive, .cs_toggle {
 	margin: 0 !important;   
    padding: 4px 20px 5px 20px;
}

.singlebutton_active {
    background: #7a7a7a;
    color: #fafafa;
}

.singlebutton_passive:hover {
    color: #ff1732;    
}

.styling_button, .gform_button, #comment_submit {
 	background: #6a6a6a;
    color: #fafafa;
}

.styling_button:hover, .gform_button:hover, #comment_submit:hover {
    background: #ff1732;     	   
}

.styling_pointer:hover {
 	color: #ff1732;
}

.navArrow_styling {
 	background: #4a4a4a !important;   
}

.navArrow_styling:hover {
 	background: #ff1732 !important;   
}

ol.commentlist li {
 	border-top: 1px solid rgba(210, 210, 210, 0.15);
}

.latestComments_postProfile {
 	border-bottom: 1px solid rgba(185, 185, 185, 0.4);   
}

.latestComments_replyButton {
 	border-top: 1px solid rgba(185, 185, 185, 0.15);   
}

.latestComments_replyButton:hover {
    color: #ff1732;
}

#popup, #popup .center-column {
    background: #383838 !important;   
    border: none;
}

#meta-container {
    color: #fafafa;
}

.music-single-post p {
    color: #fafafa;
}

textarea {
    box-sizing: border-box !important;
    background: #4a4a4a;
    border: 1px solid #6a6a6a !important;
    color: #fafafa;
    padding: 0.5em !important;
    line-height: 1;
}

textarea.sharingtextarea {
 	color: #aaa !important;
}

textarea.sharingtextarea:hover {
 	color: #fafafa !important;
}

.newEntry_inputField input {
 	background: #1d1d1d !important;
    border-bottom: 1px solid #4a4a4a !important;
}

#previewField img {
 	border: 1px solid #4a4a4a;
}

#header .index_menu_element {
    background: #4a4a4a;
    color: #fafafa;
}

#exploration_cloud {
 	background: rgba(29, 29, 29, 0.95);   
}

.explore_Tag, .explore_Sorting, .exploreActiveTag {
 	border: 1px solid #4a4a4a;
    color: #fafafa;
}

.explore_Tag:hover, .explore_Sorting:hover {
 	background: rgba(64, 64, 64, 0.8) !important;
    border: 1px solid #6a6a6a;
}

.includeTag, .explore_sDate_current, .explore_sOrder_current, .explore_loadMagnitude_current, .conditionActive {
    background: #ff1732;
    border: 1px solid #fafafa;
    color: #fafafa;
}

#submitButton {
    border: 1px solid #6a6a6a;
}

#submitButton:hover {
    border: 1px solid #8a8a8a;
 	background: rgba(64, 64, 64, 0.8) !important;
    color: #ff1732;
}

#shareBoxField {
 	background: rgba(29, 29, 29, 0.95);   
 	border-bottom: 1px solid #4a4a4a;  
}

#samplePlayerControls {
 	background: rgba(29, 29, 29, 0.95);   
}

#samplePlayer_toggleAutoAdvance {
 	color: #ff1732 !important;   
}

.center_pane_body {
 	background: #383838;  
}

.hangingButtonStyle {
 	background: #383838 !important;     
}

.hangingButtonStyle:hover {
 	background: #ff1732 !important;     
}

::selection {
    background: #ff1732 !important;    
}

::-moz-selection {
    background: #ff1732 !important;    
}

::-webkit-scrollbar {
    width: 5px;
    height: 5px;
    background: inherit;
}

::-webkit-scrollbar-button:start:decrement,::-webkit-scrollbar-button:end:increment {
    height: 5px;
    display: none;
    background: transparent;
}

::-webkit-scrollbar-track-piece:horizontal {
    background: #2d2d2d;
}

::-webkit-scrollbar-thumb:horizontal {
    background: #ff1732;
    width: 200px;
}

::-webkit-scrollbar-track-piece:vertical {
    border-left: 7px solid #2d2d2d;
    background: transparent;
}

::-webkit-scrollbar-thumb:vertical {
    height: 50px;
    border-left: 7px solid #ff1732;
}

}
