css:
@-moz-document domain("app.hacknplan.com") {
.navbar-default {
  background-color: #343640;
  border-color: #555;
}

.top-menu-logo {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 125px !important;
    padding-top: 35px !important;
    background: url(http://i.imgur.com/mdlHtFD.png) no-repeat !important;
}

.top-menu-icon, .top-menu-icon-badge {
    height: 50px;
    margin-right: 10px;
    color: #ccccce !important;
}

.remarked-text {
    color: #54565f;
}

.board-panel-content {
    background-color: #1e1f23;
}

.list-element-clickable {
   background-color: #1e1f23;
}

.board-left-panel {
   background-color: #2a2b31;
}

.h-column.board-right-panel.ng-scope {
    background-color: #1e1f23;
}

.application-side-menu {
    background-color: #2a2b31;
}

.nav-pills > li.active > a, .nav-pills > li.active > a:hover, .nav-pills > li.active > a:focus {
    color: #000000;
    background-color: #4db2ec;
}

::-webkit-scrollbar-track-piece {
    background-color: #1e1f23;
}

.list-group-item.active > .badge, .nav-pills > .active > a > .badge {
    color: #4db2ec;
    background-color: #000000;
}

.badge {
    background-color: #ff003b;
}

.nav > li > a:hover, .nav > li > a:focus {
    background-color: #1e1f23;
    color: #4db2ec;
}

hr {
    border-top: 1px solid #2a2b31 !important;
}

element.style {
    border-color: #2a2b31 !important;
}

.side-menu-element.active {
    color: #ff8800;
    background-color: #48e8a6;
}

.side-menu-element.active a {
    color: black;
    background-color: #48e8a6;
}

.list-element-clickable:hover {
    background-color: #171719 !important;
}

.blocked-task-watermark {
    color: rgb(255, 0, 24);
    opacity: 0.4;
}

.blocked-task-background {
    background-color: #312121 !important;
}

body {
    color: #ced2d8;
    background-color: #fd0909;
}

.list-element-title {
    color: #d5e2f5;
}

.icon-button, .icon-button:active, .icon-button:visited {
    color: #2b2d35;
}

.text-light-gray {
    color: #606371 !important;
}

.board-panel-header {
    background-color: #1e1f23;
}

.board-milestone-dropdown {
    border-left: 1px solid #1e1f23;
    color: #48e8a6;
}

.custom-round-button, .custom-round-button-sm, .custom-round-button-lg {
    border-radius: 2px;
    background-color: #343640;
}

.custom-circle-button a, .custom-circle-button-sm a, .custom-circle-button-lg a, .custom-circle-button span, .custom-circle-button-sm span, .custom-circle-button-lg span, .custom-round-button span, .custom-round-button-sm span, .custom-round-button-lg span, .custom-round-button a, .custom-round-button-sm a, .custom-round-button-lg a {
    color: #646973;
}

.markable:hover, .selectable:hover {
    background-color: #151617 !important;
}

.popover {
    background-color: #2a2b31;
    border: 0px solid rgba(0, 0, 0, .2);
}

.list-element-embedded {
    background-color: #2a2b31;
}

.selected {
    border: 2px solid #48e8a6 !important;
}

.custom-menu-button {
    background-color: #343640;
    color: #ddd;
}

.navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li > a:focus {
    color: #ffffff;
    background-color: rgba(42, 43, 49, 0.45);
}

.navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:hover, .navbar-default .navbar-nav > .open > a:focus {
    background-color: #1e1f23;
    color: #ffffff;
}

.custom-menu-button:hover {
    background-color: #48e8a6;
    color: #000 !important;
}

.custom-circle-button:hover, .custom-circle-button-sm:hover, .custom-circle-button-lg:hover, .custom-round-button:hover, .custom-round-button-sm:hover, .custom-round-button-lg:hover {
    background-color: #48e8a6;
    /* color: black; */
}

.dropdown-menu {
    background-color: #343640;
}

.dropdown-menu > li > a:hover {
    background-color: #48e8a6 !important;
    color: #000 !important;
}

.dropdown-menu > li > a {
    color: #9ca3ad;
}

.dropdown-header {
    color: #5c5f6d;
}

#board-panel-edition-right-content {
    background-color: #1e1f23;
}

#task-editor-right {
    border-left: 0px solid #ccc;
}

#board-panel-edition-right-content-header {
    background-color: #1e1f23;
}

#board-panel-edition-right-content-header .nav-pills > li.active > a:hover {
    background-color: #48e8a6;
    color: #000;
}

#board-panel-edition-right-content-header .nav-pills > li.active > a {
    background-color: #4db2ec;
    color: #000;
    border-radius: 3px;
}

#board-panel-edition-right-content-header .nav-pills > li > a:hover {
    background-color: #343640;
    color: #fff;
    border-radius: 2px;
}

#board-panel-edition-right-content-header .nav-pills > li > a {
    color: #646973;
}

.custom-panel-header, .custom-panel-header-blue, .custom-panel-header-green, .custom-panel-header-red, .custom-panel-header-purple, .custom-panel-header-default, .custom-panel-header-inverse {
    color: #48e8a6;
}

.task-title {
    color: #4db2ec;
}

#board-panel-edition-right-content-header .custom-round-button, #board-panel-edition-right-content-header .custom-circle-button {
    background-color: #ff003b;
}

.custom-circle-button span {
    color: #fff;
}

.custom-form-row:hover {
    background-color: rgba(16, 16, 16, 0.67);
}

.custom-form-field-icon {
    color: #48e8a6;
}

.custom-modal-lg, .custom-modal-md, .custom-modal-xs, .custom-modal-sm, .custom-modal {
    background-color: #26272d;
}


.custom-timepicker .btn-default:hover {
    background-color: #00ff95 !important;
        border-radius: 3;
}

.btn-default {
    background-color: #5d6071 !important;
    color: #fff !important;
    border-radius: 3;
}

.custom-modal-header {
    color: #48b4e8;
    border-bottom: 0px solid #aaa;
}

.custom-modal-footer {
    border-top: 0px solid #aaa;
}

.custom-round-button:disabled  {
    background-color: #ff003b !important;
    color: #000 !important;
}

.md-editor>textarea {
    border-top: 0px dashed #ddd;
    border-bottom: 0px dashed #ddd;
    background: #26272d;
}

.md-editor .md-footer, .md-editor>.md-header {
    background: #26272d;
}

.md-editor>textarea:focus {
    background: #26272d;
}

.md-editor .btn-default:hover, .md-editor .btn-primary:hover {
    background-color: #48e8a6 !important;
    /* color: black; */
}

#board-panel-edition-right-content-header .nav-pills > li.active .badge {
    background-color: #48e8a6;
    color: #1e1f23;
    border: 2px solid #48e8a6;
}

.custom-card-header-default {
    background-color: #626573;
}

.custom-card-header-blue {
    background-color: #4db2ec;
}

.custom-card-header-red {
    background-color: #ff003b;
}

.custom-card-header-green {
    background-color: #48e8a6;
}

.text-green {
    color: #9df76f !important;
}

.angular-ui-tree-empty {
    background-color: #1e1f23;
}

.custom-card-header-blue, .custom-card-header-green, .custom-card-header-red, .custom-card-header-purple, .custom-card-header-default, .custom-card-header-inverse {
	color: #000;
}

.page-tab-content {
    background-color: #1e1f23;
 }

.page-panel-content {
    background-color: #1e1f23;
}

.custom-panel {
    background-color: #1e1f23;
}

.page-panel-header {
    background-color: #343640;
}

.onoffswitch-label {
    border: 2px solid #343640;
    background-color: #343640;
}

.onoffswitch-label:before {
    background: #d0d0d0;
    border: 2px solid #343640;
}

.onoffswitch-checkbox:checked + .onoffswitch-label, .onoffswitch-checkbox:checked + .onoffswitch-label:before {
    border-color: #48e8a6;
}

.onoffswitch-label:before {
    background: #1e1f23;
    border: 2px solid #343640;
}

.onoffswitch-checkbox:checked + .onoffswitch-label {
    background-color: #48e8a6;
}

.progress {
    background-color: #54565f;
}

.help-icon {
    border: 2px solid #595c6a;
}

.help-icon span {
    color: #595c6a;
}

.stats-panel-content {
    background-color: #1e1f23;
}

.stats-panel-header {
    background-color: #343640;
}

table {
    border-color: #343640;
}

.application-view {
    background-color: #1e1f23;
}

.settings-left-panel {
    width: 250px;
    background-color: #2a2b31;
}

.right-collapsible-panel {
    background-color: #2a2b31;
}

.right-collapsible-panel-open {
    box-shadow: 0 0 20px 0 #000000;
}

.notification-element {
    background-color: #2a2b31;
}

.form-control {
    background-color: #343640;
	border: 0px solid #cccccc;
    border-radius: 3;
    color: #8c8c8c;
}

.frontpage-left-panel {
    background-color: #2a2b31;
}

.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #343640;
}

.input-group-btn .btn-default:hover {
    background-color: #222222 !important;
    color: #48e8a6 !important;
}

.input-group-btn .btn-default {
    background-color: #5d6071 !important;
    color: #eee !important;
}

.input-group-addon {
    color: #dadada;
    background-color: #5d6071;
    border: 0px solid #cccccc;
}
}
