css:
/* Ipernity neutral dark, part 2/2 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ipernity.com")

{


/* V1.03 by Spotomy, Finland */


/*////////////////////////////////////// ARTICLES //////////////////////////////////////*/



.button.lightened > .New.Post.Add { /* New article button. Basic settings are in the common part. */
	filter: brightness(50%) !important;
	margin-right: 3px !important;
	margin-left: 1px !important;
}


/* --- Latest published --- */

	
.v2 .layout.three .postList > .post.first { /* Latest post */
	background-color: #333 !important;
}

.v2 .layout.three > .main > .postList > .post.first.cc_border { /* First article's top border */
	border-color: #356098 !important;
}

.v2 .layout.three .postList > .post.first > .preview { /* Big background quote sign */
	filter: invert(100%) !important;
	opacity: 0.4 !important;
}

.v2 .layout.three .postList > .post.first > .preview > h2 { /* First post title */
	filter: invert(100%) !important;
}

.v2 .layout.three .postList > .post.first > .preview > h2 > a { /* First post title */
	filter: brightness(150%) !important;
}

.v2 .layout.three .postList > .post.first > .preview > span { /* Dash after title */
	color: #444 !important;
	opacity: 1 !important;
}

.v2 .layout.three .postList > .post.first > .preview > a.date > span { /* First post date */
	opacity: 1 !important;
}

.v2 .layout.three .postList > .post.first > .preview > a.date > span > nobr { /* First post date */
	color: #444 !important;
	opacity: 1 !important;
}

.v2 .layout.three .postList > .post.first.cc_border .content > a { /* First post caption text */
	color: #000 !important;
}

.v2 .layout.three .postList > .post.cc_border .content > a { /* Other posts caption texts */
	color: #777 !important;
}

/* --- Article list > right pane --- */


.v2 .layout.three > .side2 > #blog_infos > .blogContent.highlight { /* Article description hovered */
	background-color: #222 !important;
	color: #999 !important;
}

.v2 .layout.three > .side2 > #blog_infos > .blogContent > div { /* Author name, I guess */
	color: #666 !important;
}



/* --- Articles concerning specific people --- */


/* Title bars "Your acquaintances", "Other people" */
.v2 .layout.two.left > .main > div[style^="margin-bottom: 30px"] > div[style^="padding"] {
	border: none !important;
}

/* Title bars "Your acquaintances", "Other people" */
.v2 .layout.two.left > .main > div[style^="margin-bottom: 30px"] > div[style^="padding"] > div[style^="background"] {
	background: none !important;
	background-color: #393939 !important;
}

/* This prevents background shrinking too narrow and thus wrapping. These
user images are divided into lines and thus cannot be wrapped properly. */
.v2 .layout.two.left > .main > div[style^="margin-bottom: 30px"] > ul.tagList[style] {
	background-color: #222 !important;
	min-width: 770px !important;
}

/* User image wrapper */
.v2 .layout.two.left > .main > div[style^="margin-bottom: 30px"] > ul.tagList > li > .portrait {
	text-align: center !important;
}

/* User image frames */
.v2 .layout.two.left > .main > div[style^="margin-bottom: 30px"] > ul.tagList > li > .portrait > .bg {
	background: none !important;
	border: 1px solid #171717 !important;
	border-bottom-color: #333 !important;
	border-right-color: #333 !important;
	width: 80px !important;
	height: 80px !important;
	margin-bottom: 0px !important;
}

/* User images */
.v2 .layout.two.left > .main > div[style^="margin-bottom: 30px"] > ul.tagList > li > .portrait > .cover {
	width: 54px !important;
	height: 54px !important;
	margin: auto !important;
	margin-top: 16px !important;
	margin-left: 25px !important;
}

/* User name */
.v2 .layout.two.left > .main > div[style^="margin-bottom: 30px"] > ul.tagList > li > .portrait > .userName {
	margin-top: 18px !important;
	margin-bottom: 15px !important;
}


/* --- Search results --- */


.v2 .layout.two.left.w600 > .main > #articles > li[style] { /* Space between results */
	margin-bottom: 40px !important;
}

.v2 .layout.two.left.w600 > .main > #articles > li > .details { /* Divider line between results */
	border-top: 2px solid #333 !important;
}

.v2 .layout.two.left.w600 > .main > #articles > li > div > .content { /* Results captions */
	color: #666 !important;
}

.v2 .layout.two.left.w600 > .main > #articles > li > div > .embed { /* Results images */
	filter: brightness(50%) !important;
}

/* Details > visit number text */
.v2 .layout.two.left.w600 > .main > #articles > li > .details > .counts > div > span { 
	filter: brightness(50%) !important;
	}


/* --- Your favorite articles --- */


.v2 .titleAndOptions > .optionsBar {
	margin-right: 33px !important;
}

/* Dropdown menu button "Display articles of:" text */
.v2 .titleAndOptions > .optionsBar > span {
	color: #666 !important;
}

/* Dropdown menu button "Everybody etc" text */
.v2 .titleAndOptions > .optionsBar > #dropmenu_by > .current > span {
	color: #666 !important;
}

/* Dropdown menu button "Everybody etc" text hovered */
.v2 .titleAndOptions > .optionsBar > #dropmenu_by > .current > span:hover {
	color: #999 !important;
}

.v2 div.dropmenu > .current:hover { /* Dropdown menu button "Display articles of" */
	background-color: #222 !important;
}

.v2 .items.shadowed.arrowed { /* Dropdown menu popup "Display articles of" */
	background-color: #393939 !important;
	-webkit-box-shadow: 0 0 9px #111 !important;
	border: 1px solid #111 !important;
	left: -7px !important;
}

.v2 .items.shadowed.arrowed::after { /* Dropdown menu popup "Display articles of" */
	filter: brightness(25%) !important;
}

.v2 .items.shadowed.arrowed > .innerscroll > a:hover { /* Dropdown menu popup item hovered */
	background-color: #444 !important;
	color: lightblue !important;
}


/* --- Article list in your favorite articles --- */


.v2 .layout.s170m770 .postList > .post.cc_border { /* Remove the line above the post */
	border-top: none;
}

/* Line underneath the post */
.v2 .layout.s170m770 .postList > .post.cc_border > .preview > .copydate > hr.content_divider {
	border-top: solid 2px #444;
}

/* Post text color */
.v2 .layout.s170m770 .postList > .post.cc_border > .preview > div > .content > a.c_text.hoverBlue {
	color: #666 !important;
}

/* Post text color hovered */
.v2 .layout.s170m770 .postList > .post.cc_border > .preview > div > .content > a.c_text.hoverBlue:hover {
	color: #888 !important;
}


/* --- All articles concerning you --- */


.v2 .layout.two .sidebar { /* Left sidebar */
	padding-bottom: 10px !important;
}

.v2 .layout.two .sidebar .portrait > .bg { /* User image painting frame removed */
	background: none !important;
}

.v2 .layout.two .sidebar .portrait > .cover  { /* User image new frame */
	width: 80px !important;
	height: 80px !important;
	margin-top: 15px !important;
	margin-bottom: 10px !important;
	margin-left: 12px !important;
	position: inherit !important;
	border: 1px solid #222 !important;
	border-right: 1px solid #444 !important;
	border-bottom: 1px solid #444 !important;
}

.v2 .layout.two .sidebar .portrait > .cover > a > img { /* User image */
	width: 60px !important;
	height: 60px !important;
	position: absolute !important;
	top: 10px !important;
	left: 10px !important;
}

.v2 .layout.two .sidebar .portrait > .userName { /* User name */
	margin-top: 0px !important;
}


/* --- Right pane --- */



.v2 .postview > .side > div.rubix { /* Articles pane at the right */
	background: #444  !important;
}

.v2 .postview > .side > .rubix > div > .content > span { /* "UNREAD" badge text */
	color: #bbb !important;
}

/* --- Right pane previous button --- */


.v2 .postview > .side > .navigation > a.prevBt { /* Previous/beginning article button */
	-webkit-transform: rotate(0deg) !important;
	background: none !important;
	background-color: #333 !important;
	border-left: none !important;
	opacity: 1 !important;
	text-align: center !important;
	min-width: 95px !important;
}

.v2 .postview > .side > .navigation > a.prevBt > span  { /* Remove "previous/beginning" text background */
	background-image: none !important;
}

.v2 .postview > .side > .navigation > a.prevBt > .prevArrow  { /* Removes original prev arrow */
	display: none !important;
}

.v2 .postview > .side > .navigation > a.prevBt::after { /* Removes previous prev arrow */
	content: none !important;
}

/* Removes original text "Most recent" from previous button, because the meaning is not right */
.v2 .postview > .side > .navigation > a.prevBt > .prevText {
	display: none !important;
}

/* Inserts new prev arrow to previous/beginning button ("a[class="prevBT"]::before" does not work) */
.v2 .postview > .side > .navigation > a.prevBt::before {
	display: block !important;
	content: "◀︎	Previous" !important;
	float: none !important;
	margin-top: 6px !important;
	margin-left: 0 !important;
	margin-right: 3px !important;
}

/* Beginning button is right, so we remove text set in the previous... */
.v2 .postview > .side > .navigation > a.prevBt.disabled::before {
	display: inline !important;
	content: "" !important;
}

/* ...and make it visible again */
.v2 .postview > .side > .navigation > a.prevBt.disabled > .prevText {
	display: inline !important;
	width: auto !important;
	float: none !important;
	margin-top: 0 !important;
	font-size: 9pt !important;
}

.v2 .postview > .side > .navigation > a.prevBt.disabled  { /* When in the beginning of list */
	background-color: #111 !important;
	background-image: none !important;
	padding-top: 0 !important;
	padding-bottom: 0 !important;
	padding-left: 0 !important;
	padding-right: 0 !important;
	min-width: 95px !important;
	margin-bottom: 0 !important;
	text-align: center !important;
}

/* --- Right pane next button --- */


.v2 .postview > .side > .navigation > a.nextBt { /* Previous/beginning article button */
	-webkit-transform: rotate(0deg) !important;
	background: none !important;
	background-color: #333 !important;
	border-left: none !important;
	opacity: 1 !important;
	padding-left: 0 !important;
	padding-right: 0 !important;
	padding-top: 5px !important;
	padding-bottom: 6px !important;
	text-align: center !important;
	min-width: 95px !important;
	margin-bottom: 0 !important;
}

.v2 .postview > .side > .navigation > a.nextBt > span  { /* Remove "previous/beginning" text background */
	background-image: none !important;
}

.v2 .postview > .side > .navigation > a.nextBt > .nextArrow  { /* Removes original prev arrow */
	display: none !important;
}

/* Removes original text "Oldest" from next button, because the meaning is not right */
.v2 .postview > .side > .navigation > a.nextBt > .nextText {
	display: none !important;
}

/* Inserts new next arrow to next/end button ("a[class="nextBT"]::before" does not work) */
.v2 .postview > .side > .navigation > a.nextBt::before {
	display: inline-block !important;
	content: "Next ▶︎" !important;
	margin-right: 0 !important;
}

/* End text is right, so we remove text set above... */
.v2 .postview > .side > .navigation > a.nextBt.disabled::before {
	display: inline !important;
	content: "" !important;
	margin-left: 0px !important;
}

/* ...and make it visible again */
.v2 .postview > .side > .navigation > a.nextBt.disabled > .nextText {
	display: inline !important;
	width: auto !important;
	float: none !important;
}

.v2 .postview > .side > .navigation > a.nextBt.disabled  { /* When in the end of list */
	background-color: #111 !important;
	background-image: none !important;
	border-right: none !important;
	padding-left: 0 !important;
	padding-right: 0 !important;
	padding-top: 0 !important;
	padding-bottom: 0 !important;
	min-width: 95px !important;
	margin-bottom: 0 !important;
	text-align: center !important;
}

.v2 .layout.three > .side2 > #blog_infos > .blogContent { /* Blog info on the right */
	color: #666 !important;
}


/* --- Archives --- */


.v2 > #mainlayout > #body > .layout.one > .path { /* Publication date bar */
	background: none !important;
	border-bottom: 2px solid #333 !important;
}

.v2 > #mainlayout > #body > .layout.one > .main > .archive-nav > .day { /* Day chooser, day */
	background-color: #333 !important;
	border-top: 2px solid #333 !important;
}

.v2 > #mainlayout > #body > .layout.one > .main > .archive-nav > .day:hover { /* Day chooser, day, hovered */
	background-color: #333 !important;
	border-top: 2px solid #333 !important;
	filter: brightness(140%) !important;
}

.v2 > #mainlayout > #body > .layout.one > .main > .bgposts { /* Larger main background */
	background: none !important;
	border-bottom: 2px solid #333 !important;
}

.v2 > #mainlayout > #body > .layout.one > .main > .bgposts > div[style] { /* Smaller main background */
	background: none !important;
}

/* Date at the top of the list */
	.v2 > #mainlayout > #body > .layout.one > .main > .bgposts > div[style^="width"] > .postList > li:first-child {
	border-top: none !important;
}

/* Single post */
	.v2 > #mainlayout > #body > .layout.one > .main > .bgposts > div[style^="width"] > .postList > li {
	border-top: 2px solid #333 !important;
}

/* Single post */
	.v2 > #mainlayout > #body > .layout.one > .main > .bgposts > div > .postList > li > .preview > div > .content > a  {
	color: #777 !important;
}


/* ---Someone's articles/docs concerning someone else > user box at the left --- */


.v2 .layout.two.left > .side > .gradient { /* Left sidebar */
	padding-bottom: 10px !important;
	background: none !important;
	background-color: #333 !important;
}

.v2 .layout.two.left > .side > .gradient .portrait > .bg { /* User image painting frame removed */
	background: none !important;
}

.v2 .layout.two.left > .side > .gradient .portrait > .cover  { /* User image new frame */
	width: 80px !important;
	height: 80px !important;
	margin-top: 15px !important;
	margin-bottom: 10px !important;
	margin-left: 12px !important;
	position: inherit !important;
	border: 1px solid #222 !important;
	border-right: 1px solid #444 !important;
	border-bottom: 1px solid #444 !important;
}

.v2 .layout.two.left > .side > .gradient .portrait > .cover > a > img { /* User image */
	width: 60px !important;
	height: 60px !important;
	position: absolute !important;
	top: 10px !important;
	left: 10px !important;
}

.v2 .layout.two.left > .side > .gradient .portrait > .userName { /* User name */
	margin-top: 0px !important;
}


/* --- New article tool (inside iframe) --- */

.v2 .m600s336.postview > .main > form[action^="/blog/"] > .form > .field > .input { /* Whole shebang */
	height: auto !important;
}

html > body > table > tbody > #xToolbarRow .TB_Toolbarset { /* Toolbar color */
	background-color: #393939 !important;
	border: none !important;
	padding-bottom: 3px !important;
	padding-top: 5px !important;
}

html > body > table > tbody > #xToolbarRow .TB_sideborder { /* Toolbar side borders */
	width: 0 !important;
}

html > body > table > tbody > #xToolbarRow .TB_Toolbar { /* Icon row backgrounds */
	border-collapse: collapse !important;
	margin-bottom: 3px !important;
}

html > body > table > tbody > #xToolbarRow .TB_Toolbar > tbody td { /* Button inbetweens */
	background-color: #393939 !important;
}

html > body > table > tbody > #xToolbarRow .TB_Toolbar div[class^="TB_Button"] { /* Button backgrounds */
	background-color: #666 !important;
	border-color: #888 !important;
	border-bottom-color: #292929 !important;
	border-right-color: #292929 !important; 
}

html > body > table > tbody > #xToolbarRow .TB_Toolbar div[class^="TB_Separator"] { /* Button separators */
	background-color: #393939 !important;
	width: 10px !important;
}

html > body > table > tbody > #xToolbarRow .TB_Toolbar > tbody td > div { /* Button icon background images */
	background: none !important;
}

html > body > table #xToolbar > .TB_Toolbar .TB_Button_On { /* Activated buttons */
	filter: brightness(130%) !important;
}

/* Size-button "Size" background */
html > body > table .TB_Toolbar > tbody > tr > td:nth-child(2) > table > tbody > tr {
	background-color: #393939 !important;
}

html > body > table > tbody > #xToolbarRow .TB_Toolbar .SC_FieldCaption { /* Size-button "Size" */
	padding-left: 1px !important;
}

html > body > table > tbody > #xToolbarRow .TB_Toolbar .SC_Field[style] { /* Size-button menu */
	width: 81px !important;
	border-color: #666 !important;
	margin-right: 2px !important;
}

html > body > table > tbody > #xToolbarRow .TB_Toolbar .SC_FieldOver { /* Size field hovered */
	filter: brightness(120%) !important;
}

html > body > table > tbody > #xToolbarRow .TB_Toolbar .SC_FieldLabel { /* Size menu items ???? */
	border-color: #666 !important;
}

html > body > table > tbody > #xToolbarRow .TB_Toolbar options[style] { /* Size menu items ???? */
	border-color: #666 !important;
}

html > body > table > tbody > #xToolbarRow .TB_Toolbar .SC_FieldButton { /* Size menu button */
	border-color: #666 !important;
}

/* Size menu button disabled */
html > body > table > tbody > #xToolbarRow .TB_Toolbar .SC_FieldDisabled .SC_Field { 
	border-color: #494949 !important;
}

/* Size menu button disabled */
html > body > table > tbody > #xToolbarRow .TB_Toolbar .SC_FieldDisabled .SC_FieldButton { 
	display: none !important;
}

/* Source button */
html > body > table #xToolbar > .TB_Toolbar:first-child > tbody > tr > td:nth-child(7) > div { 
	border: none !important;
}

/* Source button activated */
html > body > table #xToolbar > .TB_Toolbar:first-child > tbody > tr > td:nth-child(7) > .TB_Button_On td { 
	background-color: #222 !important;
}

html > body > table #xEditingArea { /* Editing area */
	border: 1px solid #111 !important;
	border-bottom: 1px solid #444 !important;
	border-right: 1px solid #444 !important;
}

html > body > table #xEditingArea > textarea[style] { /* Editing area for source */
	background-color: #191919 !important;
	height: 100% !important;
}

.v2 .m600s336.postview > .main > form[action^="/blog/"] > .form > p:nth-of-type(2) { /* Save buttons below */
	margin-top: -2px !important;
}

/* --- Add introduction (another iframe) --- */

/* Source button */
html > body > table #xToolbar > .TB_Toolbar:first-child > tbody > tr > td:nth-child(8) > div { 
	border: none !important;
}

/* Source button activated */
html > body > table #xToolbar > .TB_Toolbar:first-child > tbody > tr > td:nth-child(8) > .TB_Button_On td { 
	background-color: #222 !important;
}


/* --- "Insert special character" -tool in the "Create new article tool" > popup (iframe) --- */


/* Box that covers everything during popup (div after two iframes) */
.v2 > iframe:nth-of-type(2) + div {
	background-color: #222 !important;
}

/* Same as previous, but covers everything during popup (div after THREE iframes) */
.v2 > iframe:nth-of-type(3) + div {
	background-color: #222 !important;
}

html > body.PopupBody.ltr > div:not(:first-child) { /* All but the first div were fake shadows */
	display: none !important;
}

html > body.PopupBody.ltr > .contents { /* dialog wrapper */
	box-shadow: 0 1px 11px #111 !important;
}

html > body.PopupBody.ltr > .contents > #header > #TitleArea { /* Title area */
	background-color: #444 !important;
	border-color: #222 !important;
}

html > body.PopupBody.ltr > .contents > #header > #TitleArea > #closeButton { /* Closing cross in the title area */
	filter:brightness(40%) !important;
}

html > body.PopupBody.ltr > .contents > #header > #TitleArea > #closeButton:hover { /* Closing button hovered */
	filter:brightness(60%) !important;
}

html[dir="ltr"] > body[onload] { /* (inner fucking iframe) */
	background-color: #333 !important;
}

html[dir="ltr"] > body[onload] > table tr > td:first-child td { /* Letter grid */
	background-color: #444 !important;
}

html[dir="ltr"] > body[onload] > table tr > td:first-child td:hover { /* Letter grid hovered */
	background-color: #222 !important;
}

html[dir="ltr"] > body[onload] > table tr > td:nth-child(2) { /* Area between grid and single letter box */
	background-color: #333 !important;
}

html[dir="ltr"] > body[onload] > table tr > td:last-child { /* Area under single letter box */
	background-color: #333 !important;
}

html[dir="ltr"] > body[onload] > table tr > td:last-child td { /* Single letter box */
	background-color: #292929 !important;
	border: 1px solid #191919 !important;
	border-bottom: 1px solid #444 !important;
	border-right: 1px solid #444 !important;
}

html > body.PopupBody.ltr > .contents > #PopupButtons { /* Button area at the bottom */
	background-color: #222 !important;
	border-color: #191919 !important;
}

html > body.PopupBody.ltr > .contents > #PopupButtons td { /* Button area at the bottom */
	background-color: #222 !important;
}

html > body.PopupBody.ltr > .contents > #PopupButtons td[nowrap] > #btnOk { /* Ok button at the bottom */
	background-color: #369 !important;
	border-color: #222 !important;
}
	
html > body.PopupBody.ltr > .contents > #PopupButtons td[nowrap] > #btnOk:hover { /* Ok button hovered */
	filter:brightness(120%) !important;
}

html > body.PopupBody.ltr > .contents > #PopupButtons td[nowrap] > #btnCancel { /* Cancel button at the bottom */
	background-color: #444 !important;
	border-color: #191919 !important;
}

html > body.PopupBody.ltr > .contents > #PopupButtons td[nowrap] > #btnCancel:hover { /* Cancel button hovered */
	filter:brightness(120%) !important;
}


/* --- "Insert link" -tool in the "Create new article tool" > popoup (same iframe as in previous) --- */


html > body.PopupBody.ltr > .contents #TabsRow .PopupTabEmptyArea { /* Active tab */
	border-color: #555 !important;
}

html > body.PopupBody.ltr > .contents #TabsRow .PopupTabSelected { /* Active tab */
	background-color: #222 !important;
	border-color: #555 !important;
	border-bottom: none !important;
}

html > body.PopupBody.ltr > .contents #TabsRow .PopupTab { /* Unactive tab */
	background-color: #222 !important;
	border-color: #333 !important;
	border-bottom-color: #555 !important;
	color: #444 !important;
	background-color: #191919 !important;
}


html[dir="ltr"] > body[scroll="no"][style="OVERFLOW: hidden"] input[id^="txt"] { /* Input fields */
	background-color: #333 !important;
	border: 1px solid #191919 !important;
	border-bottom: 1px solid #444 !important;
	border-right: 1px solid #444 !important;
}

html[dir="ltr"] > body[scroll="no"][style="OVERFLOW: hidden"] textarea[id^="txt"] { /* Big input fields */
	background-color: #333 !important;
	border: 1px solid #191919 !important;
	border-bottom: 1px solid #444 !important;
	border-right: 1px solid #444 !important;
}

html[dir="ltr"] > body[scroll="no"][style="OVERFLOW: hidden"] input[id^="btn"] { /* Buttons */
	background-color: #444 !important;
	border: none !important;
	height: 22px !important;
	border-radius: 5px !important;
}

