@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("devs-base.com") {

/*html { background: #2F2F2F !important; }*/
html, .footer-inner,.endbar {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
}

/* individual post titles, uncomment ".postbody > h3," to hide them */
/*.postbody > h3,*/
/* IA - related projects */ .linksbar,
/* MZ */ #gootop, #goobot, #masthead > h1 > a[href="http://forums.mozillazine.org/"] > img,
img[src="http://forums.mozillazine.org/static/common/images/navHeader.gif"], /* PMo */ .adsbygoogle
{ display: none !important; }

.search .postbody > h3, #active-subsection { display: block !important; }

body, div, ul, li, table, tbody, th, tr, td:not([bgcolor]), h2, h3, p, form, /*dl,*/ dt, dd, cite, code,
#tabs > ul > li > a, #tabs > ul > li > a > span, .tabs > ul > li > a, .tabs > ul > li > a > span, #navigation > ul > li > a, /*all*/ a > span,
/* #tabs a, #tabs a span, #navigation a, */ #page-header, .header, .side-left, .side-right,
.corners-top, .corners-bottom, .corners-top > span, .corners-bottom > span { background: none !important; }
/* MZ */ .sidebar a[class] { background-image: none !important; }
a, span, strong { background-color: transparent !important; }

option, blockquote, pre, .codebox, .attachbox, #activetab, #active-subsection, .bg3, /* phpBB3 */ .dropdown-contents, /* VLC */ #linkBar ul, /* SEO */ .sub, /* Artodia */ .popup { background: #181818 !important; }
/* Fx Feed Reader */ #feedHeaderContainer, #feedBody,
/* frames */ #phpbb_announcement, .forabg, .forumbg, /* bars */ .headerbar, .navbar, .forums, .sidebar, /* headers */ #masthead,
/* posts */ .post, .bg2, .panel.bg2, .forums > .row:nth-child(2n), /* PM */ #cp-main .panel { background: #222 !important; }
/* posts */ .panel, .bg1, /* misc */ .forums > .row, .pagination > span > strong { background: #303030 !important; }
.rules { background: #533 !important; }

/* headerbar */    
.forabg, .forumbg { background-image: linear-gradient(rgba(0,0,0,0.2), rgba(18,151,147,0.2), rgba(8,17,6,0.3) 2.5em, transparent 2.5em) !important; }
.headerbar, #masthead { background-image: linear-gradient(rgba(0,0,0,0.2), rgba(18,151,147,0.2), rgba(8,17,6,0.3)) !important; }
#masthead > h1 > a[href="http://forums.mozillazine.org/"]:before { content: "mozillaZine"; display: block; position: absolute; margin: -50px 0 0 20px; padding: 3px 6px; font: bold 26px/1.2 Ubuntu, sans-serif; border-radius: 0 1px; background: #4be; color: #111; }
/* Sublime Forum */
#site-description > h1 > a[style="color: #444"] { padding: 0 6px !important; font-family: Ubuntu, "Open Sans", sans-serif; border-radius: 0 1px; background: #4be !important; color: #111 !important; text-decoration: none !important; }

/* text colour */    
* { color: #868a8c !important; text-shadow: none !important; border-color: #555 !important; }
html { color: #ccc !important; }
hr { height: 0 !important; border: 0 solid #555 !important; border-width: 1px 0 0 !important; }
a { color: #00838f !important; border-color: currentColor !important; }
a:active { color: #559A5A !important; }  
.post:target h3 > a { color: inherit !important; }
.post:target { border: 1px solid #555 !important; }
a.forumtitle, a.topictitle { color: #918d80 !important; }
a.subforum, a.lastsubject { color: #6c8578 !important; }
.subforums-title { color: #177674 !important; }
a.username { color: #999 !important; }    
A[href="./memberlist.php?mode=viewprofile&u=2505"][class="username"] { color: #e57e8a !important; }
A[href="http://www.gophpbb.com/eles"][title="Opens in new window"][target="_blank"][onclick="window.open(this.href);return false;"][rel="nofollow"][class="elonw"]:before { color: #e57e8a !important; content: "storm119 @ " !important; }
#back2topMain { background-color: rgba(18,151,147,0.5) !important; } 
    
/* colored members */ 
/* default white */ a.username-coloured { color: #556B82 !important; }
/* boss */ a[style*="#FF0000"] { color: #fc4270 !important;}
/* dev */ a[style*="#FF8000"] { color: #1a6563 !important;}   
/* bots */ .username-coloured[style*="#9E8DA7"], /*PMo*/ .username-coloured[style*="#AA7777"] { color: #666 !important; }
/* red; #f55 */ a[style*="#660000"], a[style*="#990000"], a[style*="#AA0000"] { color: #4ac158 !important; }
/* green */ a[style*="#006600"], a[style*="#009900"], a[style*="#00AA00"] { color: #129793 !important; }
/* blue */ a[style*="#000066"], a[style*="#000099"], a[style*="#0000AA"], a[style*="#0066FF"] { color: #fa0 !important; }
/* gold; #c90 */ a[style*="#CC9900"] { color: #ca0 !important; }
/* orange */  a[style*="#CC6600"], a[style*="#FF6600"], a[style*="#FF9900"] { color: #f70 !important; }
/* magenta */ a[style*="#660066"], a[style*="#660099"], a[style*="#993399"], /*Debian*/ a[style*="#8D38C9"] { color: #f5f !important; }
/* teal */ a[style*="#009999"] { color: #0fc !important; }
/* gray */ a[style*="#888888"] { color: #987 !important; }

/* colored text */
/* default pink */ .postbody .content span[style*="color"] { color: #f99 !important; }
/* green */
.postbody .content span[style*="green"],
.postbody .content span[style*="008000"],
.postbody .content span[style*="008040"],
.postbody .content span[style*="00BF00"],
.postbody .content span[style*="00BF40"],
.postbody .content span[style*="00FF00"],
.postbody .content span[style*="00FF40"],
.postbody .content span[style*="408000"],
.postbody .content span[style*="408040"],
.postbody .content span[style*="40BF00"],
.postbody .content span[style*="40BF40"],
.postbody .content span[style*="40FF00"],
.postbody .content span[style*="40FF40"],
.postbody .content span[style*="80FF00"],
.postbody .content span[style*="80FF40"],
.postbody .content span[style*="80FF80"] { color: #9f9 !important; }
/* blue */
.postbody .content span[style*="blue"],
.postbody .content span[style*="0000BF"],
.postbody .content span[style*="0000FF"],
.postbody .content span[style*="0040BF"],
.postbody .content span[style*="0040FF"] { color: #acf !important; }
/* yellow */
.postbody .content span[style*="yellow"],
.postbody .content span[style*="FFFF00"],
.postbody .content span[style*="FFFF40"] { color: #ff7 !important; }
/* orange */
.postbody .content span[style*="orange"],
.postbody .content span[style*="FF4000"],
.postbody .content span[style*="FF4040"],
.postbody .content span[style*="FF8000"],
.postbody .content span[style*="FF8040"],
.postbody .content span[style*="FFBF00"],
.postbody .content span[style*="FFBF40"] { color: #fc5 !important; }
/* orange like members *//*
.postbody .content span[style*="CC6600"],
.postbody .content span[style*="CC6640"],
.postbody .content span[style*="FF6600"],
.postbody .content span[style*="FF6640"],
.postbody .content span[style*="FF9900"],
.postbody .content span[style*="FF9940"] { color: #f70 !important; }/**/

/* images in posts */ .postbody .content img:not(:hover):not([src^="./images/smilies/"]), .attachbox img:not(:hover) { opacity: .7 !important; }
/* search highlight */ .posthilit  { color: #f44 !important; background: #111 !important; }
/* signature */ .signature { opacity: .5 !important; }
/* .inline-attachment */ .file > dd, /* user-agent */ .ua, /* MZ */ .back2top { color: #888 !important; }
/* post title size */ .postbody > h3 { font-size: 1.5em !important; }

::-moz-selection { background: #184880 !important; }

input[type="submit"], input[type="button"], input[type="file"], input[type="reset"], button,
.button, .button2, .buttons > div > a, .profile-icons > li > a,
.download-button, a.download-main, a.colorizeit-main
{ color: #fff !important; border: 1px solid #555 !important; background: #111 !important;
border-radius: 1px !important; box-shadow: none !important; text-shadow: none !important;
background-image: linear-gradient(rgba(255,255,255,0.2),rgba(125,125,125,0.2),rgba(0,0,0,0.3)) !important; }

input[type="submit"]:hover, input[type="button"]:hover, input[type="file"]:hover, input[type="reset"]:hover, button:hover,
.button:hover, .button2:hover, .buttons > div > a:hover, .profile-icons > li > a:hover,
.download-button:hover, a.download-main:hover, a.colorizeit-main:hover
{ border-color: currentColor !important; background-color: #000 !important; color: #4be !important; box-shadow: 0 0 1em currentColor !important; }

button, input, select, textarea { -moz-appearance: none !important; }
input[type="radio"], input[type="checkbox"] { color: #4be !important; background: #222 !important; border-color: #999 !important; }

input, select, textarea, input[type="text"] { background: #181818 !important; color: #ccc !important; padding: 2px !important; border: 1px solid #555 !important; box-shadow: none !important; }
input[type] { border-width: 1px !important; border-style: solid !important; }
textarea:hover, input:hover, select:hover, textarea:focus, input:focus, select:focus { border-color: #6ceec4 /*#097 #47b*/ !important; }

select > button { background-color: transparent !important; border: none !important; box-shadow: none !important; }
option:hover, option:focus, option:active, option:checked { background: linear-gradient(#158,#158) !important; }

/* phpBB 2015 + 2017 (fixed devs-base) */
.icon-button:before, .dropdown-select:not(:hover):after { -webkit-filter: invert(1) hue-rotate(180deg); filter: invert(1) hue-rotate(180deg); }
.search-header { box-shadow: none !important; border-radius: 0 !important; border-top: none !important; }
.dropdown-select::after { border-color: #888 !important; }
.search-box button.search-icon { border-radius: 0 !important; }
.search-box a.button, .search-header a.button { padding: 2px 5px 2px 3px !important; border-radius: 0 4px 1px 0 !important; }

.forabg, .forumbg, .headerbar, .navbar, .sidebar, .panel, .post, .rules, .postprofile img { border-radius: 1px !important; box-shadow: none !important; }
blockquote { border-style: dashed solid !important; border-width: 1px 4px !important; border-radius: 1px; }
.row { border-top: 1px solid #555 !important; border-bottom: 1px solid #333 !important; }
.dropdown, .pagination > span > a:not(:hover) { border-color: transparent !important; }
.dropdown .pointer, .dropdown .pointer-inner { border-color: #555 transparent !important; }
.announce, .unreadpost { border-left-color: #533 !important; border-right-color: #533 !important; }
.online .postbody { border-left-color: #09f !important; }
.online .postprofile { border-right-color: #09f !important; }

/* PM */
.pm_marked_colour { border-color: #555 #4be #333 !important; }
.pm_replied_colour { border-color: #555 #444 #333 !important; }
.pm_friend_colour { border-color: #555 #fff #333 !important; }
.pm_foe_colour { border-color: #555 #000 #333 !important; }

/* pollbar; FGD/viewtopic.php?f=17&t=50 and https://foldingforum.org/viewtopic.php?f=86&t=23362&view=viewpoll */
.resultbar { /*background: #111 !important;*/ padding: 0 !important; border-style: solid !important; border-width: 0 5px !important; border-color: #333 transparent #333 #333 !important; }
.pollbar1, .pollbar2, .pollbar3, .pollbar4, .pollbar5 { background: #158 !important; }
.rtl .pollbar1, .rtl .pollbar2, .rtl .pollbar3, .rtl .pollbar4, .rtl .pollbar5 { border-right-color: transparent !important; }

p.author { padding: 3px 0 5px !important; }

.buttons { /*margin-top: 4px !important;*/ } /* <--- commented out for phpBB 2015 */
.buttons > div, .profile-icons > li { width: auto !important; height: auto !important; }
.buttons > div > a > span { display: none !important; }
.profile-icons > li > a > span { display: inline !important; }
.buttons > div > a, .profile-icons > li > a { width: auto !important; height: auto !important; padding: 2px 4px !important; text-decoration: none !important; font-size: 1em !important; }
.postbody > .profile-icons > li { margin: 0 0 0 3px !important; }
.postbody > .profile-icons > li:last-of-type { margin: 0 0 0 10px !important; }

/* MZ fixes */
test #page-header { margin-top: 15px !important; }
test #page-footer { margin-bottom: 15px !important; }

/* freegamedev */
.post { margin-bottom: 4px !important; }

/* topic */
/* icons */

.sitehome, .icon-faq, .icon-members, .icon-home, .icon-ucp, .icon-register, .icon-logout, .icon-bookmark, .icon-bump, .icon-subscribe, .icon-unsubscribe, .icon-pages, .icon-print, .icon-search, /* 2015 */ .dropdown-contents > .small-icon, .icon-search-unanswered, .icon-search-active, .icon-team, .icon-delete-cookies { background-position: 0 50% !important; background-repeat: no-repeat !important; }

/* IMG replacements */

img[src$="/icon_topic_latest.gif"], img[src$="/icon_topic_newest.gif"] { width: 11px !important; height: 0 !important; padding-top: 9px !important; }
img[src$="/icon_post_target.gif"], img[src$="/icon_post_target_unread.gif"] { width: 0 !important; padding-left: 11px !important; margin-right: 2px !important; }
.icon_post_target, .icon_post_target_unread { margin-right: 2px !important; }

.icon_topic_latest, img[src$="/icon_topic_latest.gif"] { background-image: url("data:image/gif;base64,R0lGODlhCwAJAKIAAAAAAP///z4+PjMzMxYWFv///wAAAAAAACH5BAEAAAUALAAAAAALAAkAAAMdWCXc+oJIKd6a1EZc4bQeUQwKqWzlYF7iqEKM+yYAOw==") !important; }

.icon_topic_newest, .icon_post_target_unread, img[src$="/icon_topic_newest.gif"], img[src$="/icon_post_target_unread.gif"] { background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAsAAAAJCAYAAADkZNYtAAAAQElEQVQYlWNggAKjFK3/2DADNmCUovWfh4cHA2PVQD/FWN2KTTEDAwODRoocXAMyG6timCIYpkwxctgiK4QpBgDBd1BiDUt3hAAAAABJRU5ErkJggg==") !important; }

.icon_post_target, .icon-search-unanswered, img[src$="/icon_post_target.gif"] { background-image: url("data:image/gif;base64,R0lGODlhCwAJAKIAAAAAAP///z4+PhYWFv///wAAAAAAAAAAACH5BAEAAAQALAAAAAALAAkAAAMXSCTM+mtIB4Ucrd6pt+jX91geyI1NRiQAOw==") !important; }

.icon_topic_attach, img[src$="/icon_topic_attach.gif"] { width: 7px !important; height: 0 !important; padding-top: 10px !important; background-image: url("data:image/gif;base64,R0lGODlhBwAKAJEAAAAAAP///9/f3////yH5BAEAAAMALAAAAAAHAAoAAAIQnD6JkqbOFowTGspazOj2AgA7") !important; }

img[src$="/feed.gif"] { width: 16px !important; height: 0 !important; padding-top: 16px !important; background-image: url("data:image/png;base64,") !important; }

/* 2015 */

.icon-search-active { background-image: url("data:image/gif;base64,R0lGODlhCwAJAKECABYWFgRlqf///////yH5BAEKAAIALAAAAAALAAkAAAIWlIJoe+DKggMpzmdvyHMvqXHYl1RCAQA7") !important; }

.icon-delete-cookies { /* need icon */ }


/* fixed devs-base by storm119 */
  

/*BLOCKQUOTE, */
.inline-attachment,  
.thumbnail { 
   background: #1e1e1e !important;
}

.progress-grey { background-image: -moz-linear-gradient(center top, rgb(32,32,32) 0% , rgb(17,17,17) 100%) !important; }
.prog-color { background-image: linear-gradient(to right, rgb(18,151,147) 0%, rgb(18,172,150) 100%) !important; }   
p.donation_stats { color: #bdb76b !important; }   
.progress-value, #page-body > P:nth-child(7) > STRONG { color: #129793 !important; }   

/* storm119 changed -webkit-transition to -moz-transition -webkit-selection to -moz-selection */
/* Juz a nice effects for fun :) */


* {
    text-decoration: none !important;
}

a {
    color: #129793;
    -webkit-transition: all 0.3s ease-out !important;
    -webkit-transition-property: color !important;
    -webkit-transition-duration: 0.0s !important;
}

*|*:-moz-any-link:hover,
a:link:hover,
a:hover { 
    text-decoration: none !important;
    color: #14e78d !important;
    text-shadow: 0.005em 0.005em 0.05em #14e78d !important;
    -webkit-transition: all 0.3s ease-out background-color 600ms linear, color 600ms linear !important;
    -webkit-transition-property: color;
    -webkit-transition-duration: 300ms;
	-moz-transition: background-color 600ms linear, color 600ms linear !important;
	-o-transition: background-color 600ms linear, color 600ms linear !important;
	-ms-transition: background-color 600ms linear, color 600ms linear !important;
	transition: background-color 600ms linear, color 600ms linear !important;
    /*font-weight:bold*/
}

*::-moz-selection { 
    background: #333333 !important; 
    color: #00FF00 !important;
    text-shadow: 0.005em 0.005em 0.05em #6A6A6B !important;
}

