css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(digg.com) {

/*Removes sidebar digg blurb*/
.intro { display: none !important;}

/*Hover&text for the body*/
.news-summary { line-height: 1.5cm; font-size: 10px !important;}
.news-body:hover { font-size: 15px ; color: #FF0000!important;}
.news-details:hover { font-weight: bold; font-size:20px !important;}
.news-digg {line-height: .5cm; font-weight: bold !important;}

/*Left sidebar*/
#sidebar { font-size: 10px }
#sidebar:hover { font-size: 15px ; color: #FF0000!important;}


/* Footer removal*/
#footer { display: none !important;}



/*top of page search bar color changes*/
#top-keywords { background-color:#000066; color: #FFFFFF !important;}

/* Footer removal*/

#footer { display:none !important;}






}
