css:
@-moz-document domain(xplosionnetwork.com) {
#logo {
	background: #eceef0 url(http://i.imgur.com/NSHEuba.png) no-repeat 0% 5%;
}
.navigation {
	margin-bottom: 15px;
	color: #fff; 
	border-radius: 1px;
		-webkit-border-radius: 1px;
		-moz-border-radius: 1px;
	font-family: "Open Sans", Sans-Serif, Arial;
	font-size: 13px;
	background: #000;
	padding: 1.175em 1.175em 1.075em 1.275em;
}
.navigation .active {
	font-weight: 900;
	color: #fff;
	font-size: 13px;
}
.navigation a:link {
 text-decoration: none; color: #fff !important;
}
.tcat {
	background: #ffd700;
	font-size: 12px;
	color: #3c3c3c;
	padding: 10px 10px 9px;
}
#footer .uppermid {
	overflow: hidden;
	padding: 2.5em 0px 2.8em;
	color: #3c3c3c;
	font-size: 13px;
	background: #ffd700;
}
.ft_menu li a {
 
	color: #3c3c3c !important; 
	text-decoration: none;
}
.fa_xtra.white {
 color: #3c3c3c;
}
.footer-head {
	font-family: "Open Sans", Sans-Serif, Arial; 
	font-size: 17px; 
	color: #3c3c3c; 
	text-transform: uppercase; 
	padding-bottom: 10px;
}
.thead {
	background: #000;
	color: #fff;
	font-family: Sans-Serif, Arial, "Open Sans";
	font-size: 14px;
	font-weight: 500;
	padding: 1.2em 1.425em 1.1em 1.075em;
}
.tcat a:visited {
 color: #3c3c3c;
}
.tcat a:link {
  color: #3c3c3c;
}
.thead_2 {
	background: #000;
	color: #fff;
	font-family: "Open Sans", Arial, Sans-Serif;
	font-size: 14px;
	font-weight: 300;
	padding: 1.2em 1.425em 1.1em 1.075em;
	border-top-left-radius: 2px;
	border-top-right-radius: 2px;
}
#panel .upper {
    color: #000;
    background: #000;
    clear: both;
    padding: 1.7em 0px 1.8em;
}
a.button:link,
a.button:visited,
a.button:active {
	background: #ffd700;
	color: #3c3c3c;
	font-size: 14px;
	display: inline-block;
	margin: 2px 0px 6px;
	padding: 9px 19px 9px 14px;
}
#backtop {
	background: rgba(255, 215, 0, 1);
	color: #000;
	position: fixed;
	margin: 0px;
	cursor: pointer;
	right: 35px;
	bottom: 25px;
	z-index: 72;
	padding: 1em 1.4em 1em 1.2em;
	transition: background-color 0.3s;
	-webkit-transition: background-color 0.3s;
	-moz-transition: background-color 0.3s;
	font-size: 17px;
}
#backtop:hover {
	background: rgba(255, 215, 0, 1);
}
#footer .lower {
    color: #fff;
    padding: 3em 0px 2.1em;
    overflow: hidden;
    font-size: 12px;
    background: #000;
}
#footer .lower a:link, #footer .lower a:visited {
    color: #ffd700;
}
.dashboard-widget-small-box i {
    color: #ffd700;
}
.dashboard-widget-small-box .element {
    margin: 0 0 4px 0;
    color: #ffd700;
    font-weight: 500;
}
.dashboard-widget-small-box {
    min-height: 20px;
    padding: 23px 20px;
    margin-bottom: 27px;
    background-color: #202020;
    border-top: 3px solid #ffd700;
    border-radius: 2px;
    overflow: hidden;
}
a {
    color: #ffd700;
}
.credits {
    padding: 10px;
    min-width: 100px;
    width: auto;
    text-align: center;
    color: #000;
    background-color: #ffd700;
    font-weight: bold;
}
  .progress-bar-danger {
    background-color: #ffd700;
}
  .categoryLink {
    width: auto;
    height: 40px;
    padding: 10px;
    color: #fff;
    display: inline-block;
    background-color: #ffd700;
    border: 0px;
}
  .buy-btn {
    padding: 10px;
    background-color: #ffd700;
    display: block;
    margin-bottom: 20px;
    text-decoration: bold;
    width: 100%;
    border-top: 0;
    border-bottom: 1px solid #1d1d1d;
    border-right: 1px solid #1d1d1d;
    border-left: 1px solid #1d1d1d;
    color: #000;
    text-align: center;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
}
  .buy-btn:hover, .buy-btn:focus {
    background-color: #ffd700;
    color: #000;
}
  .srv-box:hover .fa {
  color: #ffd700;
}
 	.info-box {
    width: 100%;
    height: auto;
    padding: 15px;
    background: #212323;
    margin-top: 15px;
    border-left: 3px solid #ffd700;
}
 	.navigation a:hover,
	.navigation a:active {
     color: #ffd700;
 	text-decoration: underline;
}
.pmbg {
    background: #404040;
    display: inline-block;
    margin-top: -1px;
    margin-left: 2px;
    padding: 1px 9px 3px 8px;
    max-width: 220px;
    text-align: center;
}
  a:visited, a:link {
    color: #F88017;
    text-decoration: none;
}
}
